css:
@-moz-document regexp("https://sketchfab.com/.*") {
body {
    background: #333;
    color: #FFF;
}

/* UI */
.content>section:nth-of-type(odd) {
    color: #EEE;
}
.form-menu, .form-menu-select, .form-menu-select .sub, .form-menu-list,
.hover-menu, .form-menu-toggle {
    background: #444;
    border: 1px solid #222;
}
.hover-menu a {
    color: #FFF;
}
.hover-menu .separator {
    border-color: #333;
}
.hover-menu.large-menu .separator-vertical {
    border-color: #333;
}
.form-menu-select .item.active>.label,
.form-menu-toggle .item.active.togglable>.label {
    background: #1CAAD9;
    color: #FFF;
}
.item-list .item, .form-list .item, .form-listbox .item {
    background: #222;
}
.item-list .item.category-item, .form-list .item.category-item, .form-listbox .item.category-item {
    background: #222;
    border-color: #333;
}
.popup-container .popup {
    background: #333;
}
.popup-container .popup .popup-header,
.popup-container .popup .popup-footer{
	background: #444;
}
.popup-preview .popup .popup-footer .stats .icon {
    color: #FFF;
}
.popup-preview .popup .popup-footer .stats {
    color: #CCC;
}
.popup-share .popup .popup-content .form-label {
    color: #EEE;
}
.popup-container .popup-overlay {
    background: rgba(0,0,0,.6);
}
.popup-container .popup .popup-footer .tip {
    color: #AAA;
}
.popup-container .popup .popup-content {
    color: #EEE;
}

/* Header */
.top {
    color: #FFF;
    background: rgba(0,0,0,.9);
    box-shadow: 0 1px 5px rgba(0,0,0,.15);
}
.top .logo .link .visual {
    opacity: 0;
}
.top .logo .link {
    background: transparent url(https://static.sketchfab.com/static/builds/web/dist/sketchfab-logo-white-4d9d00172d9b1361ee0b185a6aa1d1ff.png) no-repeat center center;
    background-size: contain;
}
.top .navigation .section .link,
.top .actions .user .name {
    color: #FFF;
}
.hover-menu>li .btn-textified {
    color: #FFF;
}
.top .search .value {
    color: #FFF;
}
.header-content {
    background: rgba(0,0,0,.9);
}
.header-content .header-menu .elements .item {
    background: transparent;
    color: #CCC;
}
.header-content .header-menu .more {
    background: transparent;
    color: #CCC;
}

.categories-navigation {
    background: #444;
}


/* Browse */
.model-card, 
.folder-card,
.user-card,
.card{
    background: #444;
    color: #FFF;
    box-shadow: 0 1px 5px rgba(0,0,0,.15);
}
.model-card-name a,
.card__footer__title a.model-name__label {
    color: #FFF !important;
}
.content>section.explore-container a, .content>section.model-container a, .content>section.tags-container a, .content>section.newsfeed-container a, .content > section.profile-container a {
    color: #EEE;
}
.model-card-user .text .username-wrapper {
    color: #EEE;
}

.comments .comment .editor {
    background: #444;
    border-color: #555;
    color: #FFF;
}
.comments .comment .body {
    background: #444;
    color: #FFF;
}
.comments .comment .body::before {
    border-right-color: #444;
}
.user-card .meta .location {
    color: #CCC;
}
.query-sorts .option, 
.query-flags .option{
    background: #555;
    color: #EEE;
}
.query-filters .filter .name:hover {
    background: #666;
    color: #FFF;
}
.query-filters .filter .name:hover .icon,
.query-filters .filter .name:hover::after{
    color: #FFF;
}
.query-filters .filter .name::before {
    background: transparent;
}

.query-parameters.explore .query-filters .explore-filters-container {
    background: #444;
    color: #CCC;
    border-color: #333;
}
.query-parameters.explore .query-filters .explore-filters-container .filter {
    border-color: #333;
}
.query-parameters.explore .query-filters {
    border-color: #555;
}
.card-collection:hover {
    box-shadow: 0 0 1px rgba(0, 0, 0, 0.2), 
    0 6px 0 -3px #333, 
    0 6px 1px -3px rgba(0, 0, 0, 0.2), 
    0 12px 0 -6px #303030, 
    0 12px 2px -6px rgba(0, 0, 0, 0.2), 
    0 11px 20px 0 rgba(21, 111, 170, 0.21);
}
.card__quick-settings .hover-menu {
    background: #333;
    border: 1px solid #222;
}
.card__quick-settings .hover-menu:before {
    border-left-color: #222;
}
.card__quick-settings .hover-menu:after {
    border-left-color: #333;
}
.card__quick-settings .hover-menu li a:hover {
    background: #444;
}
.hover-menu .separator {
    border-color: #222;
}

/* Community */
.card-user {
    background: #444;
}
.card-user-name {
    color: #FFF;
}

/* Newsfeed */
.newsfeed-header .hello-title {
    color: #FFF;
}
.newsfeed-profile .user-stats .stat a,
.newsfeed-profile .user-stats .stat a:hover{
    color: #666;
}
.newsfeed-news .newsfeed-news-piece {
    border-bottom-color: #333;
}
.feed-card .box {
    background: #444;
    color: #FFF;
    box-shadow: 0 1px 5px rgba(0,0,0,.15);
}
.feed-card .description,
.feed-card .liked-by .userliked,
.feed-card .user-details .user-tagline {
    color: #AAA;
}
.feed-card .date {
    color: #666;
}
.model-smallcard {
    background: #444;
}
.feed-card .description:before {
    background: #444;
}
.feed-card .description:after {
    background: linear-gradient(to top,#444 0%,#444 40%,rgba(255,255,255,0)100%)
}

/* Model page */
.view .header .flag-staffpicked:after, .view .responsive-model-name .flag-staffpicked:after {
    color: #CCC;
}

.two-columns.sidebar-right .right .sidebar-box {
    background: #444;
    color: #FFF;
}

.comments .new+.comment-list {
    border-color: #555;
}

.model-container .main .viewer-additional,
.model-container .main .model-informations {
    background: #444;
}

/* Profile */
.sidebar .link {
    color: #CCC !important;
}
.sidebar .link:hover {
    background: #000 !important;
}

/* Footer */
.footer .navigation .primary a {
    color: #FFF;
}

/* Search */
.top.searching {
    color: #FFF;
    background: rgba(0,0,0,.9);
}
.search-overlay .search-tools-container .search-contentTypes {
    color: #FFF;
    background: rgba(0,0,0,.9);
}
.search-overlay .search-tools-container .search-contentTypes li {
    color: #CCC;
}
.search-overlay,
.search-overlay>.container .search-empty-state{
    background: #333;
}
.search-overlay>.container .search-footer,
.search-overlay .filters-container{
    border-color: #555;
}
.top.searching .box {
    border-color: #FFF;
}
.top .search .value {
    color: #555;
}

.search-overlay .multi-results-container .results:not(.models) {
    background: #222;
}

.model-card, .collection-card {
    background: #444;
    color: #FFF;
}
.collection-card-name a, .model-card-name a {
    color: #FFF;
}
.collection-card-user .text .username-wrapper, .model-card-user .text .username-wrapper {
    color: #CCC;
}
.collection-card:before, .collection-card:after {
    background: #404040;
}

.model-uploadcard  {
   background: #555;
}

.header-notifications {
   background-color: #000;
       background: transparent url(https://static.sketchfab.com/static/builds/web/dist/bell-notification-blue-3359987270f2b670b274e35d85fa96f9.svg) no-repeat center center;
  
}
.suggestions .background{
    background: #555;
}
}
