css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
      
  body {
    background: /*[[body]]*/ !important;
  }
  
  /* home / subscriptions */
  div#appbar-guide-menu, div#appbar-nav, div.feed-item-container, a.yt-ui-ellipsis, div.yt-card {
    background: /*[[module]]*/ !important;
  } 
  
  /* watch */
  a:not(.yt-uix-sessionlink):not(.html5-title-text):not(.ytp-button):not([data-tooltip-text="Edit"]), input, div#guide-container, div#yt-masthead-container, div#watch-discussion, div#action-panel-details, div#watch-header, div#watch-action-panels, div#watch7-sidebar-contents, div.all-comments, div#yt-comments-sb-container, div#footer-container {
    background: /*[[module]]*/ !important;
  }

  /* channel */
  div.shelf-description, div.secondary-header-contents, div.branded-page-v2-header, div.branded-page-v2-body, div.yt-lockup-description, div.branded-page-related-channels, div.c4-checklist-module, div.yt-ui-ellipsis, div.description {
    background: /*[[module]]*/ !important;
  }
  
  
  /* manage subscriptions */
  div#subscriptions-container {
    background: /*[[module]]*/ !important;
  }

}
