css:
@-moz-document regexp('http://9to5mac\\.com.*') {
.ninetofive-feed.ninetofive-toys { display: none; }

.ninetofive-feed.ninetofive-google { display: none; }

.stock-data { display: none; }

.post-content .post-body { padding:0 !important; }

.powered-by-wpcom { padding: 15px !important; display: block !important; }

.author-content .feat-image-wrapper, .post-content .feat-image-wrapper, .product-content .feat-image-wrapper { max-width:860px !important; }

.feat-image-wrapper { max-width:860px; margin:0 auto; }

.author-content .post-title, .post-content .post-title, .product-content .post-title { padding: 25px 0px 0 !important; }

.author-content .post-meta, .post-content .post-meta, .product-content .post-meta { padding: 11px 0 0 !important; margin-bottom: 14px !important; }

.generations-table, .post-body, .recommendations, article.gen.current-gen { font-size: 1.3em !important; margin-bottom:15px !important; }

.elastic-container { max-width: 860px !important; }

.scroll-to-fixed-fixed { position: initial !important; }

.headerPlaceholder { display: none !important; }

.news-feed-header + div { display: none !important; }

.logo { padding: 17px 0 17px 0px !important; }

.header-top .header-right { padding-right: 0 !important; margin-right:-10px !important; }

.post-cats a { padding-right:0 !important; }

nav.site-nav, nav.site-nav ul { padding-right: 0px !important; }

.trending-wrapper { display:none; }
.post-content .aside-posts, .product-content .aside-posts { margin: 0 auto; width: 100%; max-width: 860px; margin-top:10px; } 
 .post-content:nth-child(odd) { background-color:white !important; }
 .post-content:nth-child(even) { background-color:white !important; }
.news-feed-header, .yesterday-news-feed-header { background-color:white !important; display:none; }
.header-wrap { margin-bottom:20px; }

.moretext {
  visibility: hidden;
}
.moretext:after {
  content:'Read More'; 
  visibility: visible;
  display: block;
  position: absolute;
top:-2px;
}

a.more-link {
padding: 0 !important;
text-transform: none !Important;
margin-top: 20px;
display: inherit !important;
width: auto !important;
height: auto !important;
margin-left:-2px;
font-weight:bold !important;
}

.color-scheme-mac a.more-link:hover {
	color:black !important;
	background-color:white !Important;
}

.color-scheme-mac a.more-link {
	color:black !important;
	background-color:white !Important;
}

span.lesstext, span.moretext { color:#4b8ec9 !important; font-wieght:800 !important; }
}
