css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.basilmarket.com/") {

.night, .day
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/background.png") top repeat-x #BC5200 !important;
	}
.innerWrapday, .innerWrapnight
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/banner.png") top repeat-x #BC5200 !important;
	}
.innerWrapPP1, .innerWrapPP2
	{
		width: 800px !important;
	}
.innerWrapPP1
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/logo.png") no-repeat !important;
	}
.innerWrapPP2
	{
		background-position: 666px -6px !important;
	}
.bwnight, .bwday
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/body-wrap.png") top no-repeat #DF8719 !important;
	}
.innerMenuWrap 
	{
		background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/menu-background.png") no-repeat #DF8719 !important;
	}
.left
	{
		background:url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/side-header.png") top repeat-x #6E8A27 !important;
	}
.right, td.middleColumn div.np div.right
	{
		background:url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/right-header.png") top repeat-x #990000 !important;
	}
.postSubn, .rf.small.normal, div.lh, .screen.mb10
	{
		background:url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/post-background.png") top repeat-x #FECE57 !important;
	}
.topicBar, .bbTop
	{
		background:url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/thread-header.png") top no-repeat #990000 !important;
	}
a.btn, span.btn, a.btn:visited
	{
		background:url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Thanksgiving%20Theme/button.png") no-repeat !important;
	}

#bodyWrap, .sideThumb.normal, .picc, body[style="background:url(img/back.jpg) repeat-x #F5F8F4;text-align:left;"]
	{
		background: #DF8719 !important;
	}
.leftColumn, .small.sep.normal, .misc1, .normal.lh, .normal.center.misc1, .rightColumn, .copyright, .postn, .postSub, .small.misc1.normal, div.csep, td.mainColumn div.np div.misc3, table.normal[cellpadding="5"][cellspacing="0"][border="0"][width="100%"] tr.misc3 td, .lstat .lstatn, .dropItem, .thumbItem, .refFieldset, .dropItems, .mainColumn div[style="border-bottom:1px solid #ccc;color:#666;"], .mainColumn div[style="max-height:192px;overflow:auto;"], .lstat2 div.lstatn, table[cellpadding="5"][cellspacing="0"][border="0"][width="100%"] tr.misc1.normal td, .buttons.center.sep, .mid.hand, td.sideColumn, div.sideModule, td.middleColumn div.np, .auction tr td.misc1, .listScreen, .rules, td.middleColumn div.np table tr.misc3 td, td.misc3[align="center"][width="10%"], div[style="border:1px outset #ddddcc;background:#ddddcc;"], td[style="width:200px;vertical-align:top;margin-bottom:10px;background:#F8F7F2;"], td[style="width:300px;vertical-align:top;margin-bottom:10px;background:#F8F7F2;"], .picm.hand, .mid, tr.misc1 td[width="5%"]
	{
		background: #FECE57 !important;
	}
.normal, td[width="5%"], .small.center.misc5, .ps4, div.sep, td.mainColumn div.np table, #help_scrap, .lstat div, .thumbItemHeader, .dropTitle, .lstat2 div, .lstat3 div, .lstatn div, .auction tr td, td.middleColumn table, .gachapon, .search_result, td[align="center"], #fbhelp
	{
		background: #EBB234 !important;
	}
.normal.center.errorField2, tr.errorField2 td.normal, .toolBox, td.title2, .error3, .errorField2, div.title2.misc2, .buttons a:hover, tr.hiLite1.small td, .hil, .gachapon div.rare, .hil td, .flHeader, .error2
	{
		background: #F4F0C0 !important;
	}
.msgHeader, .monsterTitle, .monsterDropTitle, .lstatl, .referenceAreaType, .lstat2.hiLite1 div, .misc1.title3.normal, .gachaponBox, table tr.misc3 td
	{
		background-color: #DDA215 !important;
	}
.stickyrow td, a[style="color:orange;"]
	{
		background: #FF9900 !important;
	}
td.sep, td.stickysep
	{
		background: #591C01 !important;
	}
div.sep, .csep, .postn, span.sep, .lstat, .dropItem, .dropItems a:hover, .lstat2, .lstat3 div, .auction td, td.sep, .rules, .gachapon div, .search_title
	{
		border-bottom: 1px solid #591C01 !important;
	}
.dropItemR, td.sideColumn
	{
		border-right: 1px solid #591C01 !important;
	}
.dropItems
	{
		border-bottom: 1px solid #EBB234 !important;
		border-right: 1px solid #EBB234 !important;
		border-left: 1px solid #EBB234 !important;
	}
.sideThumb, .picc, .thumbItem, .refFieldset, .listScreen, .picm.hand
	{
		border: 1px solid #591C01 !important;
	}
.picm, .picm.hand
	{
		border-bottom: 2px solid #591C01 !important;
	}
.picma
	{
		border: 1px solid #DDA215 !important;
		border-bottom: 2px solid #DDA215 !important;
	}
.stamp2
	{
		border: 0 !important;
	}
.copyright, .postSub
	{
		border-top: 1px solid #591C01 !important;
	}
.postBox
	{
		border:1px inset #591C01 !important;
	}
.tmenuTabActive
	{
		background-color: #EBB234 !important;
		color: #000000 !important;
		border-left:1px solid #F4F0C0 !important;
		border-top:1px solid #F4F0C0 !important;
		border-right:1px solid #591C01 !important;
	}
.tmenuTabInactive
	{
		background-color: #F4F0C0 !important;
		color: #111111 !important;
		border-left:1px solid #F4F0C0 !important;
		border-top:1px solid #F4F0C0 !important;
		border-right:1px solid #591C01 !important;
	}
span.normal
	{
		background: transparent !important;
	}
.topMsg, .oldMsg
	{
		background-color: #F4F0C0 !important;
		border: 1px solid #DDA215 !important;
	}
.likeit
	{
		background-color: #DDA215 !important;
	}
.likeit:hover
	{
		background-color: #F4F0C0 !important;
		color: #000000 !important;
	}
div.lh, .screen.mb10
	{
		border: 5px solid #FECE57 !important;
	}
.title3.sep
	{
		background-color: #DDA215 !important;
		color: #000000 !important;
	}
.ps5
	{
		border: 0 !important;
	}
div[style="border:1px outset #ddddcc;background:#ddddcc;"]
	{
		border: 1px outset #DF8719 !important;
	}
.quote
	{
		border-left: 2px solid #DDA215 !important;
	}

.small.sep.normal a, .sideThumb.normal, .csep a, .normal.lh a, .normal a, normal.center.misc1 a, .copyright, .copyright a, .rf a, .misc1, .postn *, .sep.small,  td[width="5%"], td[width="70%"], td.title2, .ps4.normal, .hand, .small, .rf, .small.misc1.normal a, div.csep, span.lh, div.lh a, .topMsg, .topMsg a, td.mainColumn div.np table, div.misc1 a, .sep, .error3, .error3 strong, td.mainColumn div.np, .monsterDropTitle, .thumbItemHeader, .refFieldset legend a, .refFieldset legend strong, .refItemBlock a, .dropItems a, .referenceAreaType a, .center a, .mainColumn div[style="border-bottom:1px solid #ccc;color:#666;"], .mainColumn div[style="max-height:192px;overflow:auto;"] *, div.title2.misc2, .lstat3 div a, .buttons a, .sep a, .subList a, tr.hiLite1.small td, div.lh.normal, div.lh.normal strong, td.middleColumn div.np div, .normal, .normal strong, .listScreen a, .sideOnline strong a, .rules, .gachaponBox div a, .gachaponLetter, .misc3 td, .flForum a, .flHeader, .flForum, .stickytopic a, .search_result, .search_result a, .search_title, .picm.hand a, #fbhelp, .mid, td.middleColumn table tr td, .oldMsg, .oldMsg a
	{
		color: #000000 !important;
	}
div.menu a.rf, .btn.lf, .small.hand, .msgHeader, .msgHeader center, .monsterTitle, .monsterTitle a, div.topicBar span.small, div.topicBar div.small, a[style="color:orange;"]
	{
		color: #FFFFFF !important;
	}
.postAuthorAn a strong, div.misc1 a:hover
	{
		color: #591C01 !important;
	}
.quote, .quote strong, .quote span.lh, .postib, .postib strong
	{
		color: #111111 !important;
	}
.postBox
	{
		color: #000000 !important;
	}

div.np.mt10 div.lh
	{
		height: 96px !important;
	}

.sub_menu a:hover
	{
		background: #990000 !important;
	}
.sub_menu
	{
		opacity: 0.9 !important;
	}
div[style="padding:0;text-align:center;margin-top:0px;margin-bottom:10px;"]
	{
		display: none !important;
	}

}
