css:
@-moz-document domain("9to5mac.com") {
* {font-family:"/*[[font]]*/" !important;}
[class*=" icon-"], [class^=icon-] {font-family:icomoon !important}
.author-content .post-title, .post-content .post-title, .product-content .post-title {font-weight:700; letter-spacing:-2px;}
.post-body {font-weight: 500;line-height: 35px;width: 80%;margin: 0 auto;}
.post-body p a {color: #2b82ad !important;}
.feat-image.wide {height: 350px;}
.header-wrap {background:#FFF !important; height:45px;}
header {height:75px;}
nav {font-size: 10px;letter-spacing: 1px;}
#menu-top-menu {text-align: right;}
.logo img {width: auto;max-height: 30px;margin-top: -7px;}
ul#menu-top-nav {margin-top: 2px;}
ul#menu-top-nav a {color: #eee;}
ul.sub-menu {display: none !important;}
.stock-data.AAPL-stock {display: none;}
.news-feed-header {background: #0d4d7c;}
.currently-reading {font-size: 1em;height: 25px;font-weight: 900;}
a.more-link {background-color: #0d4d7c;padding: 6px 0;text-align: center;}
a.more-link .x-animate {top:-5px;}
a.less-link {padding: 6px 0; text-align:center;}
span.more-text {top:0;}
a.less-link .x-animate {top:2px; right:-16px}
.post-content .individual-aside {background-color: #fafaff;border-bottom: 1px solid #ddd;}
.trending-items {background:#eee;}
.trending-items .title {font-size: .9em;font-weight: 900;}
.trending-excerpt {font-weight:400;}
.slick-slide {border-right: 1px solid #ccc;}
.ninetofive-toys .slick-slide {border-right:0;}
.post-cats a {font-weight: 500;font-size: 0.9em;}

/** COMMENTS **/
.comment {padding: 0 !important;}
.comment-body {padding: 2%;}
ul.children {margin: 0 0 0 70px;padding: 0;}

/** ADVERTISING **/
.ninetofive-google {display:none}
}
