css:
@-moz-document domain("proyecto.fedux.com.ar") {

    #wrapper{
        background: url(http://subtlepatterns.com/patterns/gray_jean.png);
    }
    #main{
        background: none;
        max-width: 1400px;
        margin: 0 auto;
    }
    #content{
        padding-left: 20px !important;
    }
    .controller-issues.action-show #content{
        background: none !important;
        border: none !important;
    }

    div.issue.details {
        background: #fffada !important;
        padding: 20px !important;
        border: none !important;
        border: 1px solid #ba9 !important;
        border-radius: 2px;
        box-shadow: 0 2px 5px #ccc;
        margin: 0 0 20px !important;
    }
    div.issue.details.closed {
        background: #fffdee !important;
    }

    div.journal {
        background: #fff;
        margin: 0 0 20px;
        padding: 25px 20px 15px;
        border: 1px solid #bbb;
        border-radius: 2px;
        box-shadow: 0 2px 5px #ccc;
    }

    #content h2{
        margin: 10px 0 !important;
    }

    table.progress{
        box-shadow: 0 1px 2px rgba(0,0,0,0.2) inset;
        border-radius: 1px;
    }
    table.progress td.todo {
        background: rgba(0, 0, 0, 0.03) !important;
    }
    table.progress td.closed {
        background: #AAE0AA !important;
    }


    hr {
        background:rgba(0, 0, 0, 0.1) !important;
        margin: 15px 0;
    }

    /* ********** */


    .journal .details {
        background: #fafafa;
        border-bottom: 1px solid #eee;
        color: #666;
        font-size: 11px;
        margin: -20px -20px 20px;
        padding-bottom: 10px;
        padding-top: 10px;
        box-shadow: 0 3px 1px #f3f3f3 inset;
    }

    .journal h4 {
        margin: -25px -20px 20px !important;
        padding: 25px 20px 20px !important;
        background: #fafafa;
        border-bottom: 1px solid #ddd !important;
        box-shadow: 0 3px 1px #FAFAFA !important;
    }


    /* ******* */

    #top-menu{
        background-color: #632 !important;
        position: relative;
        box-shadow: 0 3px 1px rgba(0, 0, 0, 0.1);
        z-index: 99;
        padding: 4px 20px 0 !important;
    }

    #header {
        background-color: #c64 !important;
        border-bottom: 6px solid #fff;
        box-shadow: 0 1px 1px rgba(0, 0, 0, 0.2);
    }

    #main-menu {
        left: 10px !important;
    }

    #main a,
    #main a:link,
    #main a:visited {
        color: #955 !important;
    }
    
    #main a.parent,
    #main a.parent:link,
    #main a.parent:visited {
        color: #fff !important;
        background: #b76;
        border-radius: 2px;
        margin-right: 3px;
        padding: 2px 4px;
    }

    h1{
        margin: 10px 14px !important;
        padding: 0 !important;
    }

    #main-menu li a:hover{
        background-color: #b54 !important;
    }

    #main-menu li a {
        border-radius: 1px 1px 0 0;
        transition: 0.2s;
    }

    #main-menu li a.selected {
        background-color: #fff !important;
    }


    /* ******** */

    #content h2 {
        background: #433;
        color: #fff !important;
        padding: 10px 20px !important;
        margin: 10px 0 30px !important;
        position: relative;
        z-index: 2;
        border-radius: 2px;
        box-shadow: 0 3px 2px rgba(60,20,0,0.2);
    }

    #content .issue h3 {
        font-size: 20px !important;
        line-height: 22px !important;
        color: #533 !important;
    }
    #content .issue.closed h3 {
        text-decoration: line-through;
    }

    #content .author {
        margin: 0;
        font-size: 11px;
    }

    .attributes{
        clear: both;
        padding: 10px 0 0 !important;
    }


    .action-show #content h2 {
        margin: 10px 0 -1px;
        border-radius: 2px 2px 0 0;
    }
    
    .action-show #content .wiki h2 {
        border-radius: 2px;
        margin: 20px 0 !important;
    }
    
    .action-show #content .wiki h3 {
        font-size: 18px;
        font-weight: normal;
        margin: 20px 10px 10px 0;
    }
    .action-show #content .wiki p {
        margin: 0 0 12px;
    }

    #content > .contextual{
        padding: 4px 0;
        float: none !important;
        text-align: right;
    }

    #sidebar{
        width: 21% !important;
    }


}
