css:
@-moz-document url-prefix("http://www.assettocorsa.net/forum/") {
.primaryContent .signature, .quickReply.message > center:first-child, #logoBlock, footer .footerLegal, a.report, .authorEnd, #pageDescription, .quickReply .messageUserInfo, .sharePage, footer, .similarThreadsThreadView, .userBanner.wrapped span.before, .userBanner.wrapped span.after {
    display: none;
}

.userBanner {
    position: absolute !important;
    bottom: 0;
    left: 16px;
    z-index: 10;
    padding: 0 5px;
}

.userBanner + .userBanner {
    bottom: 25px;
}

.avatar img, .sp-avatar img { height: 32px; width: 32px; margin: 0 !important; }
.steamprofile .sp-avatar { height: 32px; width: 32px; }
.steamprofile .sp-badge, .steamprofile .sp-loading, .steamprofile .sp-error { padding: 0 }
.steamprofile { height: 46px }
.steamprofile .sp-info { padding: 3px 41px; }
.messageUserInfo { width: 160px; }
.messageMeta.ToggleTriggerAnchor { float: right; }
.message .messageContent { min-height: 40px }
.message .likesSummary { border: none; color: #aaa; background: none; float: right; margin-top: 0; padding: 10px 5px 5px; margin-right: 18px; }

.messageList .message { padding: 4px 0 8px }

.messageUserBlock h3.userText { font-size: 10px; }
.messageUserBlock { background: rgb(198, 198, 198) !important }
.message .messageInfo { margin-left: 180px }

.avatar img, .avatar .img, .avatarCropper { border: 0; padding: 0 }

.avatarHolder { padding: 0 !important; display: inline-block;  vertical-align: bottom; }
.userText { display: inline-block; vertical-align: bottom; padding: 0px 5px 2px !important; }

#header, #headerMover #headerProxy {
    background-image: none !important;
}

#headerProxy {
    height: 60px !important;
}

.messageText, .bbCodeQuote .quoteContainer {
    font-size: 11px !important;
}

* {
    border-radius: 0 !important;
}

.navTabs, .navTabs .navTab.selected .navLink {
    border: none;
    border-radius: 0;
    background-image: none !important;
    text-shadow: none !important;
}

.navTabs .navTab.selected .navLink { margin-top:0;padding-top:0;border:none;background:#eee; }

.navTabs .navTab.selected .tabLinks {
    background-image: none !important;
}
}
