css:
@-moz-document domain("vozforums.com") {
.page, body {background: url(http://i.imgur.com/9ec0pOa.png) fixed !important}

[class*=alt], .tborder, [class*=panel], #vB_Editor_QR {background:rgba(255,255,255,0.4) !important} 

body {margin:auto !important } html {overflow-x:hidden !important} [style*=wrap] > STRONG {margin-right:100px !important}

/*Đổi font cho hợp với giao diện phẳng*/

@font-face {
    font-family: "Segoe UI";
    font-weight: 200;
    src: local("Segoe UI Light");
}
@font-face {
    font-family: "Segoe UI";
    font-weight: 300;
    src: local("Segoe UI Semilight");
}
@font-face {
    font-family: "Segoe UI";
    font-weight: 400;
    src: local("Segoe UI");
}
@font-face {
    font-family: "Segoe UI";
    font-weight: 600;
    src: local("Segoe UI Semibold");
}
@font-face {
    font-family: "Segoe UI";
    font-weight: 700;
    src: local("Segoe UI Bold");
}
@font-face {
    font-family: "Segoe UI";
    font-style: italic;
    font-weight: 400;
    src: local("Segoe UI Italic");
}
@font-face {
    font-family: "Segoe UI";
    font-style: italic;
    font-weight: 700;
    src: local("Segoe UI Bold Italic");
}

* {
	font-family: "Lucida Grande","Lucida Sans Unicode","Lucida Sans", Geneva, Verdana, sans-serif !important;
    font-size: 15px !important;
	/*font-family: Segoe UI !important;*/
}

body {
	background: #050505 !important;
}

/*Phần ghi nội dung comment*/
  #vB_Editor_QR_textarea, #vB_Editor_001_textarea, textarea { 
    	background: #f2f2f2;
    	display: block; 
    	border: 0px solid #ccc;
    	color: #000000;
    	box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    	transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    	height: 200px !important; 
    	margin: 9px 0px 0px 0px; 
  }

  #vB_Editor_QR_textarea, #vB_Editor_001_textarea:focus, textarea:focus { 
    	background: #ffffff; 
    	transition: background-color ease-in-out .25s;
  }
}
