css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {


    .u-bgUserColor, .u-bgUserColorHover:hover, .u-bgUserColorHover:focus {
    background-color: #000 !important;
}
 
    .top-timeline-tweetbox .timeline-tweet-box .tweet-form.condensed .tweet-box {
    color: #000;
}
    
    .home-tweet-box, .top-timeline-tweetbox .timeline-tweet-box {
    background-color: #242424 !important;
}
    
    .home-tweet-box, .top-timeline-tweetbox .timeline-tweet-box {
    border-color: #000 !important;
}
  
    .tweet-counter {
    background-color: transparent;
    color: #612ac5;
    display: inline-block;
    width: 35px;
    border: 0px none;
    padding: 0px 3px;
    position: relative;
    vertical-align: top;
    top: 7px;
    text-align: right;
    font-size: 14px;
    text-shadow: 0px 1px 1px rgba(71, 33, 141, 0.75);
}
    
    input, textarea, div[contenteditable], .t1-select {
    display: inline-block;
    width: 210px;
    padding: 4px;
    margin: 0px;
    outline: 0px none;
    background-color: rgb(16, 16, 16);
    border: 1px solid #612ac5;
    border-radius: 1px;
}
    
    .tweet-box[contenteditable="true"] {
    border-color: rgba(90, 0, 255, 0.3);
}
    
    .currently-dragging.modal-enabled .modal .tweet-box, .currently-dragging:not(.modal-enabled) .tweet-content .tweet-box, .tweet-box[contenteditable="true"]:focus {
    border-color: #612ac5;
    box-shadow: none;
}
   
    .global-nav-inner {
    background: #242424 none repeat scroll 0% 0%;
    height: 46px;
}

       .nav > li {
    color: #612ac5;
 }
    
    .Trends .trend-location {
    color: #999999;
    font-size: 18px;
    font-weight: 300;
}
    
    .stream-item:not(.no-header-background-module) {
    background: #000 none repeat scroll 0% 0% padding-box;
    border-left: 1px solid #E1E8ED;
    border-right: 1px solid #E1E8ED;
}
  
    .bird-topbar-etched {
    color: #612ac5;
    font-size: 21px;
    margin-top: 14px;
    margin-left: -24px;
    display: inline-block;
    transition: all 0.25s ease-in-out 0s;
}
  
    .fullname {
    font-weight: bold;
    color: #612ac5;
}
    
    a{
    color: #612ac5
}
    
    .btn.small {
    color: rgb(97,42,197);
    font-size: 13px;
    padding: 3px 11px 5px;
}
    
    .pretty-link b {
    color: #BBB;
}
    
    .Icon--protected {
    color: #BBB;
}
    
    .ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo {
    color: #612ac5;
    display: inline-block;
    font-size: 16px;
    line-height: 1;
    position: relative;
    top: 4px;
    padding: 0px 2px;
}
    
    .Icon--retweeted, .Icon--colorRetweeted {
    color: #612ac5;
}
    
    .ProfileAvatar {
    background: #612ac5 none repeat scroll 0% 0%;
    border: 5px solid #612ac5;
    border-radius: 12px;
    box-shadow: 0px 1px 1px rgba(136, 153, 166, 0.15);
    height: 200px;
    position: relative;
    width: 200px;
}
    
    body {
    color: #bbb;
    font-size: 14px;
    line-height: 18px;
}
    
    .ProfileTweet-actionButton.is-disabled, .ProfileTweet-actionButton.is-disabled:hover, .ProfileTweet-actionButton.is-disabled:focus {
    cursor: default !important;
    color: #bbb;
}
   
    .QuoteTweet-screenname {
    color: #bbb;
    font-size: 13px;
}
    
    .QuoteTweet-fullname {
    font-size: 14px;
    font-weight: bold;
    color: #612ac5;
}

.Icon--verified {
    color: #612ac5;
}
}
