css:
@-moz-document domain("forums.plex.tv") {
/* MovieFan's Dark Theme         */
/* 2015-08-13 00:27 (UTC -08:00) */

/* Global settings */
html, body {background-color: #323232; color: #777}
.row {max-width: none}
time, .Meta .Mitem.LastCommentDate, .DateUpdated {font-size: 0px}
time:after, .Meta .Mitem.LastCommentDate:after {content: attr(title); font-size: 12px; color: #ff8a00}
.DateUpdated:after {content: attr(title); font-size: 12px; color: #ccc}
.Signature {border-top: 3px solid}
.Count {background-color: #777; color: #ff8a00}

/* Square profile photos */
.Activity-Registration .ProfilePhoto, 
.Activity-WallPost .ProfilePhoto, 
.OnlineUserWrap .ProfilePhoto, 
.ItemDiscussion .ProfilePhoto, 
.Discussion .Author .ProfilePhoto, 
.MeBox > .PhotoWrap > .ProfilePhoto, 
.Tile .ProfilePhoto,
.ProfilePhoto, .ProfilePhotoMedium, 
.BlockColumn .ProfilePhoto,
.Excerpt img {border-radius: 5px !important}
.Leaderboard .ProfilePhoto {border-radius: 3px !important}

/* Navigation Bar */
.navbar {background-color: #323232; margin-bottom: 0px; border: 0px}
.navbar-collapse.collapse {background-color: #222; color: #222}
.navbar-collapse.collapse a {color: #ccc; border: 2px solid transparent}
.navbar-collapse.collapse a:hover {background-color: inherit; color: #ccc; border: 2px solid #D68840}
.navbar-collapse.collapse a.Selected {color: #D68840; background-color: inherit}

/* MeBox */
.MenuItems, .FlyoutMenu {background-color: #777; color: #ccc}
.MenuItems a, .FlyoutMenu a {color: #D68840}
.MenuItems a:hover, .FlyoutMenu a:hover {color: #D68840}

.MarkAllViewed, .EditProfileWrap {display: none}
.Section-CategoryList .MarkAllViewed {display: block}
.SpMarkCategoryViewed:before {content: "\f06e"}

/* Breadcrumbs */
span.Breadcrumbs {background-color: #222; border: 0px; padding: 0px; height: auto}
.Breadcrumbs .CrumbLabel.HomeCrumb a {color: #fff}
.Breadcrumbs .CrumbLabel.Last a {color: #ff8a00}
.Breadcrumbs .CrumbLabel a {border: 2px solid #222; padding: 0px 15px}
.Breadcrumbs .CrumbLabel a:hover {border: 2px solid #ff8a00; color: #ff8a00; padding: 0px 15px}

/* Search Box */
.well.search-form {background-color: #222; color: #777; padding: 0px; border: 0px}
.AdvancedSearch {background-color: #222; color: #777; padding: 15px; border: 0px; max-width: none}
.AdvancedSearch .InputBox {background-color: #ccc; color: #222; border: 0px; padding-left: 10px}
.AdvancedSearch .InputAndButton {max-width: 90%}
.AdvancedSearch .Button {background-color: #ccc; border: 0px}
.AdvancedSearch .P select {background-color: #ccc; border: 0px; padding : 2px}
.AdvancedSearch .token-input-list {background-color: #ccc; border: 0px; padding: 2px}
/* Search form Author autofill window */
div.token-input-dropdown {border: 0px}
div.token-input-dropdown ul li.token-input-dropdown-item, div.token-input-dropdown ul li.token-input-dropdown-item2 {background-color: #ccc; border: 2px solid #ccc}
div.token-input-dropdown ul li.token-input-dropdown-item:hover, div.token-input-dropdown ul li.token-input-dropdown-item2:hover {background-color: #ccc; color: #222; border: 2px solid #D68840}
div.token-input-dropdown ul li.token-input-selected-dropdown-item {color: #D68840}
/* Search result */
.DataList.DataList-Search {color: #ccc}
.DataList.DataList-Search a {color: #D68840 !important}
.DataList.DataList-Search li {background-color: #222; border: 2px solid #222; margin-bottom: 4px; padding-bottom: 0px}
.DataList.DataList-Search li:hover {border: 2px solid #D68840}

/* Category List */
.HomepageTitle {color: #ccc !important}
.CategoryHeading {background-color: #777 !important; border: 0px !important}
.CategoryHeading .ItemContent.Category {color: #ccc}
.CategoryDescription {color: #777}
.Item.Read {background-color: #222; padding: 5px 10px; border: 2px solid #222; margin-bottom: 2px}
.Item.Read:hover, .Item.Unread:hover, .Item.New:hover {border: 2px solid #D68840}
.Item .Meta a {color: #ccc !important}
.Item .Meta a:hover {color: #D68840 !important}
.ItemContent a.Title, .ItemContent .Title a {color: #ccc}
.Item.Unread, .Item.New {background-color: #444; padding: 5px 10px; border: 2px solid #444; margin-bottom: 2px}
a.Bookmark:before {color: #ccc}

/* Discussion Page */
.Discussions .IndexPhoto {float: right; position: relative; width: 250px}
.Discussions .IndexPhoto:after {content: attr(title); font-size: 90%; color: #ccc; padding-left: 10px}
.Discussions .IndexPhoto.Online:after {content: attr(title); background-color: #22b14c; color: #222; font-size: 90%; padding: 0px 5px; margin-left: 10px; border: 1px solid #22B14C; border-radius: 5px}
.Discussions .MItem.DiscussionAuthor {display: none}
.DataList .Item .Meta .Source {color: #ccc}
.Discussions .Options {float: left !important}
.Discussions .Item {padding: 5px 10px}
.editor-file-preview {background-color: #444; color: #777; text-shadow: none; border: 0px}
.editor-file-preview .file-data .filename {color: #ccc}

.Author .PhotoWrap.Online:after {content: "Online"; text-align:center; font-size: 80%; font-weight: normal; color: #22B14C; display: block; padding: 3px 5px 0px 0px;}
.Section-Discussion .Role_PlexPass .MItem.RoleTitle {top: 64px}

/* Message */
.MessageList .Item:hover {border: 2px solid #ff8a00}
.MessageList .Item {border: 2px solid #323232; background-color: #323232}
.MessageList h1 {color: #ccc}
.MessageList .AuthorInfo {color: #222}
.MessageList .Item-Header {background-color: #777; border: 0px; color: #ccc}
.MessageList .Item .Username {color: #ff8a00}
.MessageList .Item-Body {background-color: #222; border: 0px}
.MessageList .Message {color: #ccc}
.MessageList .Message p {color: #ccc}
.MessageList .Message ul li {color: #ccc}
.MessageList .Reactions {border-top: 3px solid; padding-top: 5px; margin-top: 5px}
.MessageList .MItem.Category {display: none}
.MessageList .Message ul li {color: #999}
.Message .Quote {background-color: #444}
.Message ol {color: #ccc}
.InlineTags {background: #777}
.InlineTags.Meta {color: #ccc}
.OptionsMenu .SpFlyoutHandle {color: #ccc}

/* Accepted Answers */
ul.AcceptedAnswers {border-top: solid 2px #22B14C; border-bottom: solid 2px #22B14C}
ul.AcceptedAnswers:before {background: #22B14C; color: #222}
ul.AcceptedAnswers .Item.QnA-Item-Accepted .Item-Header, .MessageList .Item.QnA-Item-Accepted .Item-Header {background-color: #22B14C}
.Item.QnA-Item-Accepted time:after {color: #444 !important}
.Item.QnA-Item-Accepted .QnA-Accepted {display: none}
ul.AcceptedAnswers .Item.QnA-Item-Accepted .Item-Body, .Item.QnA-Item-Accepted .Item-Body {background-color: #222; border: 2px solid #22B14C}
.Item.QnA-Item-Accepted:hover {border: 2px solid #ff8a00}
ul.AcceptedAnswers .Item.QnA-Item-Accepted {border: 0px}

/* Comment Box */
.MessageForm {background-color: #777; border: 0px; color: #222}
.MessageForm .TextBox, .MessageForm .Preview {background-color: #222; border: 0px; color: #ccc}
.MessageForm .Preview .Message p {color: #ccc}
.MessageForm .EmoticonBox {background-color: #222}
.MessageForm .Buttons {background-color: #777; border-top: 2px solid #323232}
.MessageForm .Buttons a.Button {background-color: #222; color: #ccc; border: 2px solid #222}
.MessageForm .Buttons a.Button:hover {border: 2px solid #ff8a00}

/* Filter Menu */
ul.FilterMenu {background-color: #222; padding: 0px; margin-bottom: 30px}
ul.FilterMenu li a {padding: 6px; border: 2px solid #222; margin: 0px; background-color: #222; color: #777}
ul.FilterMenu li a:hover {padding: 6px; border: 2px solid #ff8a00; margin: 0px; background-color: #222}
ul.FilterMenu li.Active a {border: 2px solid #ff8a00}
ul.FilterMenu .Reactions-BestOf {display: none}

/* Category Filter */
.CategoryFilter {background-color: #222; color: #ccc; padding: 10px; margin-bottom: 30px}

/* Popular Tags */
.Box.Tags {background-color: #222; color: #ccc; padding: 10px; margin-bottom: 30px}
.TagCloud li {background-color: #222; border: 2px solid #222}
.TagCloud li:hover {border: 2px solid #ff8a00}
.TagCloud li a .Count {color: #ff8a00}

/* Who's Online */
.WhosOnline {background-color: #222; padding: 10px}

/* Notification Box */
/* .InformMessage {display:none} */

/* Recent Activity Section */
.Activity .TextBox {background-color: #222; border: 0px; color: #ccc}
.Item.Activity {background-color: #222; padding: 10px; border: 2px solid #222; margin-bottom: 2px; color: #777}
.Item.Activity:hover {background-color: #222; border: 2px solid #ff8a00}
.Item.Activity .Title {color: #777;}

/* Leaderboard */
.Box.Leaderboard {background-color: #222; padding: 0px; margin-bottom: 30px}
.Box.Leaderboard  li a {padding: 6px; border: 2px solid #222; margin: 0px; background-color: #222; color: #777}
.Box.Leaderboard  li a:hover {padding: 6px; border: 2px solid #ff8a00; margin: 0px; background-color: #222}

/* User Profile */
.Profile .DataCounts {background-color: #222; border: 0px}
.Profile .FormWrapper .bodybox-wrap {background-color: #777; border: 0px; color: #222}
.Profile .Box.BadgeGrid {background-color: #222; padding: 10px}
.PhotoWrapLarge {background-color: #222; border: 0px; padding: 10px}
.PhotoWrapLarge .ChangePicture {background-color: #222; color: #ccc; border: 2px solid #222; padding: 5px; margin-bottom: 5px}
.PhotoWrapLarge .ChangePicture:hover {background-color: #222; color: #ccc; border: 2px solid #ff8a00; padding: 5px}
.Popup .Body {background-color: #444}
.sidebar .SideMenu .PanelInfo {background-color: #222; padding: 0px; margin-bottom: 30px}
.sidebar .SideMenu .PanelInfo li a {padding: 6px; border: 2px solid #222; margin: 0px; background-color: #222 !important; color: #777 !important}
.sidebar .SideMenu .PanelInfo li a:hover {padding: 6px; border: 2px solid #ff8a00; margin: 0px; background-color: #222}
.sidebar .SideMenu .PanelInfo li.Active a {background-color: #ff8a00 !important; color: #fff !important; border: 2px solid #ff8a00}
.Profile .TextBox {background-color: #222; color: #ccc; border: 0px}

/* Best Of */
/* The section is hidden but the coloring is here should you want to turn it back on */
.Tile {background-color: #222; border: 2px solid #777; margin: 20px}
.Tile:hover {background-color: #222; border: 2px solid #ff8a00}
.Tile .Message p {color: #ccc}
.Tile span.Author a {color: #ff8a00}

/* Form Footer */
.container-fluid.dark.footer .row {padding: 10px 60px}
}
