css:
@-moz-document domain("bukkit.pl") {
    body {
        background-color: #3A3A3A;
        color: white;
    }
    .primaryContent {
        background-color: rgb(58, 58, 58);
        border-bottom: 1px solid rgb(58, 58, 58);
    }
    .secondaryContent {
        background-color: rgb(58, 58, 58);
        border-bottom: 1px solid rgb(58, 58, 58);
    }
    .sidebar .section .primaryContent h3,
    .sidebar .section .secondaryContent h3,
    .nodeList .categoryStrip,
    .footer .pageContent,
    .subHeading {
        background-color: rgba(10, 12, 35, 0.92);
    }
    .navTabs .navTab.selected .tabLinks {
        background-color: rgba(10, 12, 35, 0.92);
    }
    .navTabs .selected {
        background-color: rgba(10, 12, 35, 0.92);
    }
    .navTabs {
        background-color: rgba(10, 12, 35, 0.73);
        border: 0;
        height: 51px;
    }
    .navLink a,
    .navLink a:link,
    .navLink a:visited {
        color: white;
    }
    .topHelper {
        background-color: rgba(10, 12, 35, 0.92);
    }
    .button {
        background-color: rgb(12, 14, 35);
    }
    .textCtrl {
        border-top-color: rgb(51, 51, 51);
        border-right-color: rgb(51, 51, 51);
        border-bottom-color: rgb(51, 51, 51);
        border-left-color: rgb(51, 51, 51);
        color: white;
        background-color: #3A3A3A;
    }
    .textCtrl:focus {
        border-top-color: rgb(51, 51, 51);
        border-right-color: rgb(51, 51, 51);
        border-bottom-color: rgb(51, 51, 51);
        border-left-color: rgb(51, 51, 51);
        color: white;
        background-color: #3A3A3A;
    }
    #taigachat_box {
        border: 0;
        background: rgb(58, 58, 58);
        color: white;
    }
    .username:link,
    .username:visited,
    .username:focus {
        color: white;
    }
    .messageText {
        color: white;
    }
    .messageList .message {
        background-color: #3A3A3A;
        border: 1px solid rgb(93, 93, 93);
    }
    .messageUserInfo,
    .messageContent {
        background-color: rgb(58, 58, 58);
    }
    .messageUserBlock,
    .messageUserBlock div.avatarHolder,
    .messageUserBlock .extraUserInfo {
        background-color: #3A3A3A;
    }
    .userBanner.Uzytkownicy {
        background-color: #565656;
        border-color: #8E8E8E;
        color: white;
    }
    .staffHeading {
        color: white;
        font-weight: 700;
    }
    .sectionFooter {
        background-color: rgb(58, 58, 58);
        border-bottom: 1px solid rgb(58, 58, 58);
    }
    .bbCodeQuote .quoteContainer {
        background-color: rgb(58, 58, 58);
    }
    .nodeList .categoryForumNodeInfo,
    .nodeList .forumNodeInfo,
    .nodeList .pageNodeInfo,
    .nodeList .linkNodeInfo {
        background-color: rgb(58, 58, 58);
        border-bottom: 1px solid rgb(58, 58, 58);
    }
    .discussionList .sectionHeaders {
        background-color: #0A0C23;
    }
    .discussionListItem {
        background-color: rgb(58, 58, 58);
        border-bottom: 1px solid rgb(58, 58, 58);
    }
    a.callToAction span {
        color: white;
        background-color: rgb(10, 12, 35);
    }
    label,
    dt,
    dd {
        color: white;
    }
    #QuickSearch,
    #QuickSearch .formPopup,
    #QuickSearchQuery {
        background-color: rgb(29, 31, 52);
    }
    #content .pageContent {
        width: 95%;
        background-color: #3A3A3A;
    }
    #taigachat_full #taigachat_message {
        width: 96%;
        height: 130%;
    }
    .contentSummary {
        color: white;
    }
    .primaryContent a {
        color: white;
    }
    a:link,
    a:visited {
        color: white;
    }
    .quickReply {
        background-color: rgb(58, 58, 58);
        border: 1px solid rgb(58, 58, 58);
    }
    .Menu .primaryContent {
        background-color: rgb(58, 58, 58);
    }
    .Menu .secondaryContent {
        background-color: rgb(58, 58, 58);
    }
    .Menu .secondaryContent:hover {
        background-color: rgb(58, 58, 58);
    }
    #taigachat_inchat {
        font-style: normal;
        color: rgb(255, 255, 255);
    }
    .dark_taigachat_full .taigachat_motd {
        margin: 10px 20px;
        padding: 3px;
        padding-left: 5px;
    }
    body.mce-content-body {
        color: white;
        background: rgb(58, 58, 58);
    }
    .mce-container,
    .mce-container *,
    .mce-widget,
    .mce-widget * {
        background: #3A3A3A;
        color: white;
    }
    .mce-menubtn span {
        color: white;
    }
    .mce-menubar .mce-menubtn button span {
        color: white;
    }
    .mce-panel {
        border: 0px;
    }
    .fa-facebook,
    .fa-rss-square,
    .forumModerators,
    .spigot,
    .sponge,
    .footerLegal,
    .breadcrumb,
    .taigachat_motd {
        display: none;
    }
    .titleBar h1 {
        color: #82D8D5;
    }
    .footer .pageWidth {
        max-width: 100% !important;
    }
    .topHelper {
        position: fixed !important;
        width: 0 !important;
        height: 0 !important;
    }
    .message .editDate {
        color: #D2D2D2;
    }
    .pairs dt,
    .pairsInline dt,
    .pairsRows dt,
    .pairsColumns dt,
    .pairsJustified dt {
        color: #95AADB;
    }
    .tabs {
        background-color: #3A3A3A;
    }
    body .dimmed,
    body a.dimmed,
    body .dimmed a {
        color: #F3E2E2;
    }
    .ugc a:link,
    .ugc a:visited {
        color: #80F6EE;
    }
    .sidebar .section .secondaryContent .footnote,
    .sidebar .section .secondaryContent .minorHeading {
        color: #A4E8EA;
        margin-top: 7px;
    }
    .sidebar .avatarList .userTitle {
        color: #0FF;
    }
    .profilePage .mast .section.infoBlock {
        border: 0px;
    }
    .profilePage .mast .followBlocks .primaryContent.avatarHeap {
        border-style: none;
    }
    .profilePage .mast .sectionFooter {
       border-left: 0px;
       border-right: 0px;
       text-align: center;
    }
    .PageNav a {
       background-color: #000;
    }
}
