css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mamba.ru") {
	.MW-Fix {
		max-width: 100% !important;
		padding: 0 10px !important;
	 }
	.ContactBanner {
		display: none !important;
	 }
	#HeadBlock {
		padding: 0 !important;
	 }
	#HeadBlock div[class*="banner"] {
		display: none !important;
	 }
	#Logo {
		display: none;
	 }
	.SearchResult {
		margin-top: 0 !important;
	 }
	.SearchResult a:visited {
		color: #ff0000 !important;
		font-weight: bold;
	 }
	.SearchResult .u-m-photo a:visited img {
		border: 1px solid #ff0000 !important;
		font-weight: bold;
	 }
	#SearchForm {
		background: none !important;
	 }
	.SearchBigPhotos li {
		background: #EDEEF0;
		border-radius: 10px;
		box-shadow: 0 0 3px #14869E;
		margin: 15px 1% 5px 1% !important;
		width: 29% !important;
	 }
	.SearchBigPhotos li .ThemeBody {
		border-radius: 10px;
	 }
	.SearchBigPhotos li .roundcorners {
		display: none !important;
	 }
	.SearchSmallPhotos li {
		background: #EDEEF0;
		border-radius: 10px;
		box-shadow: 0 0 3px #14869E;
		float: left;
		margin: 15px 1% 5px 1% !important;
		width: 26% !important;
	 }
	.SearchSmallPhotos li .ThemeBody {
		border-radius: 10px;
	 }
	.SearchSmallPhotos li .roundcorners {
		display: none !important;
	 }
	.MainBlockContainer {
		margin-bottom: 0 !important;
	 }
	.MainBlockRight {
		width: 100% !important;
	 }
	.MainBlockRight div[style*="height: 66px"] {
		display: none;
	 }
	.BlockLeader, .BottomInformers {
		display: none;
	 }
	.MainBlockLeft {
		margin-left: -9999px;
		position: absolute;
	 }
	.BlockMaketop {
		margin: -40px 0 0 10340px;
		position: absolute;
		width: 800px;
	 }
	.BlockMaketop i {
		margin-left: -9999px !important;
		position: absolute !important;
	 }
	.BlockMaketop .ap-i p {
		float: left;
		margin-left: 20px !important;
		padding: 0 !important;
	 }
	.BlockMaketop .ap-i p a.light {
		background-color: transparent !important;
		margin: 0 !important;
		padding: 0 !important;
	 }
	#CopyRights {
		display: none !important;
	 }
	#ContactsActions {
		left: 400px !important;
	 }
	#ContactList #RecoveryProfiles {
		display: none !important;
	 }
	#ContactList .adv-contacts {
		display: none !important;
	 }
	#AlbumMenu {
		left: 300px !important;
		top: 25% !important;
	 }
	#ContactsCont li {
		float: left !important;
		margin-right: 1% !important;
		width: 49%;
	 }
	.paginate {
		display: block !important;
	 }
	.b-photoline {
		display: none !important;
	 }
	#web-new_faces__0 {
		display: none !important;
	 }
	.vip_icon {
		display: none !important;
	 }
	.vip-block, .add-bonus, #xNewFaces, .new_faces__item {
		display: none !important;
	 }
	._reset.web-search-search_form.serp {
		background: #FFF !important;
		min-height: 173px !important;
	 }
	.MinimalWidth .MW-Fix {
		text-align: center !important;
	 }
	.web-search-search_results > .layout:not(.loading) > .tiles-list-wrapper {
		max-width: 100% !important;
	 }
	.web-search-search_form {
		height: 100%
	}
	#advTxt1, .txt1, .link-ad {
		display: none !important;
	}
	._reset.web-search-search_form .search-form-params .search-form-params-content {
		box-shadow: 0 1px 4px rgba(0, 0, 0, 0.3), 0 0 40px rgba(0, 0, 0, 0.1) inset;
  }
	.vk-button,.fb-button {
		display: none !important;
	}
	#web-promo-new_year2016-anketa__0 {
		display: none !important;
	}
	.web-promo-candy_box-anketa {
		display: none !important;
	}
	.web-search-search_results > .layout:not(.loading) > .tiles-list-wrapper > .tiles-list > .tiles-item {
		height: 160px !important;
		max-width: 180px !important;
	}
	._reset.web-user_face2.huge .img-responsive {
		height: 160px !important;
	}
	._reset.web-user_face2.huge .img-responsive img {
		height: 160px !important;
		width: 160px !important;
		margin-left: -78.5px !important;
	}
	
	
	
	
	
}
