css:
/* AGENT_SHEET */
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url-prefix(http), regexp(".*") {

xul|scrollbar, xul|scrollbarbutton, xul|thumb {-moz-appearance: none !important; }

xul|scrollbar[orient="vertical"] { width: 17px; }
xul|scrollbar[orient="horizontal"] { height: 17px; }
xul|scrollbar {
        background-color:#f0f0f0;
        border:none;
        }
::-webkit-scrollbar{
        width:17px;
        height:17px;
        background-color:#f0f0f0;
        border:none;
        }

xul|scrollbar thumb {
    background-color:#cdcdcd;border:1px solid #f0f0f0
}
xul|scrollbar thumb:hover { background-color:#dadada; }
xul|scrollbar thumb:active { background-color:#606060; }
xul|scrollbar[disabled="true"] thumb { background-color:#f0f; } /*though FF doesn't display disabled thumb at all */
::-webkit-scrollbar-thumb{
    background-color:#cdcdcd;border:1px solid #f0f0f0
    }
::-webkit-scrollbar-thumb:hover{
    background-color:#dadada;
    }
::-webkit-scrollbar-thumb:active{
    background-color:#606060;
    }
::-webkit-scrollbar-thumb:disabled{
    background-color:#f9f9f9;
    }

xul|scrollcorner { background-color:#f0f0f0; }
::-webkit-scrollbar-corner{
    background-color:#f0f0f0;
    }

xul|scrollbarbutton {
    background-color:#f0f0f0;
    background-image:url(data:image/png;base64,) !important;
    background-repeat:no-repeat;
    border: none;
    }
xul|scrollbarbutton[sborient="vertical"] { height:33px; }
xul|scrollbarbutton[sborient="horizontal"] { width:33px; }
::-webkit-scrollbar-button{
    background-color:#f0f0f0;
    background-image:url(data:image/png;base64,);
    background-repeat:no-repeat;
    }
::-webkit-scrollbar-button:vertical{
    height:33px;
    }
::-webkit-scrollbar-button:horizontal{
    width:33px;
    }
xul|scrollbarbutton[sborient="horizontal"][type="increment"] { background-position:0 -444px; }
xul|scrollbarbutton[sborient="horizontal"][type="decrement"] { background-position:12px -425px; }
xul|scrollbarbutton[sborient="vertical"][type="increment"] { background-position:-1px -391px; }
xul|scrollbarbutton[sborient="vertical"][type="decrement"] { background-position:-1px -358px; }
::-webkit-scrollbar-button:horizontal:increment{
    background-position:0 -444px;
    }
::-webkit-scrollbar-button:horizontal:decrement{
    background-position:12px -425px;
    }
::-webkit-scrollbar-button:vertical:increment{
    background-position:-1px -391px;
    }
::-webkit-scrollbar-button:vertical:decrement{
    background-position:-1px -358px;
    }
xul|scrollbarbutton:hover { background-color:#dadada; }
xul|scrollbarbutton[sborient="horizontal"][type="increment"]:hover { background-position:0 -548px; }
xul|scrollbarbutton[sborient="horizontal"][type="decrement"]:hover { background-position:12px -529px; }
xul|scrollbarbutton[sborient="vertical"][type="increment"]:hover { background-position:-1px -495px; }
xul|scrollbarbutton[sborient="vertical"][type="decrement"]:hover { background-position:-1px -462px; }
::-webkit-scrollbar-button:hover{
    background-color:#dadada;
    }
::-webkit-scrollbar-button:horizontal:increment:hover{
    background-position:0 -548px;
    }
::-webkit-scrollbar-button:horizontal:decrement:hover{
    background-position:12px -529px;
    }
::-webkit-scrollbar-button:vertical:increment:hover{
    background-position:-1px -495px;
    }
::-webkit-scrollbar-button:vertical:decrement:hover{
    background-position:-1px -462px;
    }
xul|scrollbarbutton:active { background-color:#606060; }
xul|scrollbarbutton[sborient="horizontal"][type="increment"]:active { background-position:0 -652px; }
xul|scrollbarbutton[sborient="horizontal"][type="decrement"]:active { background-position:12px -633px; }
xul|scrollbarbutton[sborient="vertical"][type="increment"]:active { background-position:-1px -599px; }
xul|scrollbarbutton[sborient="vertical"][type="decrement"]:active { background-position:-1px -566px; }
::-webkit-scrollbar-button:active{
    background-color:#606060;
    }
::-webkit-scrollbar-button:horizontal:increment:active{
    background-position:0 -652px;
    }
::-webkit-scrollbar-button:horizontal:decrement:active{
    background-position:12px -633px;
    }
::-webkit-scrollbar-button:vertical:increment:active{
    background-position:-1px -599px;
    }
::-webkit-scrollbar-button:vertical:decrement:active{
    background-position:-1px -566px;
    }
xul|scrollbarbutton[disabled="true"] { background-color:#f9f9f9; }
xul|scrollbarbutton[sborient="horizontal"][type="increment"][disabled="true"] { background-position:0 -756px; }
xul|scrollbarbutton[sborient="horizontal"][type="decrement"][disabled="true"] { background-position:12px -737px; }
xul|scrollbarbutton[sborient="vertical"][type="increment"][disabled="true"] { background-position:-1px -703px; }
xul|scrollbarbutton[sborient="vertical"][type="decrement"][disabled="true"] { background-position:-1px -670px; }
::-webkit-scrollbar-button:disabled{
    background-color:#f9f9f9;
    }
::-webkit-scrollbar-button:horizontal:increment:disabled{
    background-position:0 -756px;
    }
::-webkit-scrollbar-button:horizontal:decrement:disabled{
    background-position:12px -737px;
    }
::-webkit-scrollbar-button:vertical:increment:disabled{
    background-position:-1px -703px;
    }
::-webkit-scrollbar-button:vertical:decrement:disabled{
    background-position:-1px -670px;
    }

}
