css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(?!www\.).+.tumblr.com/(?!archive).*") {

  /*
  By Sorcerer
  https://userstyles.org/users/145873
  */

  /*remove players*/
embed[name="billy"], iframe#player, iframe#playlist, #hypPlayerCont, #HypPlayerScript, .hypPlayerAddCons, .usxnmahkaemoerexgjxc, #playlistW, #playerW, #audiostream, #WIKYoutube, #SCMYoutube {display:none !important;}
  /*playerW content position fix*/
  #contentW {top:0 !important;}
  #musicplayer {display:none !important;}
  
  /*remove freehostedscripts*/
  a[href^="http://freehostedscripts.net/"] {display:none !important;}
  
  /*contentW fix*/
  #contentW {height: 100% !important;}
  
  /*mouse cursor reset*/
  * {cursor:initial !important;}
}
