css:
@-moz-document url-prefix("http://streamcloud.eu/") {
/*== Transparency und Position-Fix ==*/
.jwcontrolbar {	opacity : 0.7 !important;
    			position: static;
    			top: 96%;
				transform: scale(1.1,1);}
.jwdisplayIcon {background: rgba(0,0,0, 0.4) !important;}


/*== kp ==*/
body, div.content, #page { background-color : #ebebeb !important; }

div#vmenubar, .header { border : 1px solid black; }

.top, li.active {background : none !important;}

.content {background-color: white !important;}

ul {padding: 2px; margin: -10px;}
  
li.active { border: 1px solid black;
background-color : rgba(100,100,100,0.2) !important;}

div#page .header h1 { padding-top: 13px; }

.addthis_toolbox {
  display: none; }

div#page .bottom {
  display: none; }

#turnon, #turnoff {
  display: none !important; }


#the_lights {display: block; height: 896px; opacity: 0.9;}

div#header a.logo {
  margin-top:9px;
  margin-left: -45px;
  background: url('http://i.imgur.com/uZWVGxI.png') no-repeat top;}

div#header {background: none;
    		background-color: rgba(0,0,0,0.8);
    		height: 55px;}

div#header ul li a			{color: white;}
div#header ul li a:hover	{color: rgba(0,102,255,0.5);}

div#header ul, .page {margin-top: 15px;}

#header {
    height: 55px; }

.alert-message {
    margin-top: 20px !important; }

.top, .info {
    margin-top: 40px; }

div#footer {
    margin-top: 35px;
    padding-bottom: 30px;
    border-top: 1px solid black;
	height: 90px; }

div#footer ul li a {
    margin-left: 15px; }

div#footer ul li {
    float: left; }

div#footer ul {
    float: left;
    margin-top: 6px;
    margin-left: 17px; }
}
