css:
@-moz-document domain("limelightgaming.net") {
/*
A Blue Theme for the LimeLight Gaming Forum
Version 0.3
Created by elyday
(c) by elyday
*/

.navbar {
 	background-color: #2A226E;   
}

header {
    background-image: url("http://img.elyday.net/2015_08/header.png");
}

.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus, 
.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:hover, .navbar-default .navbar-nav > .open > a:focus,
.navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus,
.navbar-default .navbar-brand:hover, .navbar-default .navbar-brand:focus
{
	background-color: #3C3293;
}

h4.catbg, h4.catbg2 , h3.catbg , h3.catbg2 , .table_list tbody.header td.catbg
{
	background: #2A226E;
}

div.cat_bar, div.roundframe div.cat_bar, div.roundframe div.cat_bar h3.catbg, div.title_barIC, div.title_barIC h4.titlebg
{
	background-color: #2A226E;
}

/* Tables */
table.table th,
#admin_content h3.titlebg,
#admin_content table tr.titlebg th,
div.title_bar,
div.title_bar h3.titlebg,
div.title_bar h4.titlebg,
table.table td.statsbar div
{
	background-color: #2A226E;
}
/**/

/* Buttons */
.mark_read .btn-group .btn-success,
.pagesection .btn-group .btn-success,
.postarea .btn-group .btn-primary,
.pagesection .floatright .btn-primary,
#admin_menu a.active,
#adm_submenus a.active,
ul.dropmenu li ul li a:hover,
.roundframe .btn-primary
{
	background-color: #483DA9;
}
.btn-primary, .btn-success, .btn-danger {
    background-color: #483DA9;
}

.btn-primary:hover, .btn-success:hover, .btn-danger:hover {
    background-color: #483DA9;
}

#admin_menu a.firstlevel:hover,
#adm_submenus ul.dropmenu li a:hover
{
    color: #483DA9;
}
/**/
footer {
    background-color: #2A226E;
    color: white;
}

footer div.container a,
a.new_win:link, a.new_win:visited {
    color: #BDBCBC;
}

/* Posts (Name Background) */
div.body_message div.post_wrapper h4 {
	background-color: #2A226E;
}
/**/

.sb-footer {
    background-color: #2A226E !important;
    color: white !important;
}

.sb-header {
    background-color: #2A226E !important;
    color: white !important;
}

.btn-success, .label-success {
	background: #483DA9 !important;
}
}
