css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zarabiam.com") {
    
    body{
        background: #000 url("http://subtlepatterns.com/patterns/blackorchid.png") repeat scroll 0 0 !important;
        font-family: Open Sans !important;
    }

    .thead {
        background: #17687c none repeat scroll 0 0 !important;
        border-bottom: 2px solid #151515 !important;
        border-radius: 3px 3px 2px 2px !important;
        color: #ffffff !important;
        height: 40px !important;
        line-height: 30px !important;
        padding-left: 16px !important;
        padding-top: 0 !important;
    }

    #forum_block {
        background: #202020 none repeat scroll 0 0 !important;
        border-radius: 3px !important;
        margin-bottom: 15px !important;
        margin-right: 235px !important;
        padding: 14px !important;
        width: auto !important;
        box-shadow:0 0 8px #000 !important;
    }

    .tborder {
        background: #151515 none repeat scroll 0 0 !important;
        border: 1px solid #282828 !important;
        border-collapse: unset !important;
        margin: 12px auto auto !important;
        padding: 2px !important;
        width: 100% !important;
    }
            
    .trow1 {
        background: #252525 none repeat scroll 0 0 !important;
        border: 0 none !important;
        padding: 5px 8px !important;
    }
    .trow1_summary {
        background: #252525 none repeat scroll 0 0 !important;
    }
    .trow2 {
        background: #252525 none repeat scroll 0 0 !important;
        border: 0 none !important;
        padding: 5px 8px !important;
    }
    .trow2_summary {
        background: #252525 none repeat scroll 0 0 !important;
    }
    .trow1, .trow2 {
        background: #252525 none repeat scroll 0 0 !important;
        border-bottom: 1px solid #151515 !important;
        border-top: 1px solid #303030 !important;
    }

    .forumbit_foot {
        display:none !important;
    }

    .tcat {
        display: none !important;
    }
    
    .menu ul {
        background: #17687c none repeat scroll 0 0 !important;
    }

    .menu ul a {
        background: #0a5768 none repeat scroll 0 0 !important;
    }
    #panel #panel_header {
    background: #17687c none repeat scroll 0 0 !important;
        border-radius: 2px !important;
        height: 26px !important;
        margin-bottom: 12px !important;
        width: 100% !important;
    }
    #panel_header_right{display:none !important;}
    #panel_header_left{display:none !important;}
    #messresult{background:#252525 none repeat scroll 0 0 !important;}
    .tfoot{background:#17687c none repeat scroll 0 0 !important; height:26px !important; border-radius:1px !important;}
    #posts{margin-top:-31px !important;}
        
    /*dodatki od linoskoczka*/
    
    .menu {
        padding: 10px 20px;
    }
    
    .menu ul {
        border-radius: 4px;
    }
    
    .menu .separate {
       background: #175B73;
    }
    
    .popup_menu {
        background-color: #151515 !important;    
    }
        
    .popup_menu .popup_item {
        background-color: #252525 !important;
    }
        
    .popup_menu a {
        color: #FFF !important;    
    }
    
    /* div[id^="pid"] a {
        color: #bfbfbf;
    } */
    
    blockquote {
        background-color: #838383 !important;
        border-radius: 5px !important;
        padding: 7px !important;
        border: 0 !important;
    }
    
    blockquote cite {
        border-color: #6a6a6a !important;
    }
    
    .codeblock, .codeblock .title {
        border-color: #7A7A7A !important;
    }

    .editor_control_bar {
        border: 0 !important;
    }
    
    table  {
        font-family: Arial !important;
    }
    
    .tborder {
        border-radius: 5px;
    }
}
