css:
@-moz-document domain(deviantart.com), domain(sta.sh) {

.username-with-symbol span.user-symbol {display: none!important}

.regular.username:after {content:""}
.banned.username:after  {content:"!"}
.username.premium:after{content:"\2a"}
.username.beta:after{content:"\42"}
.username.hell:after{content:"\68"}
.username.hell-beta:after{content:"\48"}
.username.senior:after{content:"\60"}
.username.volunteer:after{content:"\5e"}
.username.admin:after{content:"\24"}
.username.group:after{content:"g"}



.username:not(.regular):after {
font-family:'usersymbol-icons';
font-size:90%; 
font-weight: normal; 
letter-spacing:1px;
padding:3px;}

a.u.username:after {color:#304030;}

.username.super.group:after{color:#D86C2A!important}
.username.hell:after{color:#D86C2A!important}
.username.hell-beta:after{color:#D86C2A!important}
.username.banned:not(.external):after {color:#304030; font-family:inherit; font-weight:inherit; font-size:90%;letter-spacing:2px;}

.cc-copy a:after{color:#97a2a0 !important}
div.rowuser .username:after {color:#90a193}
div.deviousness_text .username:after {color:#90a193}

.external:before{font-size:75%;}
}
