css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(twitter.com) {
 /**/ 
      
  * {
    font-family: 'Roboto', 'Helvetica Neue', Helvetica, Arial, sans-serif !important;
  }
  
  body, .new-tweets-bar, .home-tweet-box, .AppContent { background-color: #fff !important;}
  
  .global-nav-inner {
    background-color: rgba(0,0,0,0.8) !important;
  }
  
  .Icon--bird { filter: brightness(10) !important; }
  
  .Icon--notifications, .Icon--home, .Icon--dm {
    color:#fff !important;
  }
  
  .Icon--notifications:hover, .Icon--home:hover, .Icon--dm:hover {
    color: #B7B7B7 !important;
    -moz-transition-duration: 0.25s;
    transition-duration: 0.25s;
  }
  
  .text { display:none !important; }
  
  .dashboard-right{
    display: none !important;
  }
  
  * { border: 0 !important; }
  * {border-radius: 0 !important;}
  
  #timeline{
    width: 100% !important;
    margin-left:0 !important;
  }
    
  .ProfileSidebar--withRightAlignment{
    display: none !important;
  }
  
  .avatar, .DashboardProfileCard-avatarImage, .DashboardProfileCard-avatarLink {
    border:0 !important;
    background: none !important;
  }
  
  .u-size2of2 { width:100% !important; }
  
  .count { background-color: rgba(0,0,0,0.0) !important;}
  .count-inner { background-color: rgba(255,255,255,0.5) !important; color: rgba(0,0,0,0.8) !important; }
  
  .DashboardProfileCard, .Trends, .header-inner, .WhoToFollow { display: none !important; }
    
  .GalleryNav--next{
    width: 30%!important;
  }

  a.media-forward {
    max-height: 100% !important;
  }

  .is-preview img {
    margin-top: 0 !important;
  }

  .multi-photos {
    height: unset;
    position: unset;
    overflow: unset;
  }

  .multi-photos .multi-photo.twitter-timeline-link {
    height: unset !important;
    position: unset;
    width: unset;
  }

  .multi-photos .multi-photo.twitter-timeline-link img {
    margin-bottom: 1em;
    position: unset;
  }
  
  .avatar:hover {
    -moz-transform:scale(1.5) !important;
     transform:scale(1.5) !important;
    -moz-transition-duration: 0.25s !important;
    transition-duration: 0.25s !important;
    z-index: 9999 !important;
  }

  /**/
}
