#droite {right:0px;display:none;background-position:top left;border-left:solid 2px black;background-image: url('http://idata.over-blog.com/2/02/63/57/animaux/fourmi-musclee.jpg') !important;background-position: top center !important;background-repeat: repeat !important;position:fixed;top:0px!important;bottom:0px;right:0px;border-left:solid 0px black !important;background-color:transparent !important;-webkit-box-shadow: inset 5px 0px 25px 0px #000;-o-box-shadow: inset 5px 0px 25px 0px #000;box-shadow: inset 5px 0px 25px 0px #000;
}

