div#data:before{content: "Information sur le joueur" !important;color: white !important;text-decoration:underline;text-align: center!important;font-size:0.85em !important;opacity:0.95!important;}
#pseudo:before{content: "Pseudo : " !important;color: white !important;font-size:0.9em !important;}
#tag_alliance:before{content: "Alliance : " !important;color: white !important;font-size:0.9em !important;}

div#data{
