input#requete.ui-autocomplete-input{margin-left : 5px !important; width: 95% !important; margin-bottom: 9px !important}
   
/* --- all bouton envoyé, etc... --- */
input[type=submit], input[type=button]{
     background: transparent!important; 
     border: 1px solid black !important;
     border-radius: 3px !important; 
     margin-right: 5px!important;
     margin-top: 5px!important; 
     font-weight: bold!important;
}
    
input[type=submit]:hover, input[type=button]:hover{
     background: white!important;         
}
    
input[type=submit]:active, input[type=button]:active{    
     background: -webkit-linear-gradient( top , #EAEAEA 0%, #30C457 100%) repeat scroll 0 0 rgba(0, 0, 0, 0)!important;
     background:    -moz-linear-gradient( top , #EAEAEA 0%, #30C457 100%) repeat scroll 0 0 rgba(0, 0, 0, 0)!important; 
     background:     -ms-linear-gradient( top , #EAEAEA 0%, #30C457 100%) repeat scroll 0 0 rgba(0, 0, 0, 0)!important; 
     background:      -o-linear-gradient( top , #EAEAEA 0%, #30C457 100%) repeat scroll 0 0 rgba(0, 0, 0, 0)!important; 
     background:         linear-gradient( top , #EAEAEA 0%, #30C457 100%) repeat scroll 0 0 rgba(0, 0, 0, 0)!important;           
}
   
/* --- bouton chat --- */  
#formulaireChat input[type=submit], #formulaireChat input[type=button], .sp-choose[type=button] {
  border : 2px solid white !important;
  background : transparent !important;
  box-shadow : none !important;
  color : black !important;
  border-radius : 4px !important;
  text-decoration : none !important;
    font-weight : bold!important;
}
  
#formulaireChat input[type=submit]:hover, #formulaireChat input[type=button]:hover, .sp-choose[type=button]:hover{
