  background : transparent !important;
  box-shadow : none !important;
  color : rgba(0,0,0,0.85) !important;
}
  
#formulaireChat input[type=submit]:active, #formulaireChat input[type=button]:active{
  padding-left : 6px !important;
  padding-right : 6px !important;
  color : white !important;
  border : 2px solid white !important;
  background : transparent !important;
}
    
.sp-choose[type=button]:active{    
  color : white !important;
  border : 2px solid white !important;
  background : transparent !important;       
}
   
/* --- bouton changement de chat --- */  
#formulaireChat span.sur_une_ligne a img{display : none !important}
#formulaireChat span.sur_une_ligne a {padding-left : 6px !important;padding-right : 3px !important;border : 2px solid white !important;background : transparent !important;box-shadow : none !important;color : black!important;border-radius : 4px !important;text-decoration : none !important;}

#formulaireChat span.sur_une_ligne a:hover{border : 2px solid rgba(0,0,0,0.7) !important;background : transparent !important;box-shadow : none !important;color : rgba(0,0,0,0.7) !important;}
  
#formulaireChat span.sur_une_ligne a:active{padding-left : 6px !important;padding-right : 3px !important;color :white !important;border : 2px solid white !important;background : transparent !important;}
  
.sur_une_ligne:after{content : "."; color : transparent;}

/* --- espace entre chat box d'envoie --- */
#formulaireChat{margin-bottom : 5px !important;}
 
/* --- new message chat --- */
#nouveauxMessages p:before {content: "» " !important;color : red !important;font-size:1em !important;font-weight:900 !important;padding-left : 8px !important;
  
      /*--- chrome ---*/
-webkit-animation-name: blinker !important;-webkit-animation-duration: 0.6s !important;-webkit-animation-iteration-count:infinite !important;-webkit-animation-timing-function:ease-in-out !important;-webkit-animation-direction: alternate !important;
    
    /*--- firefox ---*/
-moz-animation-name: blinker !important;-moz-animation-duration: 1.1s !important;-moz-animation-iteration-count:infinite !important;-moz-animation-timing-function:ease-in-out !important;-moz-animation-direction: alternate !important;
    
    /*--- Opera ---*/
-o-animation-name: blinker !important;-o-animation-duration: 0.6s !important;-o-animation-iteration-count:infinite !important;-o-animation-timing-function:ease-in-out !important;-o-animation-direction: alternate !important;
    
    /*--- vierge/opera ---*/
animation-name: blinker !important;animation-duration: 1.1s !important;animation-iteration-count:infinite !important;animation-timing-function:ease-in-out !important;animation-direction: alternate !important;
}     
    
