@-moz-document regexp(".*archiveofourown\\.org\\/works\\/(\\d+).*"), regexp(".*archiveofourown\\.org\\/chapters\\/(d+).*") {
.p2 {display:none;}
p { margin:auto 0px; }

#chapters {width:62%; min-width:500px !important;margin:auto;
font-family:PT Serif, Adobe Caslon Pro, Poppins, serif;font-size:120%;color:#181818;
padding:30px;background-color:#f7f7f7;border-radius: 4px;-webkit-box-shadow: inset 0 0 15px #888;}

#chapters p{text-indent:1.5em !important;}

.preface.group,.work.meta.group {background-color:#f7f7f7 !important;color:#181818;
  border-radius: 4px;-webkit-box-shadow: inset 0 0 15px #888;}
.chapter .preface.group {border-radius: 0px; -webkit-box-shadow: none;border:0px;}

.notes a, .summary a{color:#7a7a7a !important;text-decoration:none;}
.notes a:hover, .summary a:hover{color:#aaa !important;text-decoration:none;}

#main {background-color:#181818;color:#fff;}

.workskin {background-color:#7a7a7a; min-width:500px !important;}

#main a{color:#fff;}

#main a:hover{color:#7a7a7a;}

.work.meta.group, .work.meta.group a,.work.navigation.actions a{color:#000 !important; border-bottom:0px !important;}
.work.navigation.actions {padding-bottom:10px;}
.work.meta.group {background-color:#f7f7f7;}

.work.meta.group a:hover{color:#fff !important;background-color:#333;}

.userstuff.module p {text-align:justify;}

h2,.preface h2.title{text-shadow: 0px 2px 1px #E6D1D1;font-weight: 100;color:#333; font-family: Abject Failure,Pacifico,serif !important;font-size:48pt;}
h3.byline.heading{margin-top:-20px !important;padding-bottom:20px;}
.preface .byline a{color:#181818 !important;}
.preface .byline a:hover{color:#7a7a7a !important;}

h3.title a{color:#181818 !important; text-decoration:none;border-bottom:none;}

ul.actions a{color: #181818 !important;}
#series.series.module li > a, .workskin a {color:#181818 !important;}

#header .primary {background: #000;}
#header .primary .dropdown a {color:#fff !important;}
#header .primary .open a, #header .primary .dropdown:hover a, #header .primary .dropdown a:focus{color:#181818 !important;}

.series a, .series a:visited {color: #b3b3b3 !important;}

.notice, .comment_notice, ul.notes, .caution, .error, .comment_error,fieldset, form dl, fieldset dl dl, fieldset fieldset fieldset, fieldset fieldset dl dl, dd.hideme, form blockquote.userstuff{
background: transparent;
border: 0px solid #c2d2df;}
  
.thread .even {background: #181818;}
.comment h4.byline {background: #121212;}
.comment div.icon {border-bottom: 5px solid #121212;}
.icon {height:60px; width:60px;border-radius: 60px;}
}

