css:
@-moz-document url-prefix("http://jira"), url-prefix("https://jira") {
BODY {font-size: 14px}
BODY, #header, #dashboard-content, ul.vertical li.active {
  background-color: black
}
BODY[dir="ltr"] {
  background-color: silver !important
}
.dashboard, .command-bar, #main-content, #stalker, .detached .item-header, .stalker .command-bar, .footer, .issue-body-content, .command-bar, .aui-page-panel-content, #footer-logo {
  background-color: black !important
}

.footer {
  color: #555
}
ul.tabs li.active strong {
  color: white
}
ul.tabs li a {
  background-color: #444; color: #bbb
}

a.aui-nav-link   {color: #f2f2f2 !important}
.ajs-layer, #issuetable, #header-bottom #main-nav li.aui-dd-parent.selected {
  background-color: #CCC !important
}
.rownormal {
  background-color: #ddd !important
}

.gadget .dashboard-item-content {
  background-color: transparent !important
}
.dashboard ul.column li.gadget {
  border: none
}
#header-top div.aui-dd-parent a.lnk {
  color: #ddd
}
ul.operations li a.lnk {
  color: #cdcdcd
}

.active-area, .mod-content, .stalker .ops-cont, table.aui {
  background-color: #bbb !important
}

.webkit-css-keyword, .value {color: black !important}

/* Merge to */
.type-multiversion {
  float: left
}
.type-multiversion A {
  display: block; font-weight: bold; font-size: 30px; color: #F00000!important
}
.name[title="Merge to"] {
  font-size: 30px; color: #C00000; font-weight: bold; margin-left: -140px; width: 140px
}


a, a:link, a:visited {
  color: #0003FF
}

.breadcrumbs a {
  color: deepSkyBlue
}


#issuetable .issuerow:hover, .gadget #issuetable .issuerow.hover, #issuetable.hide-carrot > tbody > tr.issuerow:hover {
  background-color: #f0f0f0 !important
}
.issue-data-block.focused {
  background-color: #ccc
}
.issuePanelContainer TD {background-color: transparent !important}

.operations.enabled {background: #f2f2f2;}

h1 {color: #654982; font-style: italic;}

#key-val, #project-name-val, #parent_issue_summary {color: #E89A9A;}
a[rel="madlen.yordanova"], span[rel="madlen.yordanova"] {color: #EE52B9 !important;}

.aui-header {background-color: black !important;}
}
