css:
@-moz-document domain("forum.football365.com") {
    /**** GLOBAL AND TOP / BOTTOM AREA CHANGES ****/
    /* GETTING RID OF ADVERTS*/
    
    .fixedfluidfixed-content {
        width: calc(100%);
        float: left;
    }
    .fixedfluidfixed-right {
        display: none !important;
    }
    #page-footer > div:first-child {
        display: none !important;
    }
    /* END ADVERT SHITHOUSERY */
    
    #wrap {
        max-width: 100%;
    }
    .forumbg {
        border-radius: 0;
        margin-bottom: 8px;
    }
    .navbar {
        background-color: #eee;
        border-radius: 0;
    }
    .forumbg.announcement {
        margin-top: 7px;
    }
    .headerbar {
        background-color: #fff;
    }
    #logos {
        display: none !important;
    }
    #page-footer #leaderboard-2 {
        display: none;
    }
    /**** END GLOBAL AND TOP / BOTTOM AREA CHANGES ****/
    /**** NARROW SCREEN AMENDMENTS ****/
    
    @media screen and (min-width: 700px) and (max-width: 1176px) {
        .postbody {
            width: 80% !important;
        }
    }
    /**** END NARROW SCREEN AMENDMENTS ****/
    /**** THREAD AREA CHANGES ****/
    
    .post {
        border-radius: 0;
        margin-bottom: 2px;
    }
    .post .responsive-hide {
        display: none;
    }
    .content blockquote {
        background-size: 11px !important;
        padding: 7px;
    }
    blockquote.uncited {
        padding-top: 25px !important;
    }
    .postprofile {
        color: #666;
        border: none;
        float: left;
    }
    .postprofile {
        width: 12%;
    }
    .postbody {
        width: 87%;
    }
    .postbody {
        border-left: 1px solid #fff;
        padding-left: 10px;
    }
    .post .content {
        font-size: 13px !important;
        line-height: 1.5 !important;
    }
    .online {
        background-image: none !important;
    }
    .post {
        border: 1px solid #c4c4c4;
    }
    .postprofile .profile-posts a {
        font-weight: bold;
    }
    /**** END THREAD AREA CHANGES ****/
    /**** CHANGES POSTING AREA ****/
    
    #postingbox #smiley-box {
        float: left;
    }
    #postingbox #message-box {
        float: right;
    }
    /**** END POSTING AREA CHANGES ****/
    /**** CHANGE THE MAIN FORUM LIST ****/
    
    div.forumbg li.row div.list-inner {
        padding-top: 10px;
    }
    div.forumbg li.row div.list-inner div.responsive-hide {
        color: transparent;
        margin-top: -18px;
        text-align: right;
    }
    div.forumbg li.row div.list-inner div.responsive-hide a {
        display: block;
        float: right;
        margin-top: 3px;
    }
    div.forumbg li.row div.list-inner br,
    li.row div.list-inner div.responsive-hide span.imageset {
        display: none !important;
    }
    div.forumbg li.row div.list-inner div.pagination {
        display: inline-block;
        float: none;
        position: relative;
        top: -2px;
    }
    ul.topiclist .row {
        background-color: #eee;
    }
    li.row:hover {
        background-color: #eee;
    }
    .forum_unread,
    .forum_read {
        background-image: none !important;
    }
    .topiclist.forums .list-inner {
        margin-top: 11px;
        padding-left: 10px;
    }
    .sticky {
        background-color: #dfdfdf !important;
    }
    ul.topiclist.topics .responsive-hide a {
        margin-left: 4px !important;
    }
    /**** END HOME FORUM CHANGES ****/
    /**** HOME THREAD TITLES & PAGINATION ****/
    
    ul.topiclist.topics .pagination::before {
        content: "(";
        color: #777777;
    }
    ul.topiclist.topics .pagination::after {
        content: ")";
        color: #777777;
    }
    ul.topiclist.topics .pagination li a::after {
        content: ",";
    }
    ul.topiclist.topics .pagination li a {
        border: 0 none;
        font-size: 0.9em !important;
        font-weight: bold;
        background-color: transparent;
    }
    ul.topiclist.topics .pagination li a:hover {
        background-color: transparent;
        font-weight: bold;
        color: #d31141;
        text-decoration: underline;
    }
    .pagination li:last-of-type a:after {
        content: "" !important;
    }
    ul.topiclist.topics a.topictitle {
        font-size: 1.1em;
    }
    /**** END HOME THREAD TITLE & PAGINATION ****/
    /**** REMOVAL OF ICONS EVERYWHERE ****/
    
    ul.topiclist .row .topic_read,
    ul.topiclist .row .topic_unread,
    ul.topiclist .row .topic_unread_hot,
    ul.topiclist .row .topic_read_hot,
    ul.topiclist .row .topic_unread_hot_mine,
    ul.topiclist .row .topic_read_hot_mine,
    ul.topiclist .row .topic_unread_mine,
    ul.topiclist .row .topic_read_mine,
    ul.topiclist.topics .sticky_read_mine,
    ul.topiclist.topics .sticky_unread,
    ul.topiclist.topics .sticky_unread_mine,
    ul.topiclist.topics .sticky_unread_locked,
    ul.topiclist.topics .sticky_read_locked,
    ul.topiclist.topics .topic_unread_locked,
    ul.topiclist.topics .topic_read_locked,
    ul.topiclist.topics .topic_read_locked_mine,
    ul.topiclist.topics .topic_unread_locked_mine,
    ul.topiclist.cplist .sticky_read_mine,
    ul.topiclist.cplist .sticky_unread_mine,
    ul.topiclist.topics .sticky_read,
    ul.topiclist.cplist .global_read_locked,
    ul.topiclist.cplist .global_unread_locked,
    ul.topiclist.cplist .global_unread{
        background-image: none !important;
    }
    ul.topiclist.topics dt,
    ul.topiclist.cplist dt {
        background-image: none !important;
    }
    ul.topiclist.topics .row .list-inner,
    ul.topiclist.cplist .row .list-inner {
        padding-left: 7px;
    }
    .announcement ul.topiclist.topics .list-inner {
        padding-left: 45px !important;
    }
    /**** END ICON REMOVAL ****/
    /**** GET THE BELLS BACK ****/
    
    .imageset.icon_topic_newest {
        background-image: url("https://s22.postimg.cc/pp2x11sap/unread-circle.png");
        margin-top: 0;
        padding-left: 16px;
        padding-top: 14px;
    }
    /**** END OF BELLS BACK ****/
    /**** PERSONAL STYLES IN HERE (COPY FROM HERE IF UPDATING) ****/
    
    html,
    body {
        background-color: #e9e9e9;
    }
    #wrap {
        max-width: 1800px;
    }
    .panel {
        border-radius: 0 !important;
    }
    a {
        color: #255b79;
    }
    .forumbg.announcement {
        margin-bottom: 20px;
    }
    .action-bar.top {
        margin-bottom: 10px;
        margin-top: 10px;
    }
    a.topictitle,
    a.forumtitle {
        font-family: Arial !important;
        font-size: 13px !important;
        line-height: 18px;
        text-decoration: none;
    }
    .forumbg,
    .forabg {
        border: 1px solid #f2f2f2;
        padding: 0;
    }
    .forumbg .topiclist .header,
    .forabg .topiclist .header {
        background-color: #3d5e78;
        font-size: 13px;
        padding-bottom: 5px;
        padding-left: 23px;
        padding-top: 5px;
    }
    .navbar {
        font-size: 11px;
        background-color: #f4f4f4;
    }
    h2.forum-title {
        display: none;
    }
    .sticky .topictitle:before {
        content: "Sticky: ";
        color: #558e7c;
        font-weight: bold !important;
        font-size: 11px;
    }
    .sticky .imageset.icon_topic_newest {
        background-image: none;
        margin-top: 0;
        padding-top: 14px;
        padding-left: 0px;
    }
    ul.topiclist .row .topic_unread a.topictitle {
        font-weight: bold;
    }
    ul.topiclist .row .topic_read a.topictitle,
    ul.topiclist .row .topic_read_hot a.topictitle,
    ul.topiclist .row .topic_read_hot_mine a.topictitle,
    ul.topiclist .row .topic_read_mine a.topictitle {
        font-weight: normal !important;
    }
    ul.topiclist .row.bg1 {
        background-color: #fafafa !important;
    }
    ul.topiclist .row.bg2 {
        background-color: #ffffff !important;
    }
    ul.topiclist .row .sticky_read,
    ul.topiclist .row .sticky_unread,
    ul.topiclist .row .sticky_read_mine,
    ul.topiclist .row .sticky_unread_mine,
    ul.topiclist .row .sticky_unread_locked,
    ul.topiclist .row .sticky_read_locked {
        background-color: #efefef;
    }
    ul.topiclist .row .sticky_read a.topictitle,
    ul.topiclist .row .sticky_read_mine a.topictitle {
        font-weight: normal !important;
    }
    ul.topiclist li.row {
        border-bottom: none;
        border-top: none;
    }
    ul.topiclist .row .topic_read,
    ul.topiclist .row .topic_read_hot,
    ul.topiclist .row .topic_read_hot_mine,
    ul.topiclist .row .topic_read_mine,
    ul.topiclist.topics .topic_read_locked,
    ul.topiclist.topics .topic_read_locked_mine,
    ul.topiclist.cplist .global_read_locked {
        padding-left: 20px;
    }
    .sticky_read .list-inner,
    .sticky_read_mine .list-inner {
        padding-left: 10px !important;
    }
    li.row:hover dd {
        border-left-color: #fff;
    }
    .action-bar.bottom {
        background-color: #3D5E78;
        padding: 10px;
        color: #ffffff;
    }
    .action-bar.bottom a {
        color: #ffffff;
    }
    .action-bar.bottom .pagination a {
        color: #555555;
    }
    .action-bar.bottom .pagination a:hover {
        color: #ffffff;
    }
    .action-bar.bottom .buttons a {
        color: #555555;
    }
    .action-bar.bottom ul,
    .action-bar.bottom ul a {
        color: #555555;
    }
    p.jumpbox-return {
        font-family: Arial !important;
        font-size: 12px !important;
    }
    /* FRONT FORUM PAGE */
    
    ul.topiclist.forums .row {
        background-color: #fff;
    }
    .forabg {
        border-radius: 0;
    }
    .forabg .topiclist .header {
        padding-left: 10px;
    }
    .stat-block,
    form.headerspace {
        font-family: Arial !important;
        font-size: 11px !important;
        ;
    }
    /* THREAD AREA CHANGES */
    
    .postbody .content {
        font-family: Arial !important;
        color: #333333 !important;
    }
    .post {
        border: 1px solid #ededed;
        margin-bottom: 10px;
    }
    dl.postprofile a.username,
    dl.postprofile a.username-coloured {
        font-size: 11px !important;
    }
    p.author {
        font-family: Arial;
        font-size: 11px;
        font-weight: bold;
    }
    .postbody h3 {
        font-size: 13px;
    }
    .postbody h3.first {
        font-size: 13px;
    }
    .postbody {
        border-left: 1px solid #eeeeee !important;
    }
    .post.bg1,
    .post.bg2 {
        background-color: #ffffff;
    }
    .content blockquote {
        background-color: #f4f4f4 !important;
        border-color: #eeeeee;
    }
    /* POSTING AREA CHANGES */
    
    #postingbox.panel {
        background-color: #f7f7f7;
    }
    ul.topiclist .row .topic_unread_hot_mine .imageset.icon_topic_newest,
    ul.topiclist .row .topic_unread_mine .imageset.icon_topic_newest,
    ul.topiclist.topics .sticky_unread_mine .imageset.icon_topic_newest,
    ul.topiclist.topics .topic_unread_locked_mine .imageset.icon_topic_newest,
    ul.topiclist.cplist .sticky_unread_mine .imageset.icon_topic_newest {
        background-image: url("https://s22.postimg.cc/ckxcod7yp/unread-mine.png") !important;
    }
    /**** END OF PERSONAL STYLES (COPY TO HERE) ****/
}
