css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("clipafon.ru") {
  
   body{background: linear-gradient(to bottom  , rgba(0, 191, 255, 0.01) 23%, rgba(0, 0, 0,  0.45) 100%)!important;
  background-attachment: fixed !important;background-color: #000 !important;	background-clip: border-box !important;
	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important}
     *{font-family:Constantia!important;color: #3ce !important; text-shadow:none!important}          
  
 #main {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border:none!important}
  
  .panel  {background: rgba(0, 191, 255, 0.07)!important;box-shadow: 0px 0px 5px rgba(0, 0, 0, .99 )inset !important;border:none!important}  
      
   table.topvids .row1, table.newvids .row1, #favedit .row1, table.topvids th, #favedit thtable.topvids .row2, #favedit .row2, table.topvids .row2, #favedit .row2, .tth span, .rev .txt, table.newvids .row2, table.newvids th, .artists a:link, .artists a:visited {background: rgba(0, 0, 0, 0.57)!important;border:none!important}  
   
   .artists a:link:hover{background: rgba(0, 191, 255, 0.17)!important;border:none!important}  
    
  #cats a:link, #cats a:visited, #logged_menu a:link, #logged_menu a:visited, .fs a:link, .fs a:visited, #hmore .htab, .pgn a:link, .pgn a:visited, #revbox .pgn span, #newvids h1.mh, .colvids img, #itop a:link, #itop a:visited, #itop img{background:transparent!important;border:none!important}
  
  li:hover, tr.row1:hover, tr.row2:hover, form.std .submit:hover, #favedit .submit:hover, #cats .selected, #a-more .curr, #hmore .htab.curr:hover, #mvact a.act:link:hover, #mvact a.act:visited:hover, #revbox .pgn span:hover, #itop a:hover, #itop a:active {background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;border-radius:10px 0px!important;transition:all 550ms ease-in-out !important}
     
   .imag img, .tth img, .vids img, .vids img, .rvids img, .vids a:hover, .vids a:active, .rvids a:hover, .rvids a:active {background-color:transparent!important;border:none!important}
      
   #hmore .htab.curr .wrap {box-shadow: 0px 2px 0px #3ce!important}
      
  form.std input[type="text"], form.std input[type="password"], #search-box, form.std .submit, #favedit .submit, .fs li span, #abc-main a:hover, #abc-main a:active, #freview textarea, #freview select, #mvact a.act:link, #mvact a.act:visited{background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;transition:all 1s ease-in-out !important;border:none!important}    
  form.std input[type="text"]:hover, form.std input[type="password"]:hover, #search-box:hover{background: rgba(0, 0, 0, 0.5)!important; box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.2)!important;
   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.2)!important;
   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,191,255,0.2)!important;transition:all 1s ease-in-out !important}  
  
   .trim-x, .panel.uv, .panel.wlast{display:none!important}      }
