css:
@-moz-document domain("voat.co") {
body {
	overflow:hidden;
    max-height:100%;
}

.flat-list {
	font-size:14px;   
}

.link .flat-list {
	font-size:14px;   
}

.link .tagline {
	font-size:14px;   
}

.comment .tagline {
    font-size: 14px;
    float: left;
    padding: 5px;
    margin-right: 5px;
}

.tagline .score {
	display:block;   
}

.tagline .score {
	font-size:0px; 
}

.tagline .score span {
	font-size:14px;   
}

.tagline .score.dislikes:before {
	font-size:14px;  
	content:"Dislikes: ";   
}

.tagline .score.unvoted:before {
	font-size:14px;  
	content:"Unvoated: ";   
}

.tagline .score.likes:before {
	font-size:14px;  
	content:"Likes: ";   
}

.submission {
    padding:1px;
}

#container {
    border: 1px solid #222;
    border-radius: 3px;
    display: block;
    margin-left: auto;
    margin-right: auto;
    max-width: 98%;
    max-height: 740px;
    overflow: hidden;
    padding: 10px;
    position: relative;
}

.side {
    max-height: 700px;
    overflow-y: scroll;
}

#container > div.content > div.sitetable:first-of-type {
    max-height: 700px;
    overflow-y: scroll;
}

.usertext-body.original {
    max-height: 300px;
    overflow-y: scroll;
}

.commentarea {
    max-height: 240px;
    overflow-y: scroll; 
}

.well {
    max-height: 700px;
    overflow-y: scroll;   
}

::-webkit-scrollbar {
    width: 12px;
}
 
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
    border-radius: 10px;
}
 
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5); 
}
}
