.qz-grid{
  border-radius: 10px;
}
div.mod-side-nav:nth-child(1) {
  position: fixed !important;
  left: 5px !important;
  top: 390px !important;
  padding:20px 0px 15px 15px !important;
  transition: height 0.5s,margin 0.5s;
  margin-left: -150px!important;
}
div.mod-side-nav:nth-child(1):hover {
  height: auto !important;
  width: 140px !important;
  transition: height 0.5s,margin 0.5s;
  margin-right:0 !important;
  margin-left: -15px!important;
  border-radius:5px 5px 5px 5px !important;
}
#tab_menu_friend > div.qz-left > i,#tab_menu_care > div.qz-left > i,#tab_menu_me > div.qz-left > i{
  position:fixed; 
  left:5px ;
}
#tab_menu_friend > div.qz-left > i{
  position:fixed; 
  top:422px ;
}
#tab_menu_care > div.qz-left > i{
  position:fixed; 
  top:457px ;
}
#tab_menu_me > div.qz-left > i{
  position:fixed; 
  top:492px ;
}
