css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://mail.notes.na.collabserv.com/verse") {

/* IVAN */

/* V1.1 below */
.setSideBar .set-actions, .add-contacts, .remove-contacts {
 display: none;      
}
.carousel {
 display: none;   
}


/* V1 below */
.set-base.selectedSet {
    border-width: 3px;
    border-color: #325C80;
    margin: 7px 7px 0px 7px; 
}

.set-base.selectedSet.selected {
    border-width: 3px;
    border-color: #325C80;
    margin: 7px 7px 0px 7px; 
}

/* IVAN END */

/* Selected thread when loses focus */
 li.seq-msg-row.selected:before
 {
   background-color: #4178BE;
 }

/* DAVID De Olivera original style */

.carousel .loading-indicator {
    bottom: 18px;
  }
  .setSideBar.itm {
    height: 44px;
    width: 60%;
    
  }
  
  .setSideBar .set-actions {
      padding: 0px 0px 8px 20px;
      display: inline;
  }
  
  .setSideBar .add-contacts {
      margin-bottom : 0px;
  }

  .seq-window .seq-header {
    position: absolute;
    width: 37%;
    height: 26px;
    left: 61%;
    top: 0%;
  }
  
  .carousel.sets-overflown .scroll-button.left:after, .carousel.sets-overflown .scroll-button.right:before {
    height: 50px;
  }
  
  .active-sets:after {
    height: 30px;
    display: block;
    border-right: 2px solid #E8E8E8;
    content: "";
    position: absolute;
    right: -14px;
    top: 7px;
}

  .carousel .scroll-button {
    top: 16px;
  }
  .setBase {
    height: 25px;
    width: 25px;
    margin: 7px 3px 0px;
  }
  
  .set-base {
    border: 3px solid #F1F1F1;
    height: 25px;
    width: 25px;
    margin: 7px 7px 0px;
  }
  
  .set-base .setLabel {
    width: auto;
    top: -15px;
  }

  .keyset.waiting-for.show-focus-count .focus-count {
    margin-top: 12px;
    font-size: 12px;
    width: 22px;
    height: 10px;
    border-radius: 0px 0px 12px 12px;
    line-height: 10px;
  }
  
  .keyset.waiting-for.show-focus-count {
    background-position: center 10%;
    background-size: 8px auto;
  }
  
  .avatar .initials {
    font-size: 11px;
  }
  
  .seq-mcv.preview .inbox-container {
    width: 30%;
  }
  
  .seq-mcv.preview .left-header {
    width : 30%;
  }

  .preview-panel {
    width: 70%;
    left: 30%;
  }
}
