css:
@-moz-document domain("panda-community.com") {

.navTabs{
    background-image: url("http://i1219.photobucket.com/albums/dd432/skiffaa/Panda-Community/custom%20navbar/Navbar%20Slide/navB2ar1r1_zpskqgv2mmt.png"); 
    background-repeat: no-repeat;
    background-size: 160% 180%;
    background-position: left;
	width: 1170px;
    border-bottom: 0px solid rgba(174, 174, 174, 0); 
    box-shadow: inset 0 1px 0 rgba(3, 3, 3, 0);
   
}

.navTabs .navTab.PopupClosed .navLink{
    color:#4F4F4F;
	border: 0px;
    font-family: Verdana;
    font-size:12px;
    text-shadow: 0 0 0 transparent, 0 1px #2E2E2E;
    font-family:"Lucida Sans Unicode", "Lucida Grande", sans-serif;  
    font-weight: bold;
    font-size: 13px;

    
}
.navTabs .navTab.selected .navLink{
	background-color: rgba(21, 19, 19, 0.9); 
    color: #6F6F6F;
    border: 1px solid rgba(24, 23, 23, 0.63)
}
    .navTabs .navTab.selected .tabLinks{
      Background-color: rgba(24, 19, 19, 0.78);
      border-bottom-color: #000; 
      width: 1170px;

                                                              
}
.navTabs .navLink, .navTabs .SplitCtrl{
    text-shadow: 0 0 0 transparent, 0 1px 0 #130D0D; 
}
    .navTabs .navTab.selected .tabLinks a{
    color: #454545;
}

.hasTabLinks #navigation .navTabs .navTab.selected .navLink{
    border-bottom-color: #201414;
}
div[class="uix_navigationWrapper"] {display:block !important;
   position: relative;
    left: 372px;
    width: 20px;
    z-index: 1;
}

#logoBlock .pageContent {background-image: url("http://p1.pichost.me/i/68/1928753.png");
    background-size: 100%;
	background-repeat: no-repeat;
	background-position: center top;
    background-attachment: fixed;


}

div[id="uix_wrapper"] {display:block !important;

    background-image: url("http://p1.pichost.me/i/68/1928753.png");
   	background-size : 100%;
	background-repeat: no-repeat;
	background-attachment: fixed;
}

#content .pageContent{
	background-color: rgba(0, 0, 0, 0.75);     
    border: 1px solid #000;
}

.uix_collapseNodes .uix_icon{
	color: #000;
    top: 16px;
    left: 9px;
}
.uix_collapseNodes{

 border: 1px solid #fff;
    background-color: rgba(53, 53, 53, 0.47);
    height: 30px;
    width: 30px;
    border: 1px solid #232324;
    border-radius: 20px;

}

.uix_breadCrumb_toggleList li.toggleList_item{
    background-color: #050505;
    border: 1px solid #1A1919;
    border-radius: 100%;
}

.dark_postrating_positive{
	color: #1F13D3;
	font-weight: bold; 
	font-family: "Trebuchet MS", Helvetica, sans-serif;
    font-size:11px;
}

.dark_postrating_negative{
	color: #F220E7;   
    font-weight: bold; 
    font-family: "Trebuchet MS", Helvetica, sans-serif;
    font-size:11px;
}

.breadcrumb{
    background-color: rgba(15, 15, 15, 0.08);
    border: 1px solid #1f1f1f;
    border-radius: 10px;
}

.breadcrumb .crust a.crumb{
	color:rgb(63, 53, 53);
    background-color: #000;
   
}

.breadcrumb .crust:last-child a.crumb{
 color: rgba(182, 42, 186, 0.76);
}

.breadcrumb .crust .arrow span{
    border-left-color: rgba(255, 0, 252, 0.14);
}

.secondaryContent{
 	background-color: rgba(0, 0, 0, 0.13);   
}

.sidebar .section .secondaryContent{
    border: 1px solid #202020;
    border-radius: 10px;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3{
    background-color: rgb(10, 10, 10);
	color: rgba(184, 34, 182, 0.69);   
    border: 1px solid #202020;
    border-radius: 10px;
}

.tabs li a, .tabs.noLinks li{
    background-color: #1F1E1E;
	color: rgba(184, 34, 182, 0.69);   
    border: 1px solid #1B1B1B;
    border-radius: 0px 10px 0px 0px;
}

.tabs li.active a, .tabs.noLinks li.active{
    background-color: rgb(34, 32, 32);
    border: 1px solid #1B1B1B;
    border-radius: 10px 0px 0px 0px;
}

.tabs{
	border: 1px solid #000;
  
}

.sectionMain{
	background-color: rgba(11, 11, 11, 0.33);   
    border: 1px solid #2C2828; 
    border-radius: 10px;
}
.avatar img{
	background-color: rgba(69, 70, 71, 0.48);
}
.avatar img, .avatar .img, .avatarCropper{
	border-radius: 5px;   
}

.button{
 	background-color: #1D1D1D;
  	border-radius: 10px;
}

.button.primary{
    background-color: #171717;

}

.sidebar .visitorPanel .username {
    color: rgba(182, 42, 186, 0.76);
}

.node.level_2 .nodeInfo{
    border-top: 1px solid #000000;
}

.nodeList .node.level_2 .nodeInfo{
    background: rgba(0, 0, 0, 0.39);
}

.node .nodeLastPost{
	background-color: rgba(0, 0, 0, 0.4);  
}
.nodeList .categoryStrip .nodeTitle a {
	color: rgba(182, 42, 186, 0.76);  
}

.node .unread .nodeText .nodeTitle{
   color: rgba(184, 34, 182, 0.69);  
}

.node.level_1>.nodeList{
	background-color: rgba(15, 14, 14, 0);
    border: 1px solid #000;
}

.nodeList .categoryStrip{
    border-bottom: 0px solid;
}

.nodeList .categoryStrip .nodeTitle{
	background-color: rgba(0, 0, 0, 0.4)
}

.primaryContent a{
	color: rgba(184, 34, 182, 0.69); 
}

.textCtrl{
	background-color:rgba(18, 16, 16, 0.11);
    color: #838383;
    border: 1px solid #2C2828;
    border-radius: 10px;
}
.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
   background: url("http://i1219.photobucket.com/albums/dd432/skiffaa/Panda-Community/Message%20icons/panda_zpsepitesv2.png");

}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background: url("http://i1219.photobucket.com/albums/dd432/skiffaa/Panda-Community/Message%20icons/panda1_zpsbsa5ecnw.png")

}

.nodeLastPost a.avatar img{
    border: 1px solid rgba(147, 147, 147, 0.14) !important;
    
}

.uix_nodeTitle_status{
    background-color: #272727;
    color: rgba(184, 34, 182, 0.42);
    border-radius: 10px;
}

#taigachat_box{
    background: rgba(0, 0, 0, 0.36);
	border: 1px solid #000;
    border-radius: 5px;
    
}

#QuickSearch .primaryControls .uix_icon{
	background-color: rgba(184, 34, 182, 0.69);
    color: #000;
    border-radius: 0px 10px 10px 0px;
}

.profilePostListItem{
	border-top: 1px solid #222222;   
}

.username .style7{
	color: rgba(84, 84, 221, 0.81);   
}

.Menu {
	border: 1px solid #1B1A1A;
}

.Menu .primaryContent{
	background-color: rgba(0, 0, 0, 10);   
}
.Menu .sectionFooter{
	background-color:  rgba(13, 13, 13, 1);   
}

.sectionFooter{
	border-bottom: 1px solid #100D0D;
}

.Menu .secondaryContent {
	background-color: rgba(13, 13, 13, 1);
}
.navTabs .navTab.Popup.PopupContainerControl.PopupOpen .navLink{
	color: #413636; 
}
.primaryContent{
	background-color: rgba(3, 3, 3, 0.15);    
}
.profilePage .tabs.mainTabs{
	background: rgba(0, 0, 0, 0);  
}
.subHeading{
    background-color: rgb(5, 5, 5);
	color: rgb(45, 63, 74);  
    border-top: 1px solid rgb(36, 36, 36);
    border-bottom: 1px solid rgb(36, 36, 36);
}

.sectionFooter {
	background-color: rgba(0, 0, 0, 0);    
}

.blockLinksList .itemCount {
    color: rgb(27, 27, 27);
}
.dark_postrating_member{
	border-color: #2F2F2F;   
}
.profilePage .mast .followBlocks .primaryContent.avatarHeap{
	border-color: #2F2F2F;    
}
}
