css:
@-moz-document url("https://mail.notes.na.collabserv.com/verse"), url("https://mail.notes.na.collabserv.com/verse?") {

  
  /* Sender line on left and right panel */
  .seq-msg-row .name, .seq-msg-row.unread .name{
    color: #000000;
    font-weight: normal;
    font-size: 115%;
  }
  
  /* Subject line on left and right panels */
  .seq-msg-row .subject, .seq-msg-row.unread .subject{
    color: #5596e6;
  }

.socmail-calendarreadview .pimMailSubject{
	color: #000;
}
  
  /* Abstract on left panel */
  .seq-msg-row .abstract, .seq-msg-row.unread .abstract{
	display: none;
  }  
  
  /* Date on left panel */
  .seq-msg-row .date, .seq-msg-row.unread .date{
    color: #999999;
  }   
  
  /* Unread message line */
  .seq-msg-row.unread{
    background-color: #fafafa;
  }
  
  /* Unread message line */
  .seq-msg-row.unread .name{
    font-weight: bold;
  }  
  
  /* Unread message line hover */
  .seq-msg-row.unread:hover{
    background-color: #fafafa;
  }
  
  /* Unread message line left border */
  .seq-msg-row.unread:before{
    background-color: #5596e6;
  }

  /* Unread message line hover left border */
  .seq-msg-row.unread:hover:before{
    background-color: #5596e6;
  }  
  
  /* Name list in right panel */
  .socpimMailingList .socpimNameBtn, .socpimCombinedList, .name{
   color: #5596e6;
  }
  
  /* High priority mail */
  .seq-msg-row.importance .subject{
    color: #ff5050;
  }
  .seq-msg-row.unread.importance:before, .seq-msg-row.unread.importance:hover:before{
    background-color: #ff5050;
  }  
  
  .seq-msg-row.importance .priority{
    top: 23px;
  }
  
  .seq-msg-row.notice .view-icon.accepted, .seq-msg-row.appointment .view-icon.accepted{
    margin-top: 5px;
  }
  
  .seq-msg-row .msg-info{
    padding-top: 5px;
    padding-bottom: 5px;
  }
  
  .seq-msg-row .divider{
    height: 20px;
    background-color: #f1f1f1;
    line-height: 20px;    
  }

  .pimInvitees .socpimNameBtn, .pimPerson{
	color: #5596e6 !important;
  }

.pimInviteAction{
color: #FF8F3A;
}

.pimInviteFree .pimInviteAction{
	color: rgb(180, 224, 81);
}

.pimEventComment{
	padding-top: 5px;
}

.pimInviteState .pimTime{
	margin: 5px 0;
}

.socpimEventDateRangeRow, .socpimEventDateRangeRow .socpimEventFieldName, .socpimEventLocationRow .socpimEventFieldName{
	font-weight: bold;
}



  /* Mail content right panel */
  .socmail-mailreadview .pim-mailread-mailcontent{
    border-top: 1px solid #eeeeee;
    margin-top: 10px;
  }
  
  /* Hover thread */
  .seq-msg-row:hover{
    background-color: transparent;
  }
  
  /* Hover thread left border */
  .seq-msg-row:hover::before{
    background-color: #eeeeee;
  }
  
  /* Selected thread */
  .seq-msg-row.selected, .seq-msg-row.selected:hover{
    background-color: transparent;
  }
  
  /* Selected thread left border */
  .seq-msg-row.selected::before{
    background-color: #cccccc;
  }  
  
  .seq-msg-row.importance.selected:before, .seq-msg-row.importance.selected:hover:before{
    background-color: #cccccc;
  }    
   
  
  /* Right panel left border */
  .threaded .read-view.body-open::before{
    background-color: #cccccc;
  }
  
  /* Next meeting subject*/
  #nextMeeting .nextMeetingSubject{
    color: #5596e6;
  }
  
  /* Next meeting event bar */
  #eventBar .meeting{
    background-color: #5596e6;
  }
  
  /* Next meeting container */
  .eventPopup > .past-event.meeting, .eventPopup > .past-event.alldayevent{
    background-color: #5596e6;
  }
  
 .seq-mcv.preview .inbox-container {
    width: 30%;
  }
  
  .seq-mcv.preview .left-header {
    width : 30%;
  }

  .preview-panel {
    width: 70%;
    left: 30%;
  }  
  
  
  
  
  
  /* Update not YS */
.static-sets{
	margin-right: 14px;
}

.static-sets:after{
	height: 30px;
    display: block;
    border-right: 2px solid #E8E8E8;
    content: "";
    position: absolute;
    right: -7px;
    top: 7px;
}

  .carousel .loading-indicator {
    bottom: 18px;
  }

  .setSideBar.itm {
    height: 44px;
    width: 60%;
  }  

  .setSideBar .set-actions {
      padding: 2px 0px 8px 20px;
      display: inline;
  }

  .setSideBar .add-contacts {
      margin-bottom : 0px;
  }

  .seq-window .seq-header {
    position: absolute;
    width: 37%;
    height: 26px;
    left: 61%;
    top: 0%;
  }

  .carousel.sets-overflown .scroll-button.left:after, .carousel.sets-overflown .scroll-button.right:before {
    height: 50px;
  }

.active-sets{
	margin-right: 14px;
}

  .active-sets:after {
    height: 30px;
    display: block;
    border-right: 2px solid #E8E8E8;
    content: "";
    position: absolute;
    right: -7px;
    top: 7px;
  }

  .carousel .scroll-button {
    top: 16px;
  }  

  .set-base {
    height: 25px;
    width: 25px;
    margin: 7px 7px 0px;
  }  
  


  


  
  
  
  
  
  
  
  
  
  /* Update */
.set-base{
	border: 2px solid transparent !important;
}

.set-base.selectedSet, .set-base.selectedSet.selected{
	margin: 7px 7px 0px;
    border: 2px solid #4178be !important;
}

.set-base .new-indicator{
	top: -3px;
  right: -3px;
}

.keyset.inbox.show-focus-count, .keyset.needs-attention.show-focus-count, .keyset.waiting-for.show-focus-count{
    background-position: center 1px !important;
    background-size: 12px !important;
}

.keyset.inbox .setLabel, .keyset.needs-attention .setLabel, .keyset.waiting-for .setLabel{
    display: none;
}

.set-base .setLabel{
    font-size: 9px;
    margin-left: -55%;
    z-index: 2;
    top: -10px !important;
}

.people-set .ic-bizcard-section1 .action{
    width: 25px;
    height: 25px;
    padding: 0;
    padding-top: 1px;
    border: 2px solid transparent;
}

.people-set .ic-bizcard-section1 .action.act-compose{
    left: -16px;
    top: 15px;
}

.people-set .ic-bizcard-section1 .action.act-chat{
    left: 0px;
    top: 25px;
}

.people-set .ic-bizcard-section1 .action.act-card{
	left: 17px;
    top: 15px;
}

.people-set.chat-offline .ic-bizcard-actions .act-chat{
	border-color: transparent;
}

.people-set .ic-bizcard-section1 .action:hover, .people-set .ic-bizcard-section1 .action:focus{
	padding: 0px;
    padding-top: 1px;
    border-color: transparent;
    background-color: #e8e8e8;
  z-index: 999;
}

.avatar .initials{
	font-size: 16px;
}

.keyset.inbox.show-focus-count .focus-count, .keyset.needs-attention.show-focus-count .focus-count, .keyset.waiting-for.show-focus-count .focus-count{
    margin-top: 13px !important;
    font-size: 10px !important;
    width: 40px !important;
    height: 12px !important;
    border-radius: 0px 0px 12px 12px !important;
    line-height: 12px !important;
}

.seq-search-container .oti-model .shadow-input{
	background-color: transparent !important;
    top: 0px !important;
}

.seq-search-container .oti-view .placeholder, .oti-view .displayed{
	line-height: 20px;
}

.seq-search-container .chiclet{
	height: 22px !important;
    margin-top: 2px !important;
}

.seq-search-container .chiclet .chiclet-label{
	line-height: 22px !important;
}

.seq-search-container .chiclet.contactChiclet .show-tray-button{
	padding-top: 2px !important;
}

.seq-search-container .chiclet .chiclet-close{
	margin-top: 5px !important;
}

.seq-window .compose-button:hover, .seq-window .outgoing-button:hover{
	opacity: 0.8;
}

.outgoing-tray{
    left: 10px;
    top: -90px;
}

.dockable.has-action-bar .action-bar{
	height: 25px;
}

.seq-msg-row .hover-actions .attention-toggle-button .attention-icon .icon-iouo{
	margin-top: 0px;
}

.triage-actions{
	position: relative;
    top: 5px;
}

.triage-actions div.triage-action{
	padding-top: 12px !important;
}
  
.threaded .socmail-mailreadview.collapsed .status-icon.attention-toggle-button{
  padding-top: 0px !important;
}

.threaded .socmail-mailreadview.collapsed:hover .triage-actions .enabled, .threaded .socmail-mailreadview.collapsed:focus .triage-actions .enabled{
	padding-top: 0px !important;
}

.socmail-mailreadview .pim-mailread-mailcontent{
	margin-top: 0px;
}

.bar.event.meeting, .eventPopup > .meeting, .eventPopup > .past-event.meeting, .eventPopup > .past-event.alldayevent{
    background: #4178BE !important;
}

.bar.event.meeting, .eventPopup > .meeting, .bar.event.alldayevent, .eventPopup > .alldayevent, bar.event{
	background: #4178BE !important;
}  
}
