css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("instagram.com") {

/* color tweaks */
body {background-color: #222!important;}
.timelineCommentText {color: #ddd !important;}
.page-feed a, .page-feed a:visited { color: #bef !important; }
.page-feed { background: #353433 !important; color: #e9e8e7 !important; }
  
.-cx-PRIVATE-Photo__clickShield, .-cx-PRIVATE-Video__coverLayer {
    display: none!important;
  }
  
.-cx-PRIVATE-Video__states {
  pointer-events:none;  
}
  
.llRemainingCount, .llNameLink, .LikeList {
  text-shadow: none !important;
}
.llNameLink, .timelineCommentUsername, .timelineBookmarkInfoUsername, .sNameLink{
   color: #080 !important;
 }
  
.iContentArea, .LikeList, .ImageBlock, .FeedbackBar, .ibInner {
  background-color: #333 !important;
}
  
.Story.ImageBlock .ibContent {
  color: #ddd !important;
}
  
.-cx-PRIVATE-Shell__content {
  background-color: #222!important;
}
  
.-cx-PRIVATE-Shell__nav {
    border-bottom-width: 0px !important;
  }
  
  .-cx-PRIVATE-Post__feed .-cx-PRIVATE-Post__header {
    height: 35px !important;
    background-color: #333 !important;
    padding: 14px 10px;
  }
  
article, div, footer, header, main, nav, section, #react-root {
    border: 0px !important;
  }
  .coreSpriteNavigationBrandSmall {
    background-image: none !important;
  }
.-cx-PRIVATE-Navigation__brand {
    font-size: 21px !important;
    font-weight: 800 !important;
  }
  .-cx-PRIVATE-LikeButton__root {
    font-size: 13px !important;
    background: none !important;
  }
  .-cx-PRIVATE-PostInfo__optionsButton{
        font-size: 13px !important;
    display: none !important;
  }
  .-cx-PRIVATE-PostInfo__feedback, .-cx-PRIVATE-PostInfo__feedbackStackedVariant {
    height: 18px !important;
  }
  .coreSpriteHeartFull {
    margin-left: -5px !important;
    color: #FF5555 !important;
        font-weight: 800 !important;
  }
  .coreSpriteHeartOpen {
    font-weight: 800 !important;
    margin-left: -10px !important;
  }
  .-cx-PRIVATE-PostInfo__commentsLoadMoreButton {
    margin-left: -3px !important;
    font-weight: 800 !important;
  }
  .-cx-PRIVATE-Shell__nav {
    margin-bottom: -20px !important;
  }
  .-cx-PRIVATE-SearchBox__inactiveLabel, .-cx-PRIVATE-SearchBox__input {
    font-weight: 800 !important;
    color: #aaa !important;
    border: 0px !important;
    background-color: #333 !important;
    border-radius: 0px !important;
  }
  .-cx-PRIVATE-PostInfo__likesAndTimestampStackedVariant {
    margin-bottom:0px !important;
    margin-top:3px !important;
  }
  .-cx-PRIVATE-PostInfo__feedbackStackedVariant {
    margin-top: 0px;
}
  .-cx-PRIVATE-Util__hideText {
    text-indent: 0px;
  }
  .-cx-PRIVATE-PostInfo__feedback {
    min-height: 0px;
}
  
  .-cx-PRIVATE-PostInfo__comment {
    margin-bottom: 0px;
}
  
  .-cx-PRIVATE-PostInfo__commentsStackedVariant {
    margin-bottom: 0px;
}
  .-cx-PRIVATE-PostInfo__root, .-cx-PRIVATE-PostInfo__commentCreatorInput, .-cx-PRIVATE-PostInfo__commentsLoadMoreButton {
    font-size: 14px;
    line-height: 14px;
}
  .-cx-PRIVATE-Post__ownerAvatar {
    background-color: #333 !important;
    border-radius: 0% !important;
    border: 1px solid #333;
  }
  
.-cx-PRIVATE-PostInfo__root, .-cx-PRIVATE-Post__mediaInfo {
  background-color: #333 !important;
    padding: 0px 7px;
}
  
  .-cx-PRIVATE-PostLikers__root {
    color: #AAA !important;
  }
  
  .-cx-PRIVATE-PostInfo__comment {
    color: #AAA !important;
  }
  
  a {
    color: #0a7 !important;
  }
  
  .-cx-PRIVATE-Post__flexible.-cx-PRIVATE-Post__root {
    background-color: #444;
  }
.-cx-PRIVATE-Shell__footer {
  display: none !important;
}
/* center section */
.timelineCard { background: #353433 !important; border-color: #353433 !important; }
.timelineLikeList, .timelineBookmark { text-shadow: none !important; }
.timelineCenter, .timelineDividerText { background-color: #252423 !important; }
.timelineDividerLine { border-bottom: 1px solid #151413 !important; border-top: 1px solid #8b8685 !important; }
.timelineDividerText { text-shadow: none !important; color: #8b8685 !important; }

/* like section */
.timelineLikes { background: #373635 !important; border-bottom: 0 !important; }
.timelineLikeButton, .timelineLikes .mediaMoreButton { border: 0 !important; box-shadow: none !important; }

/* comment section */
.timelineCaption { border-bottom-color: #454443 !important; }
.timelineCommentTextField { border-color: #454443 !important; color: #e9e8e7 !important; }
.fadedTextField { background-color: rgba(0,0,0,0.2) !important; }
.fadedTextField:focus { background-color: #090807 !important; }

/* reset comment text link */
.timelineCommentText a { color: #e9e8e7 !important; }

/* top bar */
.top-bar { background: #090807 !important; }
.timelineTopPaginate { background: #454443 !important; text-shadow: none !important; border-bottom: 1px solid #555453 !important; }
a.timelineTopPaginate { color: #ffb !important; }

/* remove blue highlight at top bar */
.top-bar-actions > li > a.link-active, .top-bar-actions > li > a:active, html.touch .top-bar-actions > li > a.pressed { background-color: transparent !important; }

/* disable drop-shadow on user names */
.timelineBookmarkAvatar .imgImg, .timelineCommentAvatar .imgImg, .timelinePhoto .imgImg { box-shadow: none !important; }

}
