css:
@-moz-document domain("youtube.com") {
/* Original Theme (Used as base) By Pyro-Style: Colourful Youtube Theme */
/* Improved with new features by Opim10 */

/* Main Header - Change colour and fix bottom border */
#yt-masthead-container {
	background-color: /*[[MainAccent]]*/;
}
/* Main Header - Change the original logo to a white version */
#yt-masthead #logo-container .logo {
  background: url(data:image/png;base64,);
}

/* Main Header - New Upload Button */
.yt-uix-button-icon-material-upload{
	background: url(
data:image/png;base64,);
}

/* Main Header - Change the notification bell to white */
#yt-masthead-user .yt-uix-button-icon-bell,#yt-masthead-user .yt-uix-button-icon-bell:hover {
	background:url(
data:image/png;base64,);
    opacity: 100
}

/* Main Header - Change the notification bell to white */
#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell{
  background: no-repeat url(data:image/png;base64,);
  background-size: auto;
  opacity: 100;
}
#yt-masthead-user .sb-notif-on .yt-uix-button-icon-bell:hover {
  opacity: 50;
  }

/* Enable for black buttons - e.g like/dislike
.yt-uix-button-opacity, .yt-uix-button-opacity:hover{
	opacity:100   
}*/

/* Main Header - Progress line which appears above (Opim10) */
#progress {
 	background: #fff !important;   
}
#progress dd, #progress dt{
 	background: #fff !important;
    box-shadow: #fff 1px 0 6px 1px !important;
}

/* Main Header - Fix the search button's margins and height (Opim10) */
#masthead-search .search-btn-component{
 	height: 31px;
    margin-left: 1px;
    margin-top: -1px;
}

/* Main Header - Fix search bar's border and adjust the colour (Opim10) */
.masthead-search-terms-border{
    border: 1px solid #ffffff;
}
#masthead-search-terms input{
    color: /*[[MainAccent]]*/; 
}
.gssb_m{
    color: /*[[MainAccent]]*/; 
}
/* Main Header - Change the country code colour to white to match rest of theme (Opim10) */
#yt-masthead #logo-container span.content-region{
    color: #ffffff;
}

/* Main Header - Changing the colour of the page navigation tabs */
a.yt-uix-button-epic-nav-item.partially-selected:hover, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading{
  border-color: /*[[MainAccent]]*/;
}
.feed-header-feed-filter .yt-uix-button-epic-nav-item.selected, .feed-header-feed-filter .yt-uix-button-epic-nav-item:hover{
    border-color: /*[[MainAccent]]*/;
}
a.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled{
    border-color: /*[[MainAccent]]*/;
}

/* Main Header - Changing colour of cookies notification and adjusting buttons */
.yt-alert-default.yt-alert-info, .yt-alert-actionable.yt-alert-info, .yt-alert-naked.yt-alert-info .yt-alert-icon, .yt-alert-small.yt-alert-info{
 	background: /*[[MainAccent]]*/;   
}
.yt-alert-buttons{ 
 	padding: 0 30px;   
}

/* Main Header - Changing accent on account picker */
.yt-masthead-picker-footer{
 	background: /*[[MainAccent]]*/;   
}

/* Appbar Guide Menu (left) - Change the appbar guide menu's icon to white*/
.show-guide .yt-uix-button-icon-appbar-guide, .show-guide #masthead-positioner:hover .yt-uix-button-icon-appbar-guide {
    background: url(
data:image/png;base64,) !important;
}
.yt-uix-button-icon-appbar-guide {
background: url(
data:image/png;base64,) !important;
}

/* Appbar Guide Menu (Left) - Improve the appguide menu button box */
button#appbar-guide-button {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:hover {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
button#appbar-guide-button:active {
    box-shadow: 0 0 0 0;
    background-color: transparent;
    border: 0px solid;
}
#appbar-guide-menu {
    transition: ease-in-out 0.3s;
}

/* Appbar Guide Menu (Left) - Fix the appbar guide menu's arrow */
.not-yt-legacy-css #masthead-positioner #appbar-guide-button:hover .yt-uix-button-arrow, .yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
  border-top-color: #FFF;
}
.not-yt-legacy-css #masthead-positioner #appbar-guide-button .yt-uix-button-arrow, .yt-legacy-css #masthead-positioner:hover #appbar-guide-button .yt-uix-button-arrow {
  border-top-color: #FFF;
}

/* Appbar Guide Menu (Left) - Change the selected page's colour */
.guide-item.guide-item-selected, .guide-item.guide-item-selected:hover{
  background: /*[[MainAccent]]*/;
}

/* Appbar Guide Menu - Change the colour of the section headers */
li.guide-section h3 a {
  color: /*[[MainAccent]]*/;
}

/* Appbar Guide Menu (Left) - Adding a dropshadow and animation (Opim10) */
.guide-pinned .guide-pinning-enabled #appbar-guide-menu{
    box-shadow: 5px 10px 15px 5px rgba(0,0,0,.1);
}

/* Video Player - Changing the colour of the progress bar */
.ytp-play-progress {
  background: /*[[MainAccent]]*/;
}

/* Video Player (New) - Changing the colour of annotations and the auto play slider buttons */
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
  background: /*[[MainAccent]]*/;
}

/* Video Player (New) - Changing the colour of the volume slider */
.ytp-volume-slider-handle:before {
  background: /*[[MainAccent]]*/;
}

/* Video Player (New) - Changing the colour of the video quality badge */
.ytp-settings-button.ytp-hd-quality-badge:after, .ytp-settings-button.ytp-4k-quality-badge:after, .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-settings-button.ytp-8k-quality-badge:after{
  background-color: /*[[MainAccent]]*/;
}

/* Video Player (New) - Changing the background behind the controls - Enable which one you want / leave disabled (Opim10) */
/* Solid colour (bottom controls) 
.ytp-gradient-bottom{
    box-shadow: none;
    background: rgba(244,67,54,0.75);
    height:40px;
}*/

/* Fading gradient (bottom controls)
.ytp-gradient-bottom{
    box-shadow: inset 0 -120px 90px -90px rgba(244,67,54,.8);
}*/

/* Solid colour (bottom controls for fullscreen player)
.ytp-big-mode .ytp-gradient-bottom{
 	box-shadow: none;
    height:60px;
} */

/* Fading Gradient (bottom controls for fullscreen player)
.ytp-big-mode .ytp-gradient-bottom{
 	box-shadow: inset 0 -180px 135px -135px rgba(244,67,54,.8);
}*/

/* Solid colour (top controls)  
.ytp-gradient-top{
    box-shadow: none;
    background: rgba(244,67,54,0.75);
    height:40px;
} */

/* Fading Gradient (top controls)
.ytp-gradient-top{
	box-shadow: inset 0 120px 90px -90px rgba(244,67,54,.8);
}*/

/* Solid colour (top controls for fullscreen player) 
.ytp-big-mode .ytp-gradient-top{
 	box-shadow: none;
    height:60px;
} */

/* Fading gradient (top controls for fullscreen player)
.ytp-big-mode .ytp-gradient-top{
 	box-shadow: inset 0 180px 135px -135px rgba(244,67,54,.8);
}*/

/* Video Player (New) - Changing the colour of miscellaneous controls */
.ytp-chrome-controls .ytp-button-with-box:after{
  background-color: /*[[MainAccent]]*/;
}
.ytp-menuitem sup {
  color: /*[[MainAccent]]*/;
}

/* Video Player (Old) - Changing the colour of the volume slider*/
.ytp-volume-slider-foreground:before {
  background: /*[[MainAccent]]*/;
}

/* Video Player (Both) - Changing the colour of the scrubbing button */
.html5-progress-bar:focus .html5-scrubber-button, .html5-scrubber-button:active, .html5-scrubber-button:hover {
  background: /*[[MainAccent]]*/;
}

/* Video Page - Changing the loading spinner (Opim10) */
.yt-spinner-img, .yt-spinner-overlay {
    background: url(http://www.icmi.co.nz/Content/images/loading.gif) no-repeat center
}

/* Video Page - Fix the margin and the top-border colour of the "Show More" button (Opim10) */
.yt-card.yt-uix-expander .yt-uix-button-expander, .yt-card .yt-ui{
 	margin-top: -2px;
    margin-bottom: 1px;
    border-top-color: /*[[MainAccent]]*/; 
}

/* Video Page - Changing the colour of the autoplay slider button */
.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
  background-color: /*[[MainAccent]]*/;
}

/* Video Page - Changing the colour of the like scale */
.video-extras-sparkbar-likes {
  background: /*[[MainAccent]]*/ !important;
}

/* Video Page - Fixing the new subscriber amount button */
.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal {
 	border: 1px solid /*[[MainAccent]]*/;
        background: #ffffff;
}

/* Video Page - Fixing suggested videos' hover colour */
#watch7-sidebar .video-list-item a:hover .title, #watch7-sidebar .video-list-item a:hover .title .yt-deemphasized-text{
 	color: /*[[MainAccent]]*/;   
}

/* Video Page - Enabling card borders (Opim10) 
.yt-card{
 	border: 1px solid /*[[MainAccent]]*/;   
}*/

/* Video Page - Enabling extra borders in comment input box (Opim10) 
#yt-comments-sb-standin .box{
 	border: 1px solid /*[[MainAccent]]*/;
    border-top: 1px solid /*[[MainAccent]]*/;
    color: /*[[MainAccent]]*/;
    height: 36px;
}
#yt-comments-sb-standin .callout-outer{
 	border-top: 6px solid /*[[MainAccent]]*/;
    border-right: 6px solid /*[[MainAccent]]*/;
}*/

/* Video Page - Changing the colour of usernames and the like count in comments (Opim10) */
.comments .comment-header .user-name{
    color: /*[[MainAccent]]*/;
}
.comment-renderer-header .comment-author-text{
   color: /*[[MainAccent]]*/;
}
.comments .like-count{
    color: /*[[MainAccent]]*/;
}
.comment-renderer-like-count{
    color: /*[[MainAccent]]*/;
}

/* Video Page - Changing the colour of the link, username and reply colour in comments (Opim10) */
.comments .comment-replies-header .load-comments a, .comments .comment-replies-header .hide-comments a{
   color: /*[[MainAccent]]*/;   
}

/* Video Page - Changing the link colour in the description (Opim10) */
#action-panel-details:hover a{
    color: /*[[MainAccent]]*/;
}

/* Home Page - Fixing border on the "not interested" X button to be inline with video underneath it */
#browse-items-primary .item-section>li>.yt-lockup-tile .yt-uix-menu-top-level-button, .yt-section-hover-container .yt-uix-button-action-menu, .yt-section-hover-container .yt-uix-menu-top-level-button {
 	margin-right: 9px;   
}

/* Creator Pages - Adjusting the borders for the tabs */
.creator-editor-nav-tabs li.selected, .creator-editor-nav-tabs li:hover,.epic-nav-item.selected,.epic-nav-item:hover,.menu-tab:hover, .menu-tab.selected {
  border-bottom: 4px solid /*[[MainAccent]]*/;
}

/* Creator Pages - Changing the colour of the sidebar sub menu (also used in user settings) */
#creator-sidebar .creator-sidebar-item.selected, #creator-sidebar .creator-sidebar-submenu .creator-sidebar-item.selected a:hover{
background-color: /*[[MainAccent]]*/;
}

/* Manage Subscriptions Page - Fixing search input border and colour */
.subscriptions-filter .filter-field-container{
 	border: 1px solid /*[[MainAccent]]*/;
}
.subscriptions-filter input{
 	color: /*[[MainAccent]]*/;   
}

/* Browse Channels Page - Fixing search button height */
.channels-search .search-button{
 	height: 28px;   
}

/* Sitewide - Changing the colour of links (Opim10) */
a{
 	color: /*[[MainAccent]]*/;
}

/* Sitewide - Adjusting the link colour in feeds (Opim10) */
.yt-lockup-title a, .yt-lockup-byline a, .yt-lockup-byline:hover a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a{
 	color: /*[[MainAccent]]*/;
}
.feed-item-main a.feed-item-header, .feed-item-main a.yt-user-name{
   color: /*[[MainAccent]]*/;
}
.feed-item-container:hover .feed-item-header a, .feed-item-container:hover a.yt-user-name{
   color: /*[[MainAccent]]*/;   
}

/* Site Wide - Changing the style of the subscription button specifically to improve appearance (Opim10) */
.yt-uix-button-subscribed-branded, .yt-uix-button-subscribed-unbranded{
    border: 1px solid /*[[MainAccent]]*/;
    background-color: #ffffff;
}
.yt-uix-button-subscribe-unbranded:hover{
    border-color: /*[[MainAccent]]*/;
    background-color: #e9e9e9;
}
.yt-uix-button-subscribe-branded:hover{
    border-color: /*[[MainAccent]]*/;
    background-color: #e9e9e9;
}
.yt-uix-button.yt-uix-button-subscribe-branded, .yt-uix-button.yt-uix-button-subscribed-branded{
    color: #ffffff;
    background-color: /*[[MainAccent]]*/;
}
.yt-uix-button-subscribe-branded+.yt-subscription-button-subscriber-count-branded-horizontal, .yt-uix-button-subscribe-branded+.yt-uix-subscription-preferences-button+.yt-subscription-button-subscriber-count-branded-horizontal, .yt-uix-button-subscribed-branded.external+.yt-subscription-button-subscriber-count-branded-horizontal, .yt-uix-button-subscribed-branded.external+.yt-uix-subscription-preferences-button+.yt-subscription-button-subscriber-count-branded-horizontal{
 	border-color: /*[[MainAccent]]*/;
    background: #ffffff
}

/* Site Wide - Fixing the tick icon when subscribed (change it to white instead of grey) (Opim10) */
.yt-uix-button-subscribed-branded:before{
	background: no-repeat url(http://s.ytimg.com/yts/imgbin/www-hitchhiker-vfl1WP9DO.webp) -310px -158px;   
}

/* Site Wide - Updating site-wide button styles (Opim10) */
.yt-uix-button-default{
 	background: #ffffff;
    border-color: /*[[MainAccent]]*/;
    color: /*[[MainAccent]]*/;
}
.yt-uix-button{
    border-color: /*[[MainAccent]]*/;
    color: /*[[MainAccent]]*/;
}
.yt-uix-button-default:hover{
    border-color: /*[[MainAccent]]*/;
    color: /*[[MainAccent]]*/;
}
.yt-uix-button-default:pressed{
    border-color: /*[[MainAccent]]*/;
    color: /*[[MainAccent]]*/;
}
.yt-uix-button-primary, .yt-uix-button-primary[disabled], .yt-uix-button-primary[disabled]:hover, .yt-uix-button-primary[disabled]:active, .yt-uix-button-primary[disabled]:focus{
    border-color: /*[[MainAccent]]*/;
    background: #fff;
    color: /*[[MainAccent]]*/;
}
.yt-uix-button-primary:hover{
    border-color: /*[[MainAccent]]*/;
    background: #e9e9e9;
    color: /*[[MainAccent]]*/;
}
.yt-uix-button-primary:active{
    border-color: #e9e9e9;
    background: #e9e9e9;
    color: /*[[MainAccent]]*/;
}

/* Sitewide - Changing colour of input forms */
.yt-uix-form-input-select, .yt-uix-form-input-text, .yt-uix-form-input-textarea{
 	border: 1px solid /*[[MainAccent]]*/;
        color: /*[[MainAccent]]*/;
}
.yt-uix-form-input-select.focused, .yt-uix-form-input-checkbox:focus+.yt-uix-form-input-checkbox-element, .yt-uix-form-input-radio:focus+.yt-uix-form-input-radio-element, .yt-uix-form-input-text:focus, .yt-uix-form-input-textarea:focus {
 	border-color: /*[[MainAccent]]*/;   
}
}
