css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hoopladigital.com")
{
  body
  {
    background-color: black;
  }

  .labels span
  {
    color: whitesmoke;
  }

  .titleThumb img
  {
    width: 100%;
  }

 .thumbnail img
{
  width: 500px;
}

.medium-9
{
  width: 100%;
}  

.on-dark h2
{
  color: #be0;
}  
 .on-dark h3, .on-dark h4, .on-dark h5
{
  color: #32B0FF;
}  

.search-bar .columns 
  {
  border-bottom: black 0px hidden !important;
}
  .small-block-grid-2 > li
  {
    width: 33%;
  }

  input[type="text"], 
  input[type="password"], 
  input[type="date"], 
  input[type="datetime"], 
  input[type="datetime-local"], 
  input[type="month"], 
  input[type="week"], 
  input[type="email"], 
  input[type="number"], 
  input[type="search"], 
  input[type="tel"], 
  input[type="time"], 
  input[type="url"], 
  input[type="color"], 
  textarea
  {
    color: #1481C9;
    background-color: #333 !important;
    border-radius: 16px;
  }

  .autocomplete-suggestions
  {
    background-color: #86B1CC;
    border-color: #1481C9;
    border-bottom-left-radius: 16px;
    border-bottom-right-radius: 16px;
  }

  .autocomplete-suggestions .autocomplete-suggestion
  {
    color: white;
  }

  .autocomplete-suggestions .autocomplete-suggestion:hover,
  .autocomplete-suggestions .autocomplete-selected
  {
    color: black;
    background-color: #86B1CC;
  }

  
}
