css:
@-moz-document regexp("^((?!mod).)*reddit\\.com.*") {
.md blockquote, .md blockquote p {
    color: #ABABAB;
}

.report-reasons .report-reason, .report-reasons .report-reason-title {
    color: black;
}
 
.md p, .md td, tr th {
    color: rgb(215, 215, 215);
}
 
.robin--user-class--self .robin--username {
	color: #A7A7FF!important;
}

.res-nightmode .new-comment .usertext-body .md, .new-comment .usertext-body  {
        border: none !important;  
}
 
div.comment.thing.new-comment {
        border-left: solid 3px #7E8FAB !important;
}
 
.new-comment .usertext-body {
    background: none !important;
}
 
.res-commentBoxes div.comment {
    margin-left: 0px !important;
}
 
.comment .child, .comment .showreplies {
    margin-left: 5px !important;
}
 
.res-nightmode p:not(.tagline), .generic-table, .liveupdate-home .tagline, span[class^=robin-message]{
    color: rgb(221, 221, 221);
}
 
.md code, .md pre, .new-comment .md pre, .link .md :not(pre)>code, .usertext.border .md :not(pre)>code {
    border: 1px solid #507080;
    background-color: #353B3E;
    border-radius: 2px;
}

.thing.comment {
    margin-bottom: 4px !important;
}

.res-nightmode.res-commentBoxes .comments-page .comment, .res-nightmode.res-commentBoxes .comments-page .comment .comment .comment, .res-nightmode.res-commentBoxes .comments-page .comment .comment .comment .comment .comment, .res-nightmode.res-commentBoxes .comments-page .comment .comment .comment .comment .comment .comment .comment, .res-nightmode.res-commentBoxes .comments-page .comment .comment .comment .comment .comment .comment .comment .comment .comment {
    background-color: #1B1B1B !important;
}

.keyNavAnnotation {
	display: none;
}

.liveupdate-listing li.liveupdate iframe{
    background-color: white;
}

#liveupdate-statusbar p{
    color: #4f4f4f !important;
}

.infobar, .reddit-infobar {
    background-color: #000000;
    border-color: #507080;
}

.res-commentBoxes .comment {
    border-left: none !important;
    border-right: none !important;
    border-bottom: none !important;
}

.action-form {
    background-color: #191919;
}
}
