css:
@-moz-document domain("web.whatsapp.com") {
body {
    background-color: #FFF46D;
    background-image: linear-gradient(to bottom, #F3B9D8 20%, #FFF46D 100%)
}

.app-wrapper::before {
    background-color: #F5B5D0;
}

.pane-intro {
    background-color: #FFFACB;
}

.intro-image {
    background-image: url(https://mlpforums.com/uploads/post_images/img-2813094-1-fluttershy_has_a_valentine_for_me_by_vladimirmacholzraum-d5utc7w.png);
}

.pane-header {
    background-color: #fffacb;
}

.pane-chat-tile-container {
    background: linear-gradient(to bottom, #fffacb 0%, #e189b3 100%);
}

.butterbar {
    background-image: linear-gradient(to bottom, #0DBEA5, #075B4D);
}

.dropdown,
.dropdown-picker {
    background-color: #E189B3;
}

.backdrop {
    background-color: #FFF46D;
}

.popup {
    background-color: #FFF797;
}

.btn-plain {
    color: #EB81B4;
}

.btn-default {
    color: #FFF797;
    background-color: #EB81B4;
}

.subheader-search {
    background-color: #E189B3;
}

.cont-input-search {
    background-color: #F3B9D8;
    border: 1px solid #E189B3;
}

input {
    background-color: #F3B9D8;
}

.chat {
    background-color: #FFFACB;
}

.chatlist .chat:hover,
.chatlist .location:hover,
.chatlist .chat.hover,
.chatlist .hover.location {
    background-color: #FFF46D;
    border-bottom: 1px solid #FFF46D
}

*::-webkit-scrollbar-thumb {
    background-color: #FFF46D;
}

*::-webkit-scrollbar {
    background-color: #FFFACB;
}

.pane-chat {
    background-image: linear-gradient(to bottom, #FFFACB, #FFFACB);
}

.message-in {
    background-color: #FFF46D;
}

.message-out {
    background-color: #F5B5D0;
}

.message-system-body {
    background-color: #F5B5D0;
}

.btn-more {
    background-color: #F5B5D0;
}

.pane-chat-footer {
    background-color: #E189B3;
}

.input-container {
    background-color: #F5B5D0;
}

.menu-icons-item {
    background-color: #E189B3;
}

.block-compose .input-container {
    background-color: #F5B5D0;
}

.message-out:before {
    background-image: none;
}

.message-in:before {
    background-image: none;
}

.chatlist .chat.active,
.chatlist .active.location {
    background-color: #F5B5D0;
    border-bottom: 1px solid #F5B5D0;
}

.drawer-header {
    background-color: #E189B3;
}

.drawer-info {
    background-color: #FFFACB;
}

.well {
    background-color: #FFFACB;
}

.drawer-profile .drawer-header {
    background-color: #E189B3;
}

.drawer {
    background-color: #FFFACB;
}

.avatar-overlay {
    color: #FFFACB;
}

.toast {
    background-color: #DD71AF;
    color: #FFFACB;
}

.pane-intro:after {
    border-top: 6px solid #DD71AF;
}

.message-out .btn-context:after {
    background: linear-gradient(to bottom left, #F5B5D0 0%, #F5B5D0 35%, rgba(220, 248, 198, 0)65%, rgba(220, 248, 198, 0)100%);
}

.message-in .btn-context:after {
    background: linear-gradient(to bottom left, #FFF46D 0%, #FFF46D 35%, rgba(220, 248, 198, 0)65%, rgba(220, 248, 198, 0)100%);
}

.block-compose .input-container {
    border-color: #F5B5D0;
}

.dropdown .menu-item:hover,
.dropdown .menu-item:focus {
    background-color: #FFFACB;
}

.list-title {
    background-color: #FFF46D;
}

.drawer-header {
    color: #FFF46D;
}

.intro-title {
    color: #E189B3;
}

.message-out .btn-context {
    background-color: #E189B3;
}

.message-in .btn-context {
    background-color: #FFFACB;
}

.context.context-in {
    background: linear-gradient(to right, rgba(255, 255, 255, 0)0%, #FFF46D 50%)
}

.context.context-out {
    background: linear-gradient(to right, rgba(255, 255, 255, 0)0%, #F3B9D8 50%)
}

.app {
    background-color: #E189B3;
}

.app-wrapper::after {
    background: linear-gradient(to bottom, #FFF46D 0%, #F3B9D8 40%)
}
}
