css:
@-moz-document domain("bunker.lunavod.ru") {
.topic {
	border-radius: 3px;
    box-shadow: none;
    -webkit-box-shadow: none;
}
#wrapper{
	background: #303030;
    border-bottom: none;
}
#content {
	background: #303030;
}
body {
	color: #FFFFFF;
}
.comment {
	border: none;
    background: #424242;
}
.nav.nav-main {
	background: #000000;
}
.avatar {
	border-radius: 50%;
    border: none;
}
.comment-avatar {
	border-radius: 50%;
    border: none;
}

a {
	color: #26A69A;
}
.text a:hover, .topic .topic-header .topic-title a:hover, .item-list li a:hover {
	color: #80CBC4;
}
.text a:visited, .topic .topic-header .topic-title a:visited, .item-list li a:visited {
	color: #00695C;
}


.comment-delete.link-dotted {
	color: #E91E63;
    text-decoration: none;
}
a.link-dotted {
    border-bottom: none;
}
.comment.comment-self {
	background: #212121;
}
.topic {
	background: #424242;
    border: none;
}
.topic .topic-content.text {
	color: white;
}
.topic.topic-type-talk {
	background: #424242;
}
.topic.topic-type-talk .topic-header {
	background: #424242;
}
.topic.topic-type-talk .topic-content {
	background: #424242;
}
.topic-info {
	background: #424242;
}
.topic.topic-type-talk .topic-footer .topic-info {
	background: #616161;
}
.topic .topic-footer .topic-info li {
    border: none;
}
.topic.topic-type-talk .topic-header {
    border: none;
}
.topic.topic-type-talk .topic-content {
    border: none;
}
body {
	background-color: #303030;
}
.stat-performance { display: none; }
.profile-page-header {
    color: white;
    background: #616161;
    border-top: none;
}
h2.page-header {
    color: #FFFFFF;
}
.profile .vote {
    background: #303030;
}
.vote .vote-up, .vote .vote-down {
    display: inline-block;
    cursor: pointer;
    vertical-align: text-top;
    width: 14px;
    height: 14px;
    background: url(http://bunker.lunavod.ru/images/icons.png) no-repeat;
    background-image: url(http://bunker.lunavod.ru/images/icons.png);
    opacity: 3;
    filter: alpha(opacity=30);
}
.vote .vote-up {
    background-position: -408px -96px;
}
.vote .vote-down {
        background-position: -433px -96px;
}
.nav.nav-pills li a {
    background: #212121;
    color: white;
}
.nav.nav-pills li a:hover {
    background: #424242;
    color: white;
}
.nav.nav-pills li a.active{background: white;}
.block .block-content {
    background: #212121;
}

.topic .topic-footer .topic-info {
    background: #616161;
}

.item-list li .author {
    font-weight: bold;
    color: white;
}
.author {
    font-weight: bold;
    color: white;
}
.topic .topic-footer .topic-info li.topic-info-author a {
    font-weight: bold;
    color: white;
}

.block .block-header {
    background: #424242;
    border-top: none;
}
.block .block-header h3 a {
    color: white;
}
.nav.nav-pills li.active a {
    background: #111111;
}
.nav.nav-filter li a {
    background: #212121;
    color: white;
}
.nav.nav-filter li a.new {
    background: #222222;
}
.nav.nav-filter li a.new:hover {
    background: #333;
}
#sidebar.sidebar-left {
    border: none;
}
.nav.nav-main li.active a {
    background: #212121;
}
.nav.nav-main li a:hover {
    background: #212121;
}

.blog {
    background: #212121;
    border: none;
}
.item-list li {
    border: none;
}
.item-list {
    border: none;
}
.item-list.author a {color: white;}
#blog-more{
	background: #424242;
}
.blog .blog-content {
    border: none;
}
.blog .blog-header {
    border: none;
}
.blog .blog-footer {
    border: none;
}
.user, .ls-user {
    color: #26A69A;
}
.icon-user {
    background: url(http://bunker.lunavod.ru/images/icons.png) no-repeat; 
    background-position: -168px 0;
}
.table th {
    background: #424242;
}
.table td, .table th {
    border: none;
}
.search-abc {
    background: #424242;
}
.search-abc li.active a {
    color: white;
}
.stream-list li.stream-item {
    background: #424242;
}
.stream-comment-preview {
    background: #424242;
}
.stream-list li.stream-header-date {
    background: none;
    color: white;
    border: none;
}
.stream-get-more {
    background: #424242;
    border: none;
}
.nav.nav-filter li.active a {
    background: #424242;
}
.nav.nav-filter li a:hover {
    background: #414141;
}
.pagination ul li.active span {
    background: #424242;
}
.pagination ul li span, .pagination ul li a {
    background: #424242;
    color: white;
}
.spoiler-title {
    background: #212121;
    border: none;
}
.spoiler_title {
    background: #212121;
    border: none;
}
.comment.comment-new {
    background: #004D40;
}
.comment.comment-current {
    background: #01579B;
}
.modal {
    background: #424242;
}
.modal .modal-header {
    background: #424242;
    border: none;
}
.modal.modal-write .write-list li a {
    color: #26A69A;
}
.block.block-type-profile .upload-photo {
    background: none;
}
.form-profile .avatar-change {
    background: none;
}
fieldset legend {
    color: white;
}
fieldset {
    border: none;
}
.modal {
    color: white;
}
.input-text {
    border: none;
}
