css:
@-moz-document url-prefix("http://www.google.com/calendar/"), url-prefix("https://www.google.com/calendar/") {
body {
    padding: 0 !important;
    margin: 0 !important;    
    overflow: hidden !important;
    
}

#calcontent {
	padding: 0;
	margin-top: -10px;
    margin-bottom: -12px;
}

#topLeftNavigation {
    left: 140px !important;
}

#onegoogbar, #vr-header, #dp_h, #clst_my, #clst_fav, #navfooter, #searchAddCalBox, #sidebar {
    display: none;
}

::-webkit-scrollbar {
    width: auto;
    height: auto;
    overflow: hidden;
}

#calcontent.eui-t #vr-nav {
    padding-bottom: 0;
}

div.calendars .calListLabel {
    display: block;
}
div.calendars .calListLabel-sel {
    display: block;
}

#mainbody {
    width: 144%;
}

#mothertable > #nav
{
    position: relative;
    display: inline-block;
    
    width: 20% !important;
    height: 100% !important;
    overflow: hidden;
    
    padding: 0 !important;
    margin: 0 !important;
    
    vertical-align: top;
    overflow: hidden;
}

#lhscalinner_fav, #lhscalinner_my {
    padding: 0 5px 0 5px !important;
    margin: 0;
}

#lhscalinner_my .calListRow[aria-selected=false] {
    display: none;
}

#lhscalinner_my {
	margin-top: 10px !important;
}

/* Move the main calendar over to the left */
div#mothertable > div#maincell > div#mainbody
{
    margin: 0 !important;
}

#maincell {
    position: relative;
    width: 80%;
    margin: 0 !important;
    padding: 0 !important;
    display: inline-block;
    overflow: hidden;
}


}
