@-moz-document url("chrome://browser/content/browser.xul") {
    
    
    
    
    
    
    
    
  
  
  #toolbar-menubar:not(:-moz-lwtheme) {
    text-shadow: none !important;
  }

  /* Show borders on vista through win8, but not on win10 and later: */
  @media (-moz-os-version: windows-vista),
         (-moz-os-version: windows-win7),
         (-moz-os-version: windows-win8) {
    /* Vertical toolbar border */
    #main-window:not([customizing])[sizemode=normal] #navigator-toolbox:not(:-moz-lwtheme)::after,
    #main-window:not([customizing])[sizemode=normal] #navigator-toolbox > toolbar:not(#toolbar-menubar):not(#TabsToolbar):not(:-moz-lwtheme),
    #main-window:not([customizing])[sizemode=normal] #navigator-toolbox:-moz-lwtheme,
    #main-window[customizing] #navigator-toolbox::after,
    #main-window[customizing] #navigator-toolbox > toolbar:not(#toolbar-menubar):not(#TabsToolbar) {
      border: none !important;
      background-clip: padding-box;
    }

    #main-window[sizemode=normal] #browser-border-start,
    #main-window[sizemode=normal] #browser-border-end {
      display: -moz-box;
      background: none !important;
      width: 0px !important;
    }

    #main-window[sizemode=normal] #browser-bottombox {
      border: none !important;
    }
  }

  #main-window[sizemode=normal] #TabsToolbar {
    padding-left: 0px !important;
    padding-right: 0px !important;
  }

    
    
    
    
    
    
    

#nav-bar {
    height: 30px; 
    padding: 0px 0px 0 0 !important;
    margin: 0 0px 0 0px !important;
    background: #FFF !important;
    border-radius: 0px !important;
    border: none !important;
}
    

    
    
    

#nav-bar .toolbarbutton-1:not(:-moz-any(#back-button, #forward-button)),
#nav-bar .toolbarbutton-1:not(:-moz-any(#back-button, #forward-button)) > .toolbarbutton-badge-container,
#nav-bar .toolbarbutton-1:not(:-moz-any(#back-button, #forward-button)) > .toolbarbutton-menubutton-button > .toolbarbutton-icon {
  padding: 2px 4px !important;
}

#back-button {
    border-radius: unset !important;
  --toolbarbutton-hover-boxshadow: unset !important;
}
    
#back-button > .toolbarbutton-icon {
    border-radius: 0 !important;
  background-clip: unset !important;
  background-color: unset !important;
  padding: 6px !important;
  box-shadow: none !important;
  transition-property: unset !important;
  transition-duration: unset !important;
}

#back-button:not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon {
  background-color: unset !important;
}

#back-button:not([disabled="true"]):hover:active > .toolbarbutton-icon,
#back-button[open="true"] > .toolbarbutton-icon {
  background-color: unset !important;
  box-shadow: none !important;
}

    
window:not([chromehidden~="toolbar"]) #urlbar-wrapper {
  clip-path: unset !important;
  -moz-margin-start: unset !important;
}
    

    @media (-moz-os-version: windows-vista),
           (-moz-os-version: windows-win7) {
               
      #back-button > .toolbarbutton-icon,
               #forward-button > .toolbarbutton-icon {
        background-image: none !important;
        box-shadow: none !important;
      }

      #back-button:not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon,
               #forward-button:not([disabled="true"]):not([open="true"]):not(:active):hover > .toolbarbutton-icon  {
        background-color: #EEE !important;
        box-shadow: none !important;
      }

      #back-button:not([disabled="true"]):hover:active > .toolbarbutton-icon,
      #back-button[open="true"] > .toolbarbutton-icon,
               #forward-button:not([disabled="true"]):hover:active > .toolbarbutton-icon,
               #forward-button[open="true"] > .toolbarbutton-icon{
        background-color: #DDD !important;
        box-shadow: none !important;
      }

      #main-window:not([customizing]) #back-button[disabled] > .toolbarbutton-icon,
               #main-window:not([customizing]) #forward-button[disabled] > .toolbarbutton-icon  {
        box-shadow: none !important;
      }
    }





           
/* unified back/forward button */

#forward-button {
  -moz-box-align: unset !important; /* default: stretch */
}

#forward-button > .toolbarbutton-icon {
  background-clip: unset !important;
  border-radius: 0 !important;
    
    padding: 6px !important;
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper:not([switchingtabs]) > #forward-button {
  transition: margin-left 50ms ease-out; /* speeds up hiding/unhiding */
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper > #forward-button[disabled] {
  margin-left: -30px;
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper:hover:not([switchingtabs]) > #forward-button[disabled] {
  /*undoes : delay the hiding of the forward button when hovered to avoid accidental clicks on the url bar */
  transition-delay: 0s !important;
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper:not(:hover) > #forward-button[disabled] {
  /* when not hovered anymore, trigger a new transition to hide the forward button immediately */
  margin-left: -30.01px;
}



window:not([chromehidden~="toolbar"]) #urlbar-wrapper:hover:not([switchingtabs]) > #forward-button[disabled] + #urlbar > #notification-popup-box[hidden] + #identity-box {
  /* forward button hiding is delayed when hovered */
  transition-delay: 0s !important;
}

window:not([chromehidden~="toolbar"]) #urlbar-wrapper:not(:hover) > #forward-button[disabled] + #urlbar > #notification-popup-box[hidden] + #identity-box:-moz-locale-dir(ltr) {
  /* when not hovered anymore, trigger a new non-delayed transition to react to the forward button hiding */
  padding-left: 5.01px;
}


window:not([chromehidden~="toolbar"]) #urlbar-wrapper:not(:hover) > #forward-button[disabled] + #urlbar > #notification-popup-box[hidden] + #identity-box:-moz-locale-dir(rtl) {
  /* when not hovered anymore, trigger a new non-delayed transition to react to the forward button hiding */
  padding-right: 5.01px;
}


    
    
    
    
           /* remove border */
#identity-box {
  border-inline-end: 0px solid var(--identity-box-border-color) !important;
}

/* stops it from moving */
window:not([chromehidden~="toolbar"]) #urlbar-wrapper > #forward-button[disabled] + #urlbar > #notification-popup-box[hidden] + #identity-box {
  padding-inline-start: calc(var(--backbutton-urlbar-overlap) + 0px);
}

    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    

#nav-bar  #back-button > .toolbarbutton-icon,
    #nav-bar  #forward-button > .toolbarbutton-icon {
  /* padding: 4px !important; */
  /*box-shadow: 0 0 1px rgba(0,0,0,0.7) !important;*/
  border: none !important;
    box-shadow: unset !important;
    
}
    #back-button, #forward-button {
        list-style-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABIAAAASBAMAAACk4JNkAAAAD1BMVEUAAAAAAAAAAAAAAAAAAABPDueNAAAABXRSTlMATRA8JhbN2SMAAAAsSURBVAjXYyACMBnAWI4CUIayoACMAWWxCEJZTIZoLIQsQgfCFITJCNsIAABLswOz0q8TMgAAAABJRU5ErkJggg==') !important;      
        -moz-image-region: rect(0,18px,18px,0) !important;
       padding: 0 !important;
    }
    
           #back-button[disabled=true] {
           }
    
           #forward-button[disabled=true] {
               opacity: 0 !important;
               transition: 50ms !important;
           }
           #forward-button:not([disabled=true]) {
               transition: 50ms !important;
               transition-delay: 10ms !important;
           }
    
    #forward-button {
        /* list-style-image:url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABIAAAASBAMAAACk4JNkAAAAD1BMVEUAAAAAAAAAAAAAAAAAAABPDueNAAAABXRSTlMATDQLHbA5GWoAAAAlSURBVAjXYyABKMNZgg5wlpABjCUoBGcJimCyhDB1OCBMJgUAAOL0AoUaVvt8AAAAAElFTkSuQmCC') !important; */
        -moz-image-region: rect(0,18px,18px,0) !important;
        transform: scaleX(-1);
       
    }
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    

    
/* move tabs into titlebar */
#TabsToolbar {
    margin: 0px !important;
    -moz-padding-end: 0 !important;
}
.titlebar-placeholder, #titlebar-buttonbox-container #titlebar-buttonbox {display:none!important;}

/* fix for maximized */
window[sizemode="maximized"] #navigator-toolbox {margin-top: -2px;}
window[sizemode="maximized"] #TabsToolbar {margin-left: -4px;}

/*  bottom border color between the top toolbars and content */
#main-window #navigator-toolbox:after {
    border-radius: 0;
    height: 0px;
    background: none !important;
}

/* urlbar */
    #urlbar-reload-button,
    #urlbar-stop-button,
    #urlbar-go-button {
        display: none !important;
    }
    #reader-mode-button,
    #readinglist-addremove-button {
       display:none !important;
    }
    
#searchbar > .searchbar-textbox,
#urlbar {
    border-radius: 0px;
    border: none !important;
    background: none !important;
}
#urlbar {margin: 0 0px 0 0;}
#searchbar {margin-right: 3px;}

#searchbar > .searchbar-textbox:not([focused]):hover,
#urlbar:not([focused]):hover {
}
#searchbar > .searchbar-textbox[focused],
#urlbar[focused] {
    transition: 100ms !important;
}
#urlbar, 
#searchbar > .searchbar-textbox {
}
#searchbar > .searchbar-textbox:not([focused]) {
color: transparent;
    transition: 100ms !important;
}


.autocomplete-history-dropmarker.urlbar-history-dropmarker, .searchbar-dropmarker-image {display:none!important;}


/* SEARCH BAR */

/* hide google icon in search bar */
.searchbar-engine-image {display:none!important;}
.search-go-button {
 list-style-image: none;
-moz-padding-start: 0;
  -moz-padding-end: 0;

/* flip icon 
-moz-box-ordinal-group:1; transform: scaleX(-1);
margin: 0 2px 0 -3px;
*/

/* new magnifying glass icon 
background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAOBAMAAADtZjDiAAAAJFBMVEUAAAAAAAAAAAAkJCQAAAAAAAAAAAAAAAAAAAAAAAA8PDwAAAA4tsioAAAADHRSTlMARymMBhZvVmM8kQtEMc7WAAAAWElEQVQI12OAg8C2IgcgxZrVkSUCpHdkRmssAtJaAgys6UAJMQUGlrIABoZpQLoDSCtXMzAlAuXdzVWnLQTpKza2KJ4AZDgJTt1oCTGSRXg2hOFoCBVQBQDYuRCdn500wQAAAABJRU5ErkJggg==) no-repeat center left;*/
display: none !important;

}

/* place on left of search bar 
.searchbar-textbox .textbox-input-box {-moz-box-ordinal-group:2 ;} 
*/
.searchbar-textbox {padding-left: 4px;}

.tabs-newtab-button, .closing-tabs-spacer, #alltabs-button, .scrollbutton-up, .scrollbutton-down, #tabs-closebutton, .tab-close-button, .close-icon, toolbarbutton.chevron {display:none!important;}

#fullscr-toggler, #full-screen-warning-container {display:none!important;}

/* hide menu/customize button */
#PanelUI-button {display:none!important;}

/* findbar buttons next to input box */
  .findbar-container spacer { visibility: collapse }
  .findbar-container .findbar-closebutton { -moz-box-ordinal-group: 0 }











/* add bookmark dialog*/
#editBMPanel_locationRow, #editBMPanel_keywordRow { visibility: visible;} 
#editBMPanel_tagsRow {display:none;}
#editBookmarkPanelContent {min-width: 400px;}

/* hide bookmarks/history sidebar panel title */
#sidebar-header {display: none;}






/* Tabs */

/* disable cloud tabstrip background */
#TabsToolbar:not(:-moz-lwtheme)::after {display: none;}

#tabbrowser-tabs {
    min-height: 0;
    margin: 0 -15px 0 0px !important;
}

#TabsToolbar .tabbrowser-tab {
    -moz-border-top-colors: none;
    -moz-border-left-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    border-right: 1px solid rgb(220,220,220) !important;
    padding-bottom: 2px; /* move tab contents up a little */
    /*max-height: 26px; */
    min-width: 26px;
    height: 27px;
    font-family: Open Sans;
}

    #TabsToolbar .tabbrowser-tab[last-visible-tab], #tabbrowser-tabs tab[first-tab='true'][last-tab='true'] {
        border-right-width: 0px;
    }

    #TabsToolbar .tabbrowser-tab[afterselected] {
        border-left: none !important;
    }
    #TabsToolbar .tabbrowser-tab[beforeselected]{
        border-right: none !important;
    }

    #TabsToolbar .tabbrowser-tab[selected]{
        background: #FFF !important;
        background-clip: padding-box;
        color: #444;
        font-weight: 600;
        border: none !important;
    }
    #TabsToolbar .tabbrowser-tab:not([selected]) {
         /* background: rgba(255,255,255,0.2);
         color: rgba(255,255,255,0.5); */
        background: #EAEAEA;
        color: #777;
        font-weight: 400;
    }

    /* Unvisited tabs */
     .tabbrowser-tab[unread="true"] .tab-text {
         font-weight: 200;
         /*color:rgba(255,255,255,0.4);*/
         font-style: italic;
    }

    /* Restored tab that's not yet loaded */ 
    .tabbrowser-tab[pending] .tab-text {
        /*color:rgba(255,255,255,0.4);*/
        font-style: italic;
    }

    
    #TabsToolbar .tabbrowser-tab:hover[selected] {
        color: #555;
        /*background: rgba(255,255,255,.4);*/
    }
    #TabsToolbar .tabs-newtab-button:hover,
    #TabsToolbar .tabbrowser-tab:hover:not([selected]){
        color: #000;
        /*background: rgba(255,255,255,.4);*/
    }

    
/* tab container */
#TabsToolbar .arrowscrollbox-scrollbox {
    padding: 0 0px 0 0px; /*fixme — too little left padding w/ many tabs */
}

#TabsToolbar .tab-background {
    margin: 0;
    background: transparent;
}
    
/* hides curved tab images */
#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
    display:none;
}
#TabsToolbar .tab-background-middle { 
    background: none; /* needs to be here for tabs to be selectable! */
}

    /* ? */
#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before{
    display: none !important;
}





/* tab loading animation */
.tab-throbber[busy] {
list-style-image: url(data:image/png;base64,);
}
.tab-throbber[progress] {
list-style-image: url(data:image/gif;base64,);
}






#main-window[title$="(Private Browsing)"] .tabbrowser-tab:not([selected=true]) {background: rgba(70,0,140,0.4);}


/* hide context menu icons */
        #context-navigation{
       display:none !important;
    }






/* end bracket */
}
