{
  html{
    font-family: Tahoma !important;
    height: 50% !important;
  }
  #body, html, body #main-content{
    background-color: #191919 !important;
  }
  body{
    text-align: center !important;
    background-color: #191919 !important;
    font-family: Tahoma !important;
  }
    .row adjust-top nav-educational-attainment{
    margin-left: auto !important;
    margin-right: auto !important;
  }
  #working-experience{
    border-radius: 0px !important;		
  }
  .col-md-10{
    width: auto !important;
  }
  body section .section-head{
    background: #2f2f2f !important; 
  }
  #icon icon-user4, .alert-danger, body section .section-head .icon, .input-group-addon, #personal, #left-sidebar{
    display: none !important;
  }
  .well, .panel, .btn, .input-lg, #login ul.icons .selected, #login ul.icons .notselected, body section .section-head, body section, .panel-heading{
    border-radius: 0px !important;
  }
  .input-group-lg > .form-control, .input-group-lg > .input-group-addon, .input-group-lg > .input-group-btn > .btn{
    border-radius: 0px !important;
  }
  #working-experience, #educational-attainment{
    height: 500px !important;
  }
  .adjust-top{
    margin-top: 0px !important;
  }
  #letter-head{
    background: inherit !important;
    border-bottom: none !important;
    margin-top: -75px !important;
  }
  #letter-head #service{
    float: none !important;
    width: 40% !important;
  }
  #service{
    display: block !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
  
}

@-moz-document url-prefix("http://dark.im/lounge/")
{
  /*eliminate the god damned rounded edges*/
  
  #personal-info #each-holder, .btn, .list-group-item:last-child, .list-group-item:first-child, body section, body section .section-head, .panel{
    border-radius: 0px !important;
  }
  
   /*get that shit outta my face mang*/
  
  body section .section-head .icon, .col-md-7, .pluso{
    display: none !important;
  }
  
  /* no more borders on bottom*/
  
  body section, #personal-info #each-holder{
    border-bottom: none !important;
  }
  
  /*layout general shit*/
  
  html{
    font-family: Tahoma !important;
    height: 50% !important;
  }
  #body, html, body #main-content{
    background-color: #191919 !important;
  }
  body{
    text-align: center !important;
    background-color: #191919 !important;
    font-family: Tahoma !important;
  }  
  
  /*misc fixes*/
  #working-experience{
    height: 560px !important;
    float: none !important;
  }
  .adjust-top, .col-md-2, #personal-info #each-holder{
    margin-top: 0px !important;
  }
  .list-group-item center li{
    display: none !important;
  }
  #personal-info #each-holder[style]{
    padding-top: 0px !important;
  }
  #personal-info #each-holder center br{
    display: none !important;
  }
  body section .section-head{
    background-color: #2f2f2f !important;
  }
  #personal-info a button[style]{
    margin-top: 10px !important;
  }
  .list-group-item:last-child[style]{
    height: 176px !important;
  }
}
@-moz-document url("http://dark.im/room/"){
  
  /*layout general shit*/
  
  html{
    font-family: Tahoma !important;
    height: 50% !important;
  }
  #body, html, body #main-content{
    background-color: #191919 !important;
  }
  body{
    background-color: #191919 !important;
    font-family: Tahoma !important;
  } 
  
  #talks_box{
    font-family: Tahoma !important;
  }
  .block .header, .block .content, .block .footer, .block .toolbar, .block .header.header-default, .block .content.content-default, .block .toolbar.toolbar-default, .block .footer.footer-defaut, .bg-dot30{
    background: #2f2f2f !important;
  }
  .form-control, input, select, select[multiple], textarea, .btn{
    background: #191919 !important;
  }
  .btn, dl.talk dd div.bubble p.body, .block > div:first-child, .block > div:last-child, .form-control{
    border-radius: 0px !important;
  }
  dl.talk dd div.bubble p.body{
    border: 0px solid #FFF !important;
  }
  .navbar, .dropdown-menu, .block-drop-shadow, .ui-datepicker, .dropdown-submenu{
    box-shadow: none !important;
  }
  .bubble div{
    display: none !important;
  }
  .bubble p[style]{
    border-width: 2px !important;
  }
  .bubble[style]{
    margin: 0px 0px 0px !important;
  }
  .btn-group-justified[style] {
    margin-bottom: 0px !important;
  }
   
}
