css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('bicaps.net') {
html, body {
background: black !important;padding:0 !important;margin:0 !important; 
}
body{overflow-x:hidden;}
a, #sidebar, #footer, #alt, .bireklam {
display: none;  width: 0px !important;
height: 0px !important;
}

#wrap a{display: inline;}

#nav li a{
min-height: 20px;
min-width: 45px;
display: block !important;
width: 100% !important;
}

#wrap, #content, #topnavbar, #header, #navbarborder, #navbar, 
.filmslayt, .filmslaytc, #myslides, #mygallery, #mygallery {
width: 100% !important;
border:none !important;
margin:0 auto!important;
padding:0!important;
display:block;
}

.leftC, .filmborder, .filmcontent, .filmicerik, .filmicerik iframe {
background: black  !important;
border:none !important;
margin: 0 !important;
width: 100% !important;
}

.filmicerik object, .filmicerik embed, .filmicerik iframe {
background: black  !important; border:none !important; width: 100% !important;height: 100% !important; min-height: 600px;
}

#myslides{max-width: 98% !important;}
.stepcarousel .panel img {height:auto;min-height:190px;width:100%;}
.stepcarousel .panel{width:125px;margin:4px;}

.commentlist {height:200px;overflow:auto;width:98% !important;}


.moviefilm {position:relative;width:16.2%;height:auto;max-height:100%;padding:5px 0 !important;min-width:200px;min-height:322px;display:inline-flex;}
.moviefilm img{max-width:100%;height:auto !important;min-height:312px;padding:0;margin:0;border:0;min-width:200px;}

.moviefilm .imdb{position:absolute;height:20px;color:black;max-width:94%;top:0;right:0;margin:0;padding:5px 0;width:110px;font-size:large;} 
.moviefilm .imdb span{font-size:10px;color:white;font-weight:100;margin-right:5px}
.moviefilm .imdb:hover{font-size:x-large;position:absolute;height:20px;max-width:94%;width:140px;top:0;right:0;margin:0;padding:5px 0;}

#content .yazitip ul, #content .tam ul{width:100%;height:35px;display:block;clear:left;}
#content .yazitip ul li, #content .tam ul li{min-width:23%;overflow:hidden;height:35px;display:block;}
#content .yazitip ul li a, #content .tam ul li a{display:block !important;width:100% !important;overflow:hidden;height:60px !important;}

.wp-pagenavi{height:100px;margin-top:30px;}
.wp-pagenavi span.pages, .wp-pagenavi span.extend, .wp-pagenavi span.last{display:none;}
.wp-pagenavi a.larger{padding: 8px 15px;} 

.movies{width:100%;position:absolute;bottom:-20px;}
.movief{width:100%;position:absolute;bottom:5px}
}
