css:
@-moz-document domain("coffeescript.org") {
body {
	background-image: none;
	background-color: black;
	color: white;
}

h1, h2, h3, h4, h5, h6, b.header {
	text-shadow: #ccc 0 1px 1px;
	color: #fff;
}

code, pre, tt, textarea {
	color: #cc8;
}

a{
	color: #ccc;
}

#fadeout {
	display: none;
}

#flybar {
	background: none;
	background-color: #A0A0A0;
	left: 0px;
	top: 0px;
	width: 100%;
	border-radius: 0px;
}

.navigation {
	border: none;
	font-size: 13px;
}

.navigation:hover {
	background: none;
	background-color: #ccc;
}

div.code {
	border: none;   
    background: #2D4377;
}

div.code pre, div.code textarea {
    background-color: #2D4377;
}

pre code {
	background-color: #2D4377;
    color: white;
}

.tomorrow-comment, pre .comment, pre .title {
	color: #acafaa;
}

.tomorrow-orange, pre .number, pre .preprocessor, pre .built_in, pre .params, pre .constant {
	color: #ff9;
}

.tomorrow-green, pre .string, pre .value, pre .inheritance, pre .header, pre .ruby .symbol, pre .xml .cdata {
	color: #a5cc01;
}

.tomorrow-blue, pre .function, pre .function .title, pre .python .decorator, pre .python .title, pre .ruby .function .title, pre .ruby .title .keyword, pre .perl .sub, pre .javascript .title, pre .coffeescript .title {
	color: #7299ff;
}

pre .literal {
	color: #f89cff;
}

tt {
	color: white;
	background-color: #300;
	border-color: #300;
}
}
