css:
@-moz-document domain("www.upwork.com") {
body {
    background-color: #002B44;
    color: #7BC4EF;
}

select {
    background-color: #666FDB;
    color: #01064A;
}

textarea {
	background-color: #7BC4EF;
}

#room-main-body>div {
	background-color: #002B44;
}

.oLeftNav a, 
.oLeftNav .oBtnTertiary,
.oLeftNav .oBtnCancel,
.oLeftNav .oBtnPopup, 
.oLeftNav .o

umbList figcaption,
.oThumbList .oLeftNav figcaption, 
.oLeftNav .oMoreSmall {
    color: #7BC4EF;
}

.editable-item-wrapper:hover {
    background-color: #12689B;
}

.oHeader .oTwizzlr .oTopNavTabpanel,
.header-navbar-skinny .navbar-default.primary-navbar,
.header-navbar-visitor .navbar-default.primary-navbar {
    background-color: #002B44;
}

.oHeader .oTwizzlr .oNavTab,
.header-navbar-skinny .navbar-default.primary-navbar .navbar-nav>li>a,
.header-navbar-visitor .navbar-default.primary-navbar .navbar-nav>li>a,
.oHeader .oTwizzlr .oDropdownValue,
.navbar-nav > li > a,
.control-label, label:not(.btn) {
    color: #7BC4EF;
}

.air-card {
	background-color: #002B44;
}

.air-card header,
.air-card footer,
.air-card.sidebar,
.navbar,
.nav-stacked>li.active>a,
.nav-stacked>li > a:hover,
.modal-overlay .modal-content,
.modal-overlay .modal-header {
    background: #033856;
}

section.air-card-hover:hover {
    background: none;
}

section.air-card-visited,
section.air-card-visited:hover {
    background: #033856;
}

a,
.oBtnTertiary, 
.oBtnCancel, 
.oBtnPopup, 
.oThumbList figcaption, 
.oMoreSmall, 
.oLink,
.air-card section.air-card-hover a {
    color: #0BAB5A;
}

.checkbox label, 
.radio label {
    color: #58A6D4;
}

label, .control-label, .nav-stacked>li>a {
    color: #58A6D4;
}

p {
	color: #7BC4EF;
}

.h1, h1, .oH2 {
  	color: #7BC4EF;
}

.h2, h2 {
  	color: #7BC4EF;
}

h3, h4, .h3, .progress-bar, .oNote, .popover-content h3 {
    color: #58A6D4;
}

h5, top-rated {
    color: #75F2B3;
}

.oH3 {
    color: #7BC4EF;
    background-color: #002B44;
}

.btn-primary {
    background-color: #0BAB5A;
}

.btn-primary:hover {
    background-color: #006431;
}

.popover {
  	background-color: #01395A;
}

.jdPopover {
    color: #7BC4EF;
}

.oFlyout, .oFlyoutArrow, .oFlyoutArrow:after {
  	background-color: #002B44;
}

.oSupportInfo {
  	color: #7BC4EF;
}

.oRate, .oRateSmall, .oRateLarge, .oRateMedium, .oRateTime {
  color: #7BC4EF;
}

.oPopupWrapper {
  background-color: #002B44;
}

.oWidget {
  background-color: #002B44;
}

.story-message-text {
    color: #7BC4EF;
}

.oMCMessageContent {
    color: #7BC4EF;
}

.oTableLite tbody td {
    color: #7BC4EF;
}

.oTable th,
.oTablePastHires th,
.oNewRateTable th, 
.oReportTable th {
    color: #666FDB;
}

.oTable td,
.oTablePastHires td, 
.oNewRateTable td, 
.oReportTable td {
    background-color: #01395A;
}

.oTable td,
.oRowTitle {
    color: #7BC4EF;
}

.oPending {
    color: #7BC4EF;
}

.oBigData {
    background-color: #030A62;
}

.oNull {
    color: #868EF2;
}

.oBigDataNavBarContainer>li.oDataNavBarTab>a, 
.oBigDataNavBarContainer>li.oDataNavBarTab>.oBtnTertiary,
.oBigDataNavBarContainer>li.oDataNavBarTab>.oBtnCancel,
.oBigDataNavBarContainer>li.oDataNavBarTab>.oBtnPopup, 
.oThumbList .oBigDataNavBarContainer>li.oDataNavBarTab>figcaption, 
.oBigDataNavBarContainer>li.oDataNavBarTab>.oMoreSmall {
	color: #7BC4EF;
}

.oBigDataNavBarContainer>li.oDataNavBarTab.current>a, 
.oBigDataNavBarContainer>li.oDataNavBarTab.current>.oBtnTertiary,
.oBigDataNavBarContainer>li.oDataNavBarTab.current>.oBtnCancel,
.oBigDataNavBarContainer>li.oDataNavBarTab.current>.oBtnPopup, 
.oThumbList .oBigDataNavBarContainer>li.oDataNavBarTab.current>figcaption, 
.oBigDataNavBarContainer>li.oDataNavBarTab.current>.oMoreSmall,
section.air-card-visited:hover strong {
    color: #7BC4EF;
}

.oMute,
.oFormHelp {
    color: #7BC4EF;
}

.oDescTable>tbody>tr>td, 
.oDescTableTop>tbody>tr>td,
.oDescTable>thead>tr>td, 
.oDescTableTop>thead>tr>td {
    
}

.oStatsTable th, 
.oTableLite th, 
.oTableForm th {
    color: #58A6D4;
}

.modal-header, .ui-dialog-titlebar {
    background-color: #002B44;
    color: #7BC4EF;
}

.modal-content, .ui-dialog, .oPopup {
    background-color: #01395A;
    color: #7BC4EF;
}

.modal-backdrop, .ui-widget-overlay {
    background-color: #12689B;
}

ul.oList, section.oList>.oBd {
    background-color: #01395A;
}

.oMessageThread .isMe {
    color: #53DC96;
}

.oMessageThread .isOther1 {
	color: #0BAB5A;
}

.oDescTable>tbody>tr>td,
.oDescTableTop>tbody>tr>td, 
.oDescTable>thead>tr>td,
.oDescTableTop>thead>tr>td {
    color: #868EF2;
}

tr.isMuted td {
    background-color: #01064A;
}

.oJobResults {
	background: none;
}

.up-active-editor .up-active-container.up-active-container-hover .up-active-context span, .up-active-editor .up-active-container.up-active-container-hover p.up-active-context, .up-active-editor .up-active-container .up-active-context-hover, .up-active-editor .up-active-container .up-active-context-hover span, .up-active-editor .up-active-container:hover .up-active-context-hover, .up-active-editor .up-active-container:hover .up-active-context-hover span, .up-active-editor .up-active-container .up-active-context:hover span, .up-active-editor .up-active-container p.up-active-context:hover {
    background: none !important;
    color: #7BC4EF;
}

.up-active-editor .up-active-container:hover .up-active-context span, .up-active-editor .up-active-container:hover p.up-active-context {
    background-color: #030A62;
}

.modal-overlay .modal-header,
.modal-overlay .modal-content {
    box-shadow: none;
}

.modal-body {

}

.modal-body .air-card {
    margin: 0;
}

.modal-body .air-card.sidebar {
    margin-right: -30px;
}
}
