css:
@-moz-document domain("animehaven.org"), domain("animehaven.to") {
    body {
        color: #FFF;
        background-color: #000;
        background: url("https://i.imgur.com/Uagi0Fn.png") repeat;
    }
    /*Header logo*/   
    #bg_image {
        display: none;
    }
    /*New logo*/
    #branding {
        display: block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        background: url("https://i.imgur.com/Ec0FGdp.png") no-repeat center;
        background-position: top;
        background-size: auto auto;
        width: 100%;
        height: 110px;
        z-index: 1;
    }
    /*Header background color*/   
    #header-full {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Menu & Side Bar Style */
    .ubermenu-main, .sidey .widget-title, .posts_loop .nav_label {
      background: #131211;
    }
    /*Nav Style*/
    .posts_loop .nav {
      border-bottom: none !important;
    }
    .ubermenu-responsive-toggle.ubermenu-responsive-toggle-main {
        background: #131211;
        color: #FFF !important;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .ubermenu-skin-trans-black-hov.ubermenu-responsive-toggle:hover {
        background: rgba(0, 0, 0, 0.8) none repeat scroll 0 0;
    }
    .ubermenu.ubermenu-main .ubermenu-item-level-0:hover > .ubermenu-target,
    .ubermenu-main .ubermenu-item-level-0.ubermenu-active > .ubermenu-target {
        color: #FFF !important;
        background: rgba(0, 0, 0, 0.8) none repeat scroll 0 0;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .ubermenu .ubermenu-target-text {
        font-family: inherit;
        color: #FFF;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .ubermenu-skin-trans-black-hov .ubermenu-item-level-0.ubermenu-active > .ubermenu-target {
        color: #FFF;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .ubermenu-main .ubermenu-item-level-0 > .ubermenu-target {
        text-transform: capitalize;
        color: #FFF;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .ubermenu,
    .ubermenu .ubermenu-column,
    .ubermenu .ubermenu-divider,
    .ubermenu .ubermenu-icon,
    .ubermenu .ubermenu-image,
    .ubermenu .ubermenu-item,
    .ubermenu .ubermenu-nav,
    .ubermenu .ubermenu-retractor,
    .ubermenu .ubermenu-row,
    .ubermenu .ubermenu-search,
    .ubermenu .ubermenu-search-input,
    .ubermenu .ubermenu-search-submit,
    .ubermenu .ubermenu-submenu,
    .ubermenu .ubermenu-submenu-footer,
    .ubermenu .ubermenu-tabs,
    .ubermenu .ubermenu-tabs-panels,
    .ubermenu .ubermenu-target,
    .ubermenu-responsive-toggle {
        color: #FFF !important;
    }
    .ubermenu .ubermenu-target-text:hover {
        color: #BF4D28;
    }
    /*Search on menu*/   
    .ubermenu .ubermenu-submenu .ubermenu-search .ubermenu-searchform {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .ubermenu-search .ubermenu-searchform {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .sa_title a {
        color: #FFF;
    }
    .sa_title a:hover {
        color: #BF4D28 !important;
    }
    /*Dropdown menu*/
    .ubermenu-skin-trans-black-hov .ubermenu-submenu.ubermenu-submenu-drop {
        background: rgba(0, 0, 0, 0.8) none repeat scroll 0% 0%;
        border: 1px solid #111;
    }
    /*Anime & Movie list*/    
    .ubermenu-active .all_anime_series_filter .filter-content {
        background: rgba(0, 0, 0, 0.8) none repeat scroll 0 0;
    }
    /*Anime & Movie list*/    
    .category_alphabet_title_link {
        font-family: "open sans";
        line-height: 26px;
        color: #FFF;
        font-size: 16px;
    }
    /*Information below title while viewing*/   
    .meta-light .entry-meta span,
    .meta-light .entry-utility span.bl_posted {
        border: hidden;
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Source selection button*/   
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li.ui-state-active a,
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li.ui-state-disabled a,
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li.ui-state-processing a {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li a {
        color: #FFF;
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .ik_select_link {
        color: #FFF !important;
    }
    .ik_select_link:hover {
        color: #C3C3C3 !important;
    }
    .ik_select_link_text {
        color: #FFF !important;
    }
    .ik_select_link_text:hover {
        color: #C3C3C3 !important;
    }
    .btn-1 {
        color: #FFF !important;
    }
    .btn-1:hover {
        color: #BF4D28 !important;
    }
    /*Twitter sharing icon fix*/
    .essb_links.essb_template_blocks-retina .essb_link_twitter a {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Side bar element's*/    
    .posts_loop .nav {
        border-bottom: 4px solid #131211;
    }
    /*Un-Wanted side bar element's either empty or not working for me (Updating this is required)*/    
    #socials-left,
    #text-15,
    #text-27,
    #recent-posts-2,
    #recently_watched-2 {
        display: none !important;
    }
    /*Widget Container Modifyier,*/    
    .widget-container {
        padding: unset;
        margin: unset;
        border: none;
    }
    .sidey .widget-container {
        color: #FFF;
        background: rgba(0, 0, 0, 0);
        background-color: rgba(0, 0, 0, 0);
    }
    /*Chat box title size & background color*/    
    .sidey .widget-title {
        width: 80px;
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Chat box title size & background color extra*/
    
    .sidey .widget-title::after {
        border: none;
    }
    /*Chat box position*/   
    #text-41 {
        position: absolute;
        top: 25px;
        right: 30px;
        z-index: 100 !important;
    }
    /*Main colum when viewing*/   
    #container.two-columns-right #content {
        width: 100%;
    }
    div.post,
    div.page,
    #comments,
    .comments,
    .column-text,
    .column-image,
    #srights,
    #slefts,
    #frontpage blockquote,
    .page-title,
    .page-header,
    article.post,
    article.page,
    .contentsearch,
    #author-info,
    #nav-below {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Main colum when viewing*/  
    div.post,
    div.page,
    .sidey .widget-container,
    #comments,
    .commentlist .comment-body,
    article.post,
    article.page,
    #nav-below,
    .page-header {
        border: none;
    }
    /*Staff page & author info*/   
    #upright-profile {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
        font-family: helvetica, Arial, sans-serif;
        border: none;
        text-shadow: #000000 0px 2px 3px !important;
        box-shadow: unset;
    }
    #profilephoto {
        border: none;
    }
    #socialprofile {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Entry color*/   
    .entry-content h1,
    .entry-content h2,
    .entry-content h3,
    .entry-content h4,
    .entry-content h5,
    .entry-content h6 {
        color: #FFF;
    }
    /*Homepage Slider*/  
    #slider {
        border: none;
    }
    .theme-default .nivo-controlNav::before,
    .theme-default .nivo-controlNav::after {
        border: none;
    }
    .slider-bullets .nivo-controlNav a {
        background-color: #131211;
    }
    .theme-default .nivo-controlNav {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Homepage below sllider*/  
    .latest-updates-container {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
        margin-bottom: 15px;
    }
    /*Deatiled anime list*/ 
    .all-anime-list-section {
        margin-bottom: 0px;
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .all-anime-list-section > header::before {
        border: none;
    }
    .all-anime-list-section .section_title,
    .all-anime-list-section .section_controls {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Dropdown*/ 
    .all_anime_series_filter .latest-updates-more,
    .sa_more {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
        color: #FFFFFF !important;
    }
    .latest-updates-more {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
        color: #FFFFFF !important;
    }
    /*Next page buttons*/  
    .pagination span,
    .pagination a {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
        border: none;
    }
    .pagination span:hover,
    .pagination a:hover {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Episodes list title*/    
    .entry-title,
    .entry-title a {
        color: #FFF;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .entry-title:hover,
    .entry-title a:hover {
        color: #BF4D28;
        text-shadow: #000000 0px 2px 3px !important;
    }
    /*Episodes list image*/   
    .parabola-image-two .entry-content img[class*="align"],
    .parabola-image-two .entry-summary img[class*="align"] {
        border: none;
        box-shadow: 0px 0px 3px #000;
    }
    .parabola-image-two .entry-content img[class*="align"]:hover,
    .parabola-image-two .entry-summary img[class*="align"]:hover {
        border: none;
        box-shadow: 0px 0px 6px #000 !important;
    }
    /*Comment count & link*/   
    .comments-link {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .comments-link::before {
        border: none;
    }
    /*More link*/   
    .columnmore {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .columnmore::before {
        border: none;
    }
    /*Report box*/    
    .wp-report-post-box {
        background-color: rgba(0, 0, 0, 0);
        border: none !important;
    }
    /*Copy right & thank you*/    
    #site-copyright {
        color: #FFF;
        text-shadow: #000000 0px 2px 3px !important;
    }
    /*Rating circle for show*/    
    .c100 {
        background: rgba(0, 0, 0, .5) none repeat scroll 0 0 !important;
    }
    .c100.orange .bar,
    .c100.orange .fill {
        border-color: #FFF;
    }
    .c100::after {
        background: rgba(0, 0, 0, 1) none repeat scroll 0 0 !important;
    }
    .c100 > span {
        color: #FFF;
        text-shadow: #000000 0px 2px 3px !important;
    }
    /*Info panel on Anime info pages*/    
    .series_page .left-col {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .p_content_more {
        background: transparent linear-gradient(to bottom, transparent 50%, #000 95%) repeat scroll 0% 0%;
    }
    /*Buttons on certin pages*/   
    .ah_button {
        background: rgba(0, 0, 0, .5) none repeat scroll 0 0;
    }
    .series_page .extra_info .ah_button.tag_button {
        background: rgba(0, 0, 0, .5) none repeat scroll 0 0;
    }
    .series_page .extra_info .ah_button:hover {
        background: #BF4D28 none repeat scroll 0% 0%;
        color: #FFF !important;
        text-shadow: #000000 0px 2px 3px !important;
    }
    .series_page .extra_info .ah_button.tag_button:hover,
    .series_page .extra_info .ah_button.tag_button a:hover {
        background: #BF4D28 none repeat scroll 0% 0%;
        color: #FFF !important;
        text-shadow: #000000 0px 2px 3px !important;
    }
    /*Anime epsiode list via thumbnail fix*/   
    .posts_loop .post-box {
        width: auto;
    }
    /*Remove avatar boarder from uploader info*/   
    #entry-author-info #author-avatar,
    #author-info #author-avatar {
        border: none;
    }
    #author-info,
    #entry-author-info {
        border: hidden;
    }
    /*Latest anime updated list link*/ 
    .latest_anime_link {
        color: #FFF;
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Input box & dropdown style*/   
    input[type="text"],
    input[type="password"],
    input[type="email"],
    input[type="file"],
    textarea,
    select {
        background: rgba(0, 0, 0, 1) none repeat scroll 0 0;
        color: #FFF;
        border: none;
        text-shadow: #000000 0px 2px 3px !important;
    }
    input[type="text"]:hover,
    input[type="password"]:hover,
    input[type="email"]:hover,
    input[type="file"]:hover,
    textarea:hover,
    select:hover {
        color: #FFF;
        background: rgba(0, 0, 0, 1) none repeat scroll 0 0;
    }
    #main_filter_section .form-items-row .form-item select {
        color: #FFF;
        background: rgba(0, 0, 0, 1) none repeat scroll 0 0;
    }
    /*Homepage anime title's*/   
    .three-post-row .entry-title {
        color: #FFF;
    }
    .three-post-row .entry-title:hover {
        color: #BF4D28;
    }
    /*New episode icon removal*/   
    .new_episode {
        color: #FFF;
        text-transform: uppercase;
        background: none;
    }
    /*New series icon removal*/   
    .new_series {
        color: #BF4D28;
        text-transform: uppercase;
        background: none;
    }
    /*Latest updates view more button*/   
    .latest-updates-more {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    .column-image h3 {
        color: #FFF;
        background-color: rgba(0, 0, 0, 0.6);
        text-shadow: #000000 0px 2px 3px !important;
    }
    #latest_updates_section li .views > span,
    #latest_updates_section li .genres > span,
    #latest_updates_section li small > span {
        color: #FFF;
    }
    #latest_updates_section li .entry-content {
        background-color: rgba(0, 0, 0, 0.6);
    }
    #latest_updates_section li .entry-content a {
        color: #FFF;
    }
    /*Latest updates title*/   
    .list-page-title {
        color: #FFF;
    }
    /*Remove border in help section*/   
    #ppm-tabs h3 {
        border: none;
    }
    #ppm-tabs h3.open a:hover,
    #ppm-tabs h3.close a:hover,
    #ppm-tabs h3.open a {
        background-position: unset;
    }
    /*Anime list page buttons & text*/   
    .pages-list.page a {
        color: #FFF;
        background-color: rgba(0, 0, 0, 0);
    }
    .form-items-row .form-item label {
        color: #FFF;
    }
    #filter-form .pages-list.page li a {
        border-bottom: 4px solid #FFF;
    }
    /*Anime list border removal & text*/    
    .whide-article {
        border: none;
    }
    .whide-article .entry-title {
        color: #FFF;
    }
    .whide-article .entry-title:hover {
        color: #BF4D28;
    }
    /*Anime list search box*/    
    .search-holder {
        background: rgba(0, 0, 0, 1) none repeat scroll 0 0;
    }
    .search-holder #search-button {
        height: 26px;
        width: 34px;
    }
    /*imdb rating*/   
    .imdbRatingStyle2 {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    }
    /*Next & Previous buttons */   
    .nav-previous a:hover {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0 !important;
    }
    .nav-next a:hover {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0 !important;
    }
    /*Contact us page*/   
    .contact-us-page #FSContact1 {
        background: rgba(0, 0, 0, 0) none repeat scroll 0 0 !important;
    }
    .contact-us-page .entry-content input[type="text"],
    .contact-us-page .entry-content input[type="email"],
    .contact-us-page .entry-content input[type="password"],
    .contact-us-page .entry-content input[type="url"],
    .contact-us-page .entry-content input[type="tel"],
    .contact-us-page .entry-content input[type="number"],
    .contact-us-page .entry-content input[type="color"],
    .contact-us-page .entry-content textarea {
        background: rgba(0, 0, 0, .4) none repeat scroll 0 0 !important;
    }
    input[type="submit"] {
        background: rgba(0, 0, 0, .4) none repeat scroll 0 0 !important;
    }
    input[type="submit"],
    input[type="reset"] {
        background: rgba(0, 0, 0, .4) none repeat scroll 0 0 !important;
    }
    /*Homepage updates info*/   
    .genres {
        color: #FFF;
    }
    .genres a {
        color: #BF4D28 !important;
    }
    .genres a:hover {
        color: #E6AC27 !important;
    }
    /*Video sources text color*/   
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li.ui-state-active a,
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li.ui-state-disabled a,
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li.ui-state-processing a {
        color: #BF4D28 !important;
    }
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li a {
        color: #FFF !important;
    }
    .wordpress-post-tabs-skin-default .ui-tabs .ui-tabs-nav li a:hover {
        color: #E6AC27 !important;
    }
}
@-moz-document domain("chatwing.com") {
    /*Chat*/   
    .modal-content {
        color: #FFF;
        background: rgba(0, 0, 0, .4) none repeat scroll 0 0;
        border: none;
        outline: none;
        box-shadow: none;
    }
    /*Chat background*/    
    .message-list {
        color: #FFF;
        background: url("https://i.imgur.com/Uagi0Fn.png") repeat;
    }
    /*Chat outline*/   
    .widget-box {
        background: #000;
    }
    /*Input box*/    
    .message-input .form-actions .input-group input[type="text"],
    .message-input .form-actions .input-group textarea {
        background: #000;
        color: #FFF;
    }
}
@-moz-document domain("disqus.com") {
    body {
        color: #FFF !important;
    }
    .nav-primary > ul > li.user-menu .dropdown-toggle .dropdown-toggle-wrapper:hover {
        color: #BF4D28 !important;
    }
    .discovery-col-header h2,
    .discovery-options .discovery-help {
        color: #FFF !important;
    }
    .post-content header .parent-link,
    .post-content header .state-byline,
    .post-content header .time-ago {
        color: #FFF !important;
    }
    .post-content header .parent-link:hover,
    .post-content header .state-byline:hover,
    .post-content header .time-ago:hover {
        color: #BF4D28 !important;
    }
    .post-content footer a {
        color: #FFF !important;
    }
    .post-content footer a:hover {
        color: #BF4D28 !important;
    }
    .post-content:hover {
        color: #FFF!important;
    }
    .discovery-post .inline-meta {
        color: #FFF!important;
    }
    .nav > ul > li > a,
    .nav > ul > li > div a {
        color: #FFF!important;
    }
    .nav-primary > ul > li.active > a {
        color: #FFF !important;
    }
    .nav-primary > ul > li > a:hover {
        color: #BF4D28 !important;
    }
    .nav .dropdown-toggle {
        color: #FFF !important;
    }
    h6 {
        color: #FFF !important;
    }
    .acceptance-wrapper {
        color: #FFF !important;
    }
    .postbox section label {
        color: #FFF !important;
    }
    #footer a {
        color: #FFF !important;
    }
    #footer a:hover {
        color: #BF4D28 !important;
    }
    .post-content footer .voting .vote-down,
    .post-content footer .voting .vote-up {
        color: #FFF !important;
    }
    .post-content footer .voting .vote-down:hover,
    .post-content footer .voting .vote-up:hover {
        color: #BF4D28 !important;
    }
    .active .publisher-nav-color::after {
        background: #BF4D28 none repeat scroll 0% 0% !important;
    }
    .publisher-anchor-color a {
        color: #BF4D28 !important;
    }
    .publisher-anchor-color a:hover {
        color: #E6AC27 !important;
    }
}
