css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aimp.ru"){

.aimp_content
{
	padding: 20px;
	background: #1a1b1f;
}

.aimp_page_header, .aimp_page_footer
{
	background: #1a1b1f;
	color: #79767e;
 /* for dear IE */
}

.aimp_page_header_separator
{
	background: #1a1b1f;
	height: 4px;
}

.aimp_page_footer_section
{
	padding: 24px;
	height: 36px;
}

.aimp_main_menu
{
	background: #c33d1e;
	height: 40px;
}

.aimp_main_menu table
{
	margin-top: -3px;
}

div.aimp_main_menu a, div.aimp_main_menu a:link , div.aimp_main_menu a:visited
{
	display: block;
	float: left;
	height: 40px;
	line-height: 40px;
	padding: 0 8px;
	font-size: 10pt;
	color: #000000;
	font-family: tahoma;
	font-weight: bold;
	text-decoration: none;
	margin-top: -3px;
	outline: none;
}

div.aimp_main_menu a:hover 
{
	background: #e74621;
	outline: none;
}

/***********************/
/*      Top Header     */
/***********************/



.button
{
	color: #79767e;
	text-align: center;
	font-weight: bold;
	border: 0px;
	background: transparent url left top no-repeat;
	line-height: 32px;
	width: 122px;
	height: 32px;
	float: left;
}

.button:hover
{
	color: #000000;
	text-decoration: none;
}

/***********************/
/*   I T E M   B O X   */
/***********************/

.itembox_table
{
	width: 510px;
	border: 0px;
}

/***************************************************************************************/
/* Cards */
/***************************************************************************************/

.card_title 
{
	font-size: 120%;
	color: #3c3640;
	margin: 0px;
	padding: 2px 0;
	padding-top: 4px;
	clear: both;
}

.card_description 
{
	margin: 0px;
	font-size: 80%;
	padding-bottom: 4px;
}

.card_preview_host
{
	border: 0px solid #CACACA; 
	padding: 1px;
	margin-right: 8px;
}

.card 
{
	width: 100%;
	margin-bottom: 30px;
	background-color: #202227;
	padding: 8px;
} 

.card_header 
{
	margin: 3px;
	margin-top: 0px;
	margin-bottom: 6px;
	padding-bottom: 4px;
}

.card_buttons_bar
{
	padding: 8px; 
	padding-right: 0px;
	padding-bottom: 4px;
	float: right;
}

/* Styles for the general looks for the Curve theme.
------------------------------------------------------- */

a:link, a:visited
{
	color: #c33d1e;
	text-shadow: 0 0 0 #1a1b1f;
	text-decoration: none;
}

a:hover
{
	text-decoration: underline;
}

#footer_copyrights
{
	color: #A0A0A0;
	float: left;
}

#footer_counter
{
	float: right;
}

hr
{
	height: 1px;
	background-color: #3c3640;
	border: 0px;
}

/* Normal, standard links. */
.aimp_content a:link, .aimp_content a:visited, bbc_link:link, bbc_link:visited
{
	color: #c33d1e;
	text-decoration: none;
}
.aimp_content a:hover, bbc_link:hover
{
	text-decoration: underline;
	cursor: pointer;
}

/* Links that open in a new window. */
.aimp_content a.new_win:link, .aimp_content a.new_win:visited
{
	color: #c33d1e;
	text-decoration: none;
}
.aimp_content a.new_win:hover
{
	text-decoration: underline;
}

#info_center_frame
{
	border: 1px solid #3c3640;
}

/* Tables should show empty cells. */
table
{
	empty-cells: show;
}

body, .editor
{
	font: 10pt Verdana;
}

body
{
	background: #1a1b1f;
	padding: 0px;
	margin: 0px;
}

/* Help popups require a different styling of the body element. */
body#help_popup
{
	padding: 1em;
}

/* use dark grey for the text, leaving #000 for headers etc */
body, td, th, tr
{
	color: #79767e;
}

/* This division wraps the entire forum when a forum width is set. */
div#wrapper
{
	margin: 0 auto;
	min-width: 780px;
	max-width: 2300px;	
}

/* lets give all forms zero padding/margins */
form
{
	padding: 0;
	margin: 0;
}

button, select, textarea, input, .input_text, .button, .button_submit, .button_reset
{
	color: #79767e;
	border: 1px solid #3c3640;
	background: #1a1b1f;	
	padding: 8px;
}

select
{
	padding: 7px;
}

button, .button, .button_submit, .button_reset
{
	text-align: center;
	padding: 8px;
}

button:hover, button:focus, .button:hover, .button_submit:hover, .button_reset:hover
{
	text-decoration: none;
	background: #3c3640;
	color: #79767e;
	border: 1px solid #3c3640;
	cursor: pointer;
}

input:focus, textarea:focus, select:focus
{
	border: 1px solid #3c3640;
}

label
{
	display: inline-block;
	padding: 2px;
}

.post_options label, .options label
{
	vertical-align: top;
}

input.input_check, input.input_radio
{
	border: none;
	background: none;
	height: 16px;
	margin: 0px;
}
h3.catbg input.input_check
{
	margin: 9px 7px 0 7px;
}


/* Standard horizontal rule.. ([hr], etc.) */
hr, .hrcolor
{
	height: 1px;
	border: 0;
	color: #ccc;
	background-color: #ccc;
}

/* By default set the color on these tags as #000. */
h1, h2, h3, h4, h5, h6
{
	color: #000;
	font-size: 1em;
	margin: 0;
	padding: 0;
}

/* Fieldsets are used to group elements. */
fieldset
{
	border: 1px solid #3c3640;
	padding: 1em;
	margin: 0 0 0.5em 0;
}
fieldset legend
{
	font-weight: bold;
	color: #444;
}
/* No image should have a border when linked. */
a img
{
	border: 0;
}

/* Define strong as bold, and em as italics */
strong
{
	font-weight: bold;
}

em
{
	font-style: italic;
}
/* Alternative for u tag */
.underline
{
	text-decoration: underline;
}

/* Common classes to easy styling.
------------------------------------------------------- */

.floatright
{
	float: right;
}
.floatleft
{
	float: left;
}

.flow_auto
{
	overflow: auto;
}
.flow_hidden
{
	overflow: hidden;
	padding: 8px;
	border-bottom: 1px solid #3c3640;
	background: #1a1b1f;
}
.flow_hidden .windowbg, .flow_hidden .windowbg2
{
	margin-top: 2px;
}

br.clear
{
	clear: none;
}

.clear
{
	clear: both;
}
.clear_left
{
	clear: left;
}
.clear_right
{
	clear: right;
}

/* Default font sizes: small (8pt), normal (10pt), and large (14pt). */
.smalltext, tr.smalltext th
{
	font-size: 0.85em;
}
.middletext
{
	font-size: 0.9em;
	line-height: 1em;
}
.normaltext
{
	font-size: 1em;
	line-height: 1.2em;
}
.largetext
{
	font-size: 1.4em;
}
.centertext
{
	margin: 0 auto;
	text-align: center;
}
.righttext
{
	margin-left: auto;
	margin-right: 0;
	text-align: right;
}
.lefttext
{
	margin-left: 0;
	margin-right: auto;
	text-align: left;
}
.double_height
{
	line-height: 2em;
}
/* some common padding styles */
.padding
{
	padding: 0.7em;
}
.main_section, .lower_padding
{
	padding-bottom: 0.5em;
}
/* a quick reset list class. */
ul.reset, ul.reset li, .aimp_page_header ul.reset, .aimp_page_header ul.reset li
{
	padding: 0;
	margin: 0;
	list-style: none;
	color: #79767e;
}

.aimp_page_header ul.reset, .aimp_page_header ul.reset li
{
	color: #79767e;
}

/* Some BBC related styles.
------------------------------------------------------- */

/* A quote, perhaps from another post. */
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote
{
	color: #000;
	line-height: 1.4em;
	padding: 1.1em 1.4em;
	margin: 0.1em 0 0.3em 0;
	overflow: auto;
}

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote, code.bbc_code
{
	border-top: 2px solid #3c3640;
	border-bottom: 2px solid #3c3640;
	background-color: #202227;
	color: #707070;
}

#blockcut
{
	color: #707070;
	border: 1px solid #3c3640;
	background-color: #202227;
	margin-top: 2px;
	padding: 10px;
}

/* A code block - maybe PHP ;). */
code.bbc_code
{
	display: block;
	line-height: 1.5em;
	padding: 0.6em 1em;
	overflow: auto;
	white-space: pre;
	/* Show a scrollbar after about 24 lines. */
	max-height: 24em;
	color: #000000;
}

/* The "Quote:" and "Code:" header parts... */
.codeheader, .quoteheader
{

	font-size: x-small;
	font-weight: bold;
	padding: 0 0.3em;
}

/* For links to change the code stuff... */
.codeoperation
{
	font-weight: normal;
}

/* Styling for BBC tags */
.bbc_size
{
	line-height: 1.4em;
}
.bbc_color a
{
	color: inherit;
}
.bbc_img
{
	border: 0;
}
.bbc_table
{
	font: inherit;
	color: inherit;
}
.bbc_table td
{
	font: inherit;
	color: inherit;
	vertical-align: top;
}
.bbc_u
{
	text-decoration: underline;
}
.bbc_list
{
	text-align: left;
}
.bbc_tt
{
}

/* Generally, those [?] icons.  This makes your cursor a help icon. */
.help
{
	cursor: help;
}

/* /me uses this a lot. (emote, try typing /me in a post.) */
.meaction
{
	color: red;
}

/* Highlighted text - such as search results. */
.highlight
{
	font-weight: bold;
	color: #ff7200 !important;
}

/* A more discreet highlight color, for selected membergroups etc. */
.highlight2
{
	background-color: #D1E1EF;
	color: #000 !important;
}

/* Generic, mostly color-related, classes.
------------------------------------------------------- */

.titlebg, .titlebg2, tr.titlebg th, tr.titlebg td, tr.titlebg2 td
{
}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th
{
	color: #79767e;
	background: #3c3640;
	padding-left: 9px;
}

/* adjust the table versions of headers */
tr.titlebg th, tr.titlebg2 th, td.titlebg, td.titlebg2, tr.catbg th, tr.catbg2 th, td.catbg, td.catbg2
{
	padding: 6px 16px;
}
tr.catbg th a:link, tr.catbg th a:visited, tr.catbg2 td a:link, tr.catbg2 td a:visited
{
	color: #79767e;
}

.pagesection
{
	font-size: 0.9em;
	overflow: hidden;
	margin-top: 6px;
	margin-bottom: 6px;
}

#admin_content div.pagesection
{
	padding: 8px;
}

div.pagesection div.floatright input
{
	margin-top: 3px;
}

.pagelinks
{
	padding: 0.6em 0 0.4em 0;
}

.windowbg, #preview_body
{
	background-color: #1a1b1f;
}
.windowbg2
{
	background-color: #202227;
}
.windowbg3
{
	color: #000;
	background-color: #cacdd3;
}

.postbg
{
	background-color: #1a1b1f;
}

.post
{
	margin: 10px;
}

/* Colors for background of posts requiring approval */
.approvebg
{
	color: #000;
	background-color: #ffeaea;
}
.approvebg2
{
	color: #000;
	background-color: #ff0000;
}

/* Color for background of *topics* requiring approval */
.approvetbg
{
	color: #000;
	background-color: #e4a17c;
}
.approvetbg2
{
	color: #000;
	background-color: #f3bd9f;
}

/* Sticky topics get a different background */
.stickybg
{
	background: #202227;
}
.stickybg2
{
	background: #202227;
}


/* Locked posts get a different shade, too! */
.lockedbg, .lockedbg2
{
	font-style: italic;
}

/* Posts and personal messages displayed throughout the forum. */
.post, .personalmessage
{
	overflow: auto;
	line-height: 1.4em;
	padding: 0.1em 0;
}

/* All the signatures used in the forum.  If your forum users use Mozilla, Opera, or Safari, you might add max-height here ;). */
.signature, .attachments
{
	width: 98%;
	overflow: auto;
	clear: right;
	padding: 1em 0 3px 0;
	border-top: 1px solid #777777;
	line-height: 1.4em;
	font-size: 0.85em;
}
.custom_fields_above_signature
{
	width: 98%;
	clear: right;
	padding: 1em 0 3px 0;
	border-top: 1px solid #aaa;
	line-height: 1.4em;
	font-size: 0.85em;
}

/* Sometimes there will be an error when you post */
.error
{
	color: yellow;
	font-weight: bold;
}

/* Messages that somehow need to attract the attention. */
.alert
{
	color: yellow;
	font-weight: bold;
}

/* Calendar colors for birthdays, events and holidays */
.birthday
{
	color: #920ac4;
}

.event
{
	color: #078907;
}

.holiday
{
	color: #000080;
}

/* Colors for warnings */
.warn_mute
{
	color: red;
}

.warn_moderate
{
	color: #ffa500;
}

.warn_watch, .success
{
	color: green;
}

a.moderation_link, a.moderation_link:visited
{
	color: red;
	font-weight: bold;
}

.openid_login
{

	padding-left: 18px;
}

/* a descriptive style */
.description, .description_board, .plainbox
{
	padding: 0.5em 1em;
	font-size: 0.9em;
	background: #1a1b1f;
}
.description_board
{
	margin: 1em 1px 0 1px;
}

/* an informative style */
.information
{
	padding: 0.5em 1em;
	font-size: 0.9em;
	line-height: 1.3em;
	border: 1px solid #3c3640;
	background: #1a1b1f;
	margin: 0.2em 1px 1em 1px;
}
.information p
{
	padding: 1em;
	margin: 0;
}
p.para2
{
	padding: 1em 0 3.5em 0;
	margin: 0;
}
/* AJAX notification bar
------------------------------------------------------- */
#ajax_in_progress
{
	color: #f96f00;
	text-align: center;
	font-size: 16pt;
	padding: 8px;
	width: 100%;
	height: 66px;
	line-height: 25px;
	position: fixed;
	top: 0;
	left: 0;
}

#ajax_in_progress a
{
	color: orange;
	text-decoration: underline;
	font-size: smaller;
	float: right;
	margin-right: 20px;
}

/* Lists with settings use these a lot.
------------------------------------------------------- */
dl.settings
{
	clear: right;
	overflow: auto;
	margin: 0 0 10px 0;
	padding: 0;	
}
dl.settings dt
{
	width: 48%;
	float: left;
	margin: 0 0 10px 0;
	padding: 0;
	clear: both;
}
dl.settings dt.settings_title
{
	width: 100%;
	float: none;
	margin: 0 0 10px 0;
	padding: 5px 0 0 0;
	font-weight: bold;
	clear: both;
}
dl.settings dt.windowbg
{
	width: 98%;
	float: left;
	margin: 0 0 3px 0;
	padding: 0 0 5px 0;
	clear: both;
}
dl.settings dd
{
	width: 48%;
	float: left;
	margin: 0 0 3px 0;
	padding: 0;
}
dl.settings img
{
	margin: 0 10px 0 0;
}
/* help icons */
dl.settings dt a img
{
	position: relative;
	top: 2px;
}

/* Styles for rounded headers.
------------------------------------------------------- */
h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg
{
	overflow: hidden;
	height: 31px;
	line-height: 31px;
}
h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited, h3.catbg, .table_list tbody.header td, .table_list tbody.header td a
{
	color: #79767e;
}
h3.catbg2 a, h3.catbg2
{
	color: #feb;
}
h3.catbg a:hover, h4.catbg a:hover, .table_list tbody.header td a:hover
{
	color: #fd9;
	text-decoration: none;
}
h3.catbg2 a:hover
{
	color: #79767e;
	text-decoration: none;
}
.titlebg a, .titlebg a, 
.titlebg a:link, .titlebg a:link
.titlebg a:visited, .titlebg a:visited
{
	color: #79767e;
	text-decoration: none;
}
.titlebg a:hover, .titlebg a:hover
{
	color: #79767e;
	text-decoration: none;
}
h3.catbg img.icon, h4.titlebg img.icon
{
	vertical-align: middle;
	margin: -2px 5px 0 0;
}
h4.catbg a.toggle img
{
	vertical-align: middle;
	margin: -2px 5px 0 5px;
}
h4.catbg, h4.catbg2 , h3.catbg , h3.catbg2 , .table_list tbody.header td.catbg
{
	padding-right: 0px;
}
h4.titlebg, h3.titlebg
{
	padding-right: 9px;
}
h4.titlebg img.icon
{
	float: left;
	margin: 5px 8px 0 0;
}
div.cat_bar
{
	background: #3c3640;
	padding: 6px 8px;
	height: 31px;
	overflow: hidden;
	margin-bottom: 1px;
}
div.title_bar
{
	background: #3c3640;
	padding-left: 9px;
	height: 31px;
	overflow: hidden;
	margin-bottom: 1px;
}

/* rounded bars needs a different background here */

div.roundframe div.cat_bar
{
	background: #3c3640;
}

div.title_barIC
{
	background: #202227;
	padding-left: 9px;
	height: 31px;
	overflow: hidden;
	margin-bottom: 1px;
}

#upshrinkHeaderIC p.pminfo
{
	margin: 0;
	padding: 0.5em;
}
img#upshrink_ic, img#newsupshrink
{
	float: right;
	margin: 10px 5px 0 0;
}
table.table_list a.unreadlink, table.table_list a.collapse
{
	float: right;
}
table.table_list a.collapse
{
	margin: 7px 7px 0 1em;
}

/* The half-round header bars for some tables. */
.table_grid tr.catbg, .table_grid tr.titlebg
{
	font-size: 0.95em;
}
.table_grid tr.catbg th, .table_grid tr.titlebg th
{
	height: 28px;
	line-height: 28px;
}
.table_grid th.last_th input
{
	margin: 0 2px;
}
.table_grid th.lefttext
{
	padding: 0 0.7em;
}

/* a general table class */
table.table_grid
{
	border-collapse: collapse;
}
table.table_grid td, table.table_list td
{
	padding: 8px 12px;
}
/* GenericList */
.additional_row
{
	padding: 0.5em 0 0.5em 0;
}
table.table_grid thead tr.catbg th
{
	white-space: nowrap;
}

/* table_grid styles for Profile > Show Permissions. */
#permissions table.table_grid  td
{
	padding: 0.4em 0.8em;
	cursor: default;
} 

/* Common styles used to add corners to divisions.
------------------------------------------------------- */
.windowbg span.topslice
{
	display: block;
	padding-left: 20px;
}
.windowbg span.topslice span
{
	display: block;
	height: 11px;
}
.windowbg span.botslice
{
	display: block;
	padding-left: 20px;
	font-size: 5px;
	line-height: 5px;
	margin-bottom: 0.2em;
}
.windowbg span.botslice span
{
	display: block;
	height: 11px;
}
.windowbg2 span.topslice
{
	display: block;
	padding-left: 20px;
}
.windowbg2 span.topslice span
{
	display: block;
	height: 11px;
}
.windowbg2 span.botslice
{
	display: block;
	padding-left: 20px;
	font-size: 5px;
	line-height: 5px;
	margin-bottom: 0.2em;
}
.windowbg2 span.botslice span
{
	display: block;
	height: 11px;
}
.approvebg span.topslice
{
	display: block;
	padding-left: 20px;
}
.approvebg span.topslice span
{
	display: block;
	height: 11px;
}
.approvebg span.botslice
{
	display: block;
	padding-left: 20px;
	margin-bottom: 0.2em;
}
.approvebg span.botslice span
{
	display: block;
	height: 11px;
}
.postbg
{
	border-left: 1px solid #3c3640;
	border-right: 1px solid #3c3640;
}

/* Used for sections that need somewhat larger corners.
----------------------------------------------------------- */
.roundframe
{
	padding: 8px;
	background: #202227;
}
.roundframe dl, .roundframe dt, .roundframe p
{
	margin: 0;
}
.roundframe p
{
	padding: 0.5em;
}
span.upperframe
{
	padding: 0;
	display: block;
	padding-left: 20px;
}
span.upperframe span
{
	padding: 0;
	height: 12px;
	display: block;
}
span.lowerframe
{
	padding: 0;
	display: block;
	padding-left: 20px;
	border-bottom: 1px solid #3c3640;
}
span.lowerframe span
{
	padding: 0;
	height: 12px;
	display: block;
}

/* The main content area.
------------------------------------------------------- */
.content
{
	padding: 0.5em 1.2em;
	margin: 0;
	border: none;
}
.content p
{
	margin: 0 0 0.5em 0;
}

/* Styles used by the auto suggest control.
------------------------------------------------------- */
.auto_suggest_div
{
	border: 1px solid #000;
	position: absolute;
	visibility: hidden;
}
.auto_suggest_item
{
	color: #000000;
	background-color: #ddd;
}
