/* Styles for the standard dropdown menus.
------------------------------------------------------- */
#main_menu
{
	padding: 4px;
	height: 23px;
	background: #3c3640;
	line-height: 23px;
}

.dropmenu, .dropmenu ul
{
	list-style: none;
	padding: 0;
	margin: 0;
}
.dropmenu
{
	padding: 0 0.5em;
}
.dropmenu a
{
	display: block;
	color: #000000;
	text-decoration: none;
}
.dropmenu a span
{
	display: block;
	padding: 0 0 0 5px;
	font-size: 0.9em;
}
/* the background's first level only */
.dropmenu li a.firstlevel
{
	color: #000000;
	margin-right: 8px;
}

.dropmenu li a.firstlevel span.firstlevel
{
	display: block;
	position: relative;
	left: -5px;
	padding-left: 5px;
	height: 22px;
}
.dropmenu li
{
	float: left;
	padding: 0;
	margin: 0;
	position: relative;
}
.dropmenu li ul
{
	z-index: 90;
	display: none;
	position: absolute;
	font-weight: normal;
	border: 1px solid #3c3640;
}
.dropmenu li li
{
	width: 19em;
	margin: 0;
	background-color: #3c3640;
}
.dropmenu li li a span
{
	color: #79767e;
	display: block;
	padding: 8px;
}
.dropmenu li ul ul
{
	margin: -1.8em 0 0 13em;
}

.aimp_page_header .dropmenu li a.firstlevel
{
	color: #79767e;
}

/* the active button */
.dropmenu li a.active
{
	color: #c33d1e;
	font-weight: bold;
}
.aimp_page_header .dropmenu li a.active
{
	color: #c33d1e;
	font-weight: bold;
}
/* the hover effects */
.dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel
{	
	color: #c33d1e;
	cursor: pointer;
	text-decoration: none;
}
/* the hover effects on level2 and 3 */
.dropmenu li li a:hover, .dropmenu li li:hover a
{
	background-color: #3c3640;
	text-decoration: none;
}
.dropmenu li li a:hover span
{
	color: #c33d1e;
}
.dropmenu li:hover ul ul, .dropmenu li:hover ul ul ul
{
	top: -999em;
}
.dropmenu li li:hover ul
{
	top: auto;
}
.dropmenu li:hover ul
{
	display: block;
}

/* The dropdown menu toggle image */
#menu_toggle
{
	float: right;
	margin-right: 10px;
	padding-top: 3px;
}
