css:
@-moz-document regexp('https?://\\w+\\.google\\..*') {

/* search button */
#gbqfb {
	background: #268BD2;
}

/* all the major elements */
body,
#main,
#cnt,
#rcnt,
#top_nav,
#wob_wc,
span.kno-sh,
.kno-ec,
.kno-msc-d,
.kno-rdesc,
#hmp,
#appbar,
.gb_9,
.gb_f,
.fbar,
#gbx1,
#hdtb_msb,
#hdtbSum,
#center_col,
.wta,
li.g,
.g,
.st,
.top-shopping-title-text {
	font-family: Segoe UI, Lucida Mac, Lucida Grande, Lucida, Calibri, sans-serif; 
	/*background-color: white !important;*/
	/* base 3 */
	
	/*color: #93a1a1 !important;*/
	color: #6f7a7a !important; 
	/* cyan */
	
	border: none !important;
}

/* add a border to the sidebar results */
.kno-ec,
.kno-xs {
	border: solid #657b83 1px !important;
	/* base 0 */
	
}

/*color the ads so that they're separate from results*/
/* apply same style to drop down menus */
#tads,
.c,
.ab_dropdown,
.hdtb-mn-o,
.gbmc,
.gbmt {
	background-color: #efefef !important;
	border: none !important;
}

/* top bar in google search +You, Search, Images, etc */
#gbx3,
.hdtb-td-o {
	background-color: #073642 !important;
	
	border: none !important;
}

/* link coloring */
a, div.kno-lc {
	color: #268bd2 !important;
	/* blue */
}
a:visited {
	color: #ff7d66 !important; /* light orange */
	
	/*color: #d33682 !important;*/ /* magenta */
	
}
a:hover {
	color: #ff5233 /*#ff330f*/ !important;
	/* orange */
	
}

/* the actively selected category at the top */
.hdtb_msel { 
	border-bottom-color: #ff5233 !important;
	border-left-color: #ff5233 !important;
	border-right-color: #ff5233 !important;
	border-top-color: #ff5233 !important;
	color: #ff5233 !important;
}

/* the address shown below the link in the result */
cite{
	color: #4fa244 !important; /* green */
}
.f {
	margin-bottom:4px;
	margin-top:4px;
}
/* the search result snippet and your user name if signed in */
span.st,
#gbgs4d {
	color: #6f7a7a !important;
	/*base 1 */
}
/* the date in the results, sub-results */
span.f,
.st {
	/*color: #657b83 !important;*/
	color: #93a1a1 !important;
	/* base 00 */
	
}
/* google search button and drop down buttons*/
#gbqfb,
#gbqfbb,
#gbqfba,
.hdtb-tl-sel,
.hdtb-tl:hover,
.clickable-dropdown-arrow {
	background-image: none !important;
	/*background-color: #b58900 !important;*/
	/* yellow to kill the blue overload */
	
	border: none !important;
	/*color: #fdf6e3 !important;*/
	/*base 3 */
	
}

/* selected search area */
.hdtb_msel {
	color: #dc322f !important;
	/* red */
	
	border-color: #dc322f !important;
}

/* button on the right hand side */
.ab_button,
#abar_button_opt {
	background-image: none !important;
	/*background-color: #586e75 !important;*/
	/* base 01 */
	
}

/* link colors in the top bar */
#gbz a {
	color: #839496 !important;
	/*base 1 */
	
}
#gbz a:active {
	color: #fdf6e3 !important;
	/*base 3 */
	
}
#gbz a:hover {
	color: #ff4929 !important;
	/*base 2 */
	
}

#gbql {
	background-image: none !important;
	/* google logo is cruft */
	
}

/*sign in button */
.gbgs,
.gbgs a {
	background-image: none !important;
	border: none !important;
	background-color: #6c71c4 !important;
	/* violet - one badass flower */
	
	color: #ff4929 !important;
	/* base 2 */
	
}
.gbgs:hover {
	color: #fdf6e3 !important;
	/* base 3 */
	
}

/* input box has inverted color scheme */
.gsib_a,
.gbqfsf {
	/*background-color: #ff4929 !important;*/
	/* base 2 */
	
	color: #002b36 !important;
	/* base 03 */
	
}

/* remove indentation of search results */
#res {
	padding: 0;
}

/* increase spacing between search results */
li.g {
	margin-bottom: 27px;
}

/* hide Goooooogle images in bottom nav links */
.csb {
	display:none;
}

/* hide google's notices */
#epbar {
	display:none;
}

/* add spacing to botton nav links and increase font size (now that images are gone) */
#nav a.fl, #nav td.cur {
	padding-left: 7px;
	padding-right: 7px;
	font-size: medium;
}

/* google logo */
#logo {
	margin-top: 0px;
	background-repeat: no-repeat;
	background-size: 100px 40px;
	width: 100px;
	height: 40px;
	background-position: 0px 0px;
	background-image: url(data:image/png;base64,) !important;
}
#logo img {
	display:none;
}


}
