css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("turtleseed.com") {

  body, #wowcontent, #container, #loadmore {
    background-image: linear-gradient(#000) !important;
    color: #FFF !important;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 30px; 
    border-top-right-radius: 30px;
    border-top-left-radius: 30px;  
    background-color: #000 !important;;
  }
  
   div.suggest_boxes, div.status_boxes, div.bigger_status_boxes, div.huge_status_boxes, div.giant_status_boxes {
     /*  background: #000 !important;  */
    background-image: linear-gradient(#000) !important;
    color: #FFF !important;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 30px; 
    border-top-right-radius: 30px;
    border-top-left-radius: 30px;  
    box-shadow: 0px 0px 4px -1px #000 !important;
    border: 1px solid #000 !important;
    background-color: #000 !important;
   }
  
  #container, #scrollbox, #wowcontent, #allstuffaside, #sidebar { 
    background-image: linear-gradient(#000) !important;
  }
  
  div#timmytime, spawn.agonow {
    background-color: #353535;
    box-shadow: none !important;
    border:1px solid #000 !important;;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-top-right-radius: 30px;
    border-top-left-radius: 30px;  
  }
  
  a>div.namesauth {
    color: #FfffFF !important;
  }
  
  div.content {
    background: #252525 !important;
    box-shadow: none !important;
    border:1px solid #000 !important;;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-bottom-right-radius: 30px;  
  }
  
  div#statusarea>div.status_boxes { 
    background: #333 !important;
    box-shadow: none !important;
    border:1px solid #000;
    -webkit-border-radius: 30px;
    -moz-border-radius: 30px;
    border-radius: 30px;  
  }
  
  div.hidestatus {
    color: #000 !important;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 30px; 
    border-top-right-radius: 30px;
    border-top-left-radius: 30px;  
  }
  
  dl#sample.dropdown {
    color: #000 !important;
  }
  
  div#invitecontainer {
    background: #000 !important;
  }
  
  div.shorelove {
    color: #222222 !important;
  }
  
  li>div.status_boxes {
    border-radius: 5px;
    border: 1px solid #252525;
    background-color: #333;
    overflow: auto;
    min-height: 42px;
    max-height: 60px;
    color: #ffffff;
    padding: 10px 10px 17px;
    border-radius: 30px;
    box-shadow: 0px 0px 4px -1px #212121;
    border: 1px solid #000;
    background-color: #000;
  }
  
  li>div.status_boxes:hover {
    border-radius: 5px;
    border: 1px solid #ccc;
    background-color: #444;
    overflow: auto;
    min-height: 42px;
    max-height: 60px;
    color: #fff;
    padding: 10px 10px 17px;
  }
  
 div.yourverif {
   display: block;
   background: url(http://cniangel.net/assets/tsdt-assets/checkmark-white.svg) no-repeat scroll center center !important;
   background-size: 20px;
   width: 20px; /* Width of new image */
   height: 20px; /* Height of new image */
   padding-left: 20px;
 }
  
  div#replyscroller {
    background-color: #252525;
  }
  
  div#notesBox > h2, .shack li, .sidebarinfo {
    background-color: #333 !important;
    
  }
  
  .status_boxes > div.controlthewords {
    background-color: #333 !important;
    color: #c5c5c5 !important;
  }
  
  .status_boxes > span.chartinfo {
    background-color: #444 !important;
    color: #ddd !important;
  }
  
  .status_boxes a {
    color: #888 !important;
  }
  
  div.sectionchange {
    background-color: #555 !important;
    color: #b5b5b5 !important;
  }
  
   div.sectionchange a {
    color: #55a5f5 !important;
  }
  
  div.sectionchange > span#actualdata {
    color: #d5d5d5 !important;
  }
  
  div#lefttitle, div#righttitle, p.blockchanges, p.halfchanges{
    background-color: #777 !important;
    color: #b5b5b5 !important;
  }
  
  p.sectionhalf, p.sectionchange {
    background-color: #555 !important;
    color: #b5b5b5 !important;
  }
  
  div#likeheader, div#interactfooter {
    background-color: #353535;
    box-shadow: 0px -3px 4px -2px #454545;
  }
  
  div#talkback {
    box-shadow: -2px 0px 3px 0px #444;
  }
  
  span.thewhite, div.nopictext {
    background-color: #353535 !important;;
    color: #ccc !important;
  }
  
   span.thewhite, div.nopictextreplies {
    background-color: #353535 !important;;
    color: #ccc !important;
  }
  
  /* */
  
  div.yoursearchwrapper {
    height: 31px !important;
  }
  
  dl#sample.dropdown > dt > a {
    color: #fff !important;
  }
  
  /* Area where I define the icons  */
  
  img[alt~=Stats] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/stats-bars-white.svg) no-repeat;
    background-size: 18px;
    width: 18px; /* Width of new image */
    height: 18px; /* Height of new image */
    padding-left: 18px;
  }
  
  img[alt~=Like] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/heart-white.svg) no-repeat;
    background-size: 18px;
    width: 18px; /* Width of new image */
    height: 18px; /* Height of new image */
    padding-left: 18px;
  }
  
  img[src="https://cdn.turtleseed.com/alreadyliked.png"] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/heart-red.svg) no-repeat;
    background-size: 18px;
    width: 18px; /* Width of new image */
    height: 18px; /* Height of new image */
    padding-left: 18px;
  }
  
  img[alt~=Reply] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/bubble-white.svg) no-repeat;
    background-size: 18px;
    width: 18px; /* Width of new image */
    height: 18px; /* Height of new image */
    padding-left: 18px;
  }
  
  img[alt~=Delete] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/bin-white.svg) no-repeat;
    background-size: 18px;
    width: 18px; /* Width of new image */
    height: 18px; /* Height of new image */
    padding-left: 18px;
  }
  
  img[alt~=Share] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/share-white.svg) no-repeat;
    background-size: 18px;
    width: 18px; /* Width of new image */
    height: 18px; /* Height of new image */
    padding-left: 18px;
  }
  
  a[title~=Reseed]>img[alt~=Stats] {
    display: none;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/loop-white.svg) no-repeat;
    background-size: 18px;
    width: 18px; /* Width of new image */
    height: 18px; /* Height of new image */
    padding-left: 18px;
  }
  
  div.container {
    background-color: #00A885;
    border-color: #111 -moz-use-text-color -moz-use-text-color;
    background-image: linear-gradient(to bottom, #111 1%, #151515 60%, #1f1f1f 90%);
    /* background-image: linear-gradient(to bottom, #2C82C9 1%, #2C82C9 60%, #2C82C9 90%); */
    box-shadow: 0px 0px 0px 1px #111;
  }
  
  div#logo {
    box-shadow: -2px 0px 0px -1px #222, 2px 0px 0px -1px #222 !important;
    background-image: linear-gradient(to bottom, #111 1%, #151515 60%, #1f1f1f 90%);
    border-right:  #222 !important;
    border-left: #222 !important;
  }
  
  div#logo:hover {
    box-shadow: -2px 0px 0px -1px #222, 2px 0px 0px -1px #222 !important;
    background-image: linear-gradient(to bottom, #222 1%, #252525 60%, #2f2f2f 90%);
    border-right:  #444 !important;
    border-left: #444 !important;
  }
  
  /* Profile Icon in Top Panel */
  div#profilepage>h2>a {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/user-white.svg) no-repeat;
    background-size: 30px;
    width: 30px; /* Width of new image */
    height: 30px; /* Height of new image */
    padding-left: 30px;
  }
  
  div#profilepage>h2 {
    background-image: linear-gradient(to bottom, #111 1%, #151515 60%, #1f1f1f 90%);
  }
  
  /* Message Icon in Top Panel */
  div#messagepage>h2>a {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/bubble.svg) no-repeat;
    background-size: 30px;
    width: 30px; /* Width of new image */
    height: 30px; /* Height of new image */
    padding-left: 30px;
  }
  
  div#messagepage>h2>a:hover {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/bubble-white.svg) no-repeat;
    background-size: 30px;
    width: 30px; /* Width of new image */
    height: 30px; /* Height of new image */
    padding-left: 30px;
  }
  
  /* Hatch 100 Icon in Top Panel */
  div#contentpage>h2>a {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/sphere-white.svg) no-repeat;
    background-size: 30px;
    width: 30px; /* Width of new image */
    height: 30px; /* Height of new image */
    padding-left: 30px;
  }
  
  /* Notifications Icon in Top Panel */
  div#notespage>h2>a {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(http://cniangel.net/assets/tsdt-assets/power-white.svg) no-repeat;
    background-size: 30px;
    width: 30px; /* Width of new image */
    height: 30px; /* Height of new image */
    padding-left: 30px;
  }
  
  
}
