css:
@-moz-document domain("www.spi0n.com") {

#sidebar 
{
  display: none;
}

#middle.white_ver .middle_line.sidebar_bg 
{
  background: none;
}

#content 
{
  width: auto;
}

.entry iframe 
{
  display: inherit;
  text-align: center;
  width: 100%;
}

#middle img 
{
  height: 50%;
  margin: 0 auto;
}

img.noimgbg, #middle img.noimgbg 
{
  display: none;
}

span.image_link_hover 
{
  display: none;
}

#page 
{    
  background: url("http://i.imgur.com/PtJ6fl7.jpg") bottom fixed no-repeat !important;
  background-size: cover !important;
}

#middle.white_ver, .blog_txt a, .post_data {
    color: #B1B1B1 !important;
    background: rgba(0, 0, 0, 0)!important;
}

}
